  }
fragColor=vec4(v<20.?abs(s(y+.02)-l)/vec3(4.,3.,2.)*200.:vec3(0.), 1.);
}

#define bg_color vec4(0.0)
#define red vec4(1,0,0,1)
#define blue vec4(0,0,1,1)
#define green vec4(0,1,0,1)
#define white vec4(1,1,1,1)
#define d_pix (1.0/iResolution.y)

float d_square(vec2 uv, float a, float b){
    float px  = 0.5*a; float py = 0.5*b;  
    float d = length(uv - vec2(clamp(uv.x,-px,px), clamp(uv.y, -py, py)) );
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0*(fragCoord.xy / iResolution.xy) - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 2.0;

    float size = 1.0 + sin(iTime);
    float dist = d_square(uv, size, size);

    fragColor = bg_color;
    fragColor += vec4(tanh(dist), 0.5, 0,1) * sin(50.0 * dist);
    fragColor += 0.8*white * smoothstep(4.0*d_pix, 0.0, dist );


}

// some constants
#define size 0.015
#define radius 0.09
#define speed 3.0
#define amplitude 4.0
#define spacing 0.1

// some functions
#define S(x) smoothstep(2.0 / iResolution.y, 0.0, x)
#define solidCircle(uv, p, r) S(length(uv - p) - r)
#define outlinedCircle(uv, p, r) S(abs(length(uv - p) - r))


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float circles = 0.0;
    float guides = 0.0;

    vec2 uv = fragCoord / iResolution.y;
    ivec2 cell = ivec2(uv / spacing);
    for (int i=cell.x - 1; i<=cell.x + 2; i++) {
        for (int j=cell.y - 1; j<=cell.y + 2; j++) {

            vec2 offset = vec2(i, j) * spacing;
            guides += outlinedCircle(uv, offset, radius);

            float phase = float(i + j) * spacing * amplitude;
            float factor = (iTime * speed) + phase;
            vec2 pos = (vec2(cos(factor), sin(factor)) * radius) + offset;
            circles += solidCircle(uv, pos, size);
        }
    }

	fragColor = vec4(circles + 0.3 * guides);
}

// CSG on Intervals
// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)

// note that this version is verbose and purposefully lacks 
// any form of optimization for the sake of education.

// the general principle here is that we first evaluate the near and
// far boundaries of the brushes that cross our line of sight, 
// so we end up with a list of 1D intervals for each brush.
//
// then we can perform CSG in rayspace and raymarch towards the
// nearest resulting visible surface by repeatedly evaluating
// a distance estimator that gives us the distance to the 
// nearest horizon.
//
// the distance estimator evaluates the CSG tree to receive 
// a lower bound on the distance to the edge closest to 
// the right of a given 1D scalar t.
//
// the CSG tree evaluation is sped up by transforming the tree
// to an instruction list first (here done manually) that operates
// on up to four virtual registers A, B, C, D to store temporary
// values; after the instruction list has been executed, the
// final result can be found in register A.
//
// the VM executing the instruction list needs only four operators:
// SET A B (copy)
// OR A B (boolean union)
// AND A B (boolean intersection)
// ANDN A B (boolean subtraction)
// each operator can be encoded as a function that receives
// a 4-bit bitmask encoding the comparisons a0>b0 a1>b0 a0>b1 a1>b1
// for the two intervals passed to the operator, and returns
// a new interval in D that sources one of the four possible values
// for d0 d1 based on the bitmask.
//
// the complexity is O(n) for n union-only instructions,
// but can reach O(n*n) in the worst case; the performance in
// practice however is typically around O(3n). Through hierarchical
// culling one can aggressively reduce the number of instructions
// required to solve a tile:
//
// it is possible, not demonstrated here, to source the brush 
// intervals from a primitive-frustum intersection instead, and
// to cull/rewrite instructions that yield no result, allowing
// for hierarchical quadtree-style raymarching using
// per-tile linked lists for tiles that contain more than one 
// instruction.


// evaluation interval legend:
// green: union
// orange: intersection
// magenta: subtraction

// set to 1 to show CSG operator debug setup
#define DEBUG_CSG_OPS 0
#define DEBUG_CSG_OP CSG_ANDN
#define TEST_SETUP 0

const vec2 ray_origin = vec2(0.0);
const vec2 ray_dir = vec2(1.0,0.0);

const int op_set = 0;
const int op_or = 1;
const int op_and = 2;
const int op_and_not = 3;

const int reg_a = -1;
const int reg_b = -2;
const int reg_c = -3;
const int reg_d = -4;
const int empty = -5;

const float infinity = 1.0/0.0;
const vec2 empty_interval = vec2(infinity);

// these brushes are usually imported using a SSBO
// here we provide a small local array
#if DEBUG_CSG_OPS
#define NUM_BRUSHES 2
#elif TEST_SETUP
#define NUM_BRUSHES 3
#else
#define NUM_BRUSHES 7
#endif
vec3 brushes[NUM_BRUSHES];
vec2 brush_intervals[NUM_BRUSHES];

vec2 iCircle(vec2 o_ro, vec2 rd, vec3 params) {
	vec2 ro = o_ro - params.xy / params.z;
	float k = dot(ro, rd);
    float q = sqrt(k*k - dot(ro, ro) + 1.0);
    vec2 d = (vec2(-q,q) - k) * params.z;
    return (d.x != d.x)?empty_interval:d;
}

float dCircle(vec2 p, vec3 params) {
    return length(p - params.xy) - params.z;
}

// dynamic array index hack
vec3 get_brush(int brushid) {
    for (int i = 0; i < NUM_BRUSHES; ++i) {
        if (i == brushid)
            return brushes[i];
    }
    return vec3(0.0);
}

// dynamic array index hack
vec2 get_brush_interval(int brushid) {
    for (int i = 0; i < NUM_BRUSHES; ++i) {
        if (i == brushid)
            return brush_intervals[i];
    }
    return empty_interval;
}

// dynamic array index hack
float get_reg(vec4 regs, int regid) {
    for (int i = 0; i < 4; ++i) { if (i == reg_a-regid) return regs[i]; }
    return infinity;
}
void set_reg(inout vec4 regs, int regid, float val) {
    for (int i = 0; i < 4; ++i) { if (i == reg_a-regid) { regs[i] = val; return; } }
}

#if DEBUG_CSG_OPS
#define NUM_CSG_OPS 2
#elif TEST_SETUP
#define NUM_CSG_OPS 3
#else
#define NUM_CSG_OPS 9
#endif
#define CSG_OP ivec3
// opcode, src1, src2
// src1 must be register
// src2 must be register, empty or brush
// computation result will be written to src1
// in a realworld example, this would be a SSBO
CSG_OP csg_ops[NUM_CSG_OPS];
void setup_globals(float t) {
#if DEBUG_CSG_OPS
    brushes[0] = vec3(0.5,0.0,0.08);
    brushes[1] = vec3(mix(0.26,0.74,sin(t)*0.5+0.5),0.0,0.1);
#elif TEST_SETUP
    vec2 c = vec2(0.5,0.05);
    float r0 = 0.21;
    float a0 = t;
    float a1 = a0 + radians(90.0);
    brushes[0] = vec3(c.x + cos(a0)*r0, c.y + sin(a0)*r0,0.2);
    brushes[1] = vec3(c.x - cos(a0)*r0, c.y - sin(a0)*r0,0.2);
    brushes[2] = vec3(c.x, c.y, 0.1);
#else
	brushes[0] = vec3(0.3,0.0,0.1);
    brushes[1] = vec3(mix(0.25,0.35,sin(t*0.7)*0.5+0.5),0.26,0.29);
    brushes[6] = vec3(0.3,mix(0.0,0.1,sin(t*0.6)*0.5+0.5),0.12);
    vec2 c = vec2(0.8,0.03);
    float r0 = 0.08;
    float r1 = 0.09;
    float a0 = t;
    float a1 = a0 + radians(90.0);
    brushes[2] = vec3(c.x + cos(a0)*r0, c.y + sin(a0)*r0,0.1);
    brushes[3] = vec3(c.x - cos(a0)*r0, c.y - sin(a0)*r0,0.1);
    brushes[4] = vec3(c.x + cos(a1)*r1, c.y + sin(a1)*r1,0.1);
    brushes[5] = vec3(c.x - cos(a1)*r1, c.y - sin(a1)*r1,0.1);
#endif

// dynamic array indexing hack just so we can squeeze out a little convenience
#define CSG_INSTR(OP, A, B) if (i == k) { csg_ops[i] = CSG_OP(OP, A, B); } k++;
#define CSG_SET(A, B) CSG_INSTR(op_set, A, B)
#define CSG_OR(A, B) CSG_INSTR(op_or, A, B)
#define CSG_AND(A, B) CSG_INSTR(op_and, A, B)
#define CSG_ANDN(A, B) CSG_INSTR(op_and_not, A, B)
    for (int i = 0; i < NUM_CSG_OPS; ++i) {
	    int k = 0;
#if DEBUG_CSG_OPS
        CSG_SET(reg_a, 0);
        DEBUG_CSG_OP(reg_a, 1);
#elif TEST_SETUP
        CSG_SET (reg_a, 0);
        CSG_OR	(reg_a, 1);
        CSG_AND	(reg_a, 2);
#else
        CSG_SET	(reg_a, 2);
        CSG_AND	(reg_a, 3);
        CSG_SET	(reg_b, 4);
        CSG_AND	(reg_b, 5);
        CSG_OR	(reg_a,	reg_b);
        CSG_SET (reg_b, 0);
        CSG_ANDN(reg_b, 1);
        CSG_OR  (reg_a, reg_b);
        CSG_ANDN(reg_a, 6);
#endif
    }
#undef CSG_INSTR
#undef CSG_OR
#undef CSG_AND
#undef CSG_AND_NOT

    // precalculate brush intervals
    for (int i = 0; i < NUM_BRUSHES; ++i) {
        brush_intervals[i] = iCircle(ray_origin, ray_dir, brushes[i]);
    }
}

float interval_edge(vec2 range, float t) {
    if (range.x > t) {
    	return range.x; 
	} else if (range.y > t) {
        return -range.y;
    } else {
        return infinity;
    }
}

// given a ray scalar, compute a lower bound on the
// distance to the next fully occluding interval
// when the distance is near 0, we have hit a surface.
float scene_next_feature(float t) {
    vec4 regs;
    for (int i = 0; i < NUM_CSG_OPS; ++i) {
        CSG_OP op = csg_ops[i];
        int opcode = op[0];
        float d = get_reg(regs, op[1]);
        float src;
        if (op[2] < 0) {
            src = get_reg(regs, op[2]);
        } else {
            src = interval_edge(get_brush_interval(op[2]), t);
        }
        if (opcode == op_or) {
            d = min(d, src);
        } else if (opcode == op_and) {
            d = max(d, src);
        } else if (opcode == op_and_not) {
            d = max(d, -src);
        } else if (opcode == op_set) {
            d = src;
        }
        set_reg(regs, op[1], d);
    }
    return regs[0];
}

