// returns a gradient for 1D graph function f at position x
#define gradient1D(f,x) (f(x + get_gradient_eps()) - f(x - get_gradient_eps())) / (2.0*get_gradient_eps())
// returns a gradient for 2D graph function f at position x
#define gradient2D(f,x) vec2(f(x + vec2(get_gradient_eps(),0.0)) - f(x - vec2(get_gradient_eps(),0.0)),f(x + vec2(0.0,get_gradient_eps())) - f(x - vec2(0.0,get_gradient_eps()))) / (2.0*get_gradient_eps())
// draws a 1D graph at the current position
#define graph1D(f) { vec2 pp = get_origin(); graph(pp, f(pp.x), gradient1D(f,pp.x)); }
// draws a 2D graph at the current position
#define graph2D(f) { vec2 pp = get_origin(); graph(pp, f(pp), gradient2D(f,pp)); }

// represents the current drawing context
// you usually don't need to change anything here
struct Context {
    // screen position, query position
    vec4 position;
    vec2 shape;
    vec2 clip;
    vec2 scale;
    float line_width;
    bool premultiply;
    vec2 blur;
    vec4 source;
    vec2 start_pt;
    vec2 last_pt;
    int source_blend;
    bool has_clip;
};

float AA;
float AAINV;

// save current stroke width, starting
// point and blend mode from active context.
Context _save();
// restore stroke width, starting point
// and blend mode to a context previously returned by save()
void restore(Context ctx);

#define save(name) Context name = _save();

// draws a half-transparent debug gradient for the
// active path
void debug_gradient();
void debug_clip_gradient();
// returns the gradient epsilon width
float get_gradient_eps();

void paint() {
    float t = iTime;
    setup_globals(t);    

    float rdot = AAINV*1.0;

    scale(3.);
    translate(-0.5,0.0);

    set_line_width_px(1.0);

    // compute CSG field per pixel
    vec4 regs = vec4(infinity);
    vec2 p = get_origin();
    for (int i = 0; i < NUM_CSG_OPS; ++i) {
        CSG_OP op = csg_ops[i];
        int opcode = op[0];
        float src1 = get_reg(regs, op[1]);
        float src2 = (op[2] < 0)?get_reg(regs, op[2]):dCircle(p, get_brush(op[2]));
        float dst;
        if (opcode == op_or) {
            dst = min(src1, src2);
        } else if (opcode == op_and) {
            dst = max(src1, src2);
        } else if (opcode == op_and_not) {
            dst = max(src1, -src2);
        } else if (opcode == op_set) {
            dst = src2;
        }
        set_reg(regs, op[1], dst);
    }    
    add_field(regs[0]);
    set_source_rgb(hsl(0.6,0.7,0.95));
    fill_preserve();
    set_source_rgb(hsl(0.6,0.2,0.5));
    stroke();

    // paint brushes
    set_source_rgba(hsl(0.6,0.2,0.5,0.3));
    for (int i = 0; i < NUM_BRUSHES; ++i) {
        vec3 brush = brushes[i];
		circle(brush.xy, brush.z);
	    stroke();
    }   

    // paint ray    
    set_source_rgba(0.0,0.0,0.0,0.2);
	move_to(0.0, 0.0);
    line_to(1.0, 0.0);
    stroke();    
    set_source_rgb(vec3(0.0));
    circle(0.0, 0.0, rdot);
    fill();    
    move_to(1.0, rdot);
    line_to(1.0, -rdot);
    line_to(1.0+rdot*3.0, 0.0);
    close_path();
    fill();

    vec2 m = get_query();
    if (iMouse.z > 0.5) {
        // paint brush ranges
        vec4 iregs;
        for (int i = 0; i < 4; ++i) { iregs[i] = infinity; }
        const float h = 0.005;
        float y = h * float(NUM_CSG_OPS * 5);
        for (int i = 0; i < NUM_CSG_OPS; ++i) {
            CSG_OP op = csg_ops[i];
            int opcode = op[0];        
            float src1 = get_reg(iregs, op[1]);
            float src2 = (op[2] < 0)?get_reg(iregs, op[2]):interval_edge(get_brush_interval(op[2]), m.x);
            float dst;
            vec3 color;
            if (opcode == op_or) {
                color = hsl(0.4,0.7,0.5);
                dst = min(src1, src2);
            } else if (opcode == op_and) {
                color = hsl(0.1,1.0,0.5);
                dst = max(src1, src2);
            } else if (opcode == op_and_not) {
                color = hsl(0.9,0.7,0.5);
                dst = max(src1, -src2);
            } else if (opcode == op_set) {
                color = hsl(0.6,0.7,0.5);
                dst = src2;
            } else {
                dst = infinity;
                color = vec3(1.0,0.0,0.0);
            }
            set_reg(iregs, op[1], dst);

            vec2 total_range = vec2(infinity,-infinity);
            if (src1 != infinity) {
                total_range[0] = min(total_range[0], abs(src1));
                total_range[1] = max(total_range[1], abs(src1));
            }
            if (src2 != infinity) {
                total_range[0] = min(total_range[0], abs(src2));
                total_range[1] = max(total_range[1], abs(src2));
            }
            if (dst != infinity) {
                total_range[0] = min(total_range[0], abs(dst));
                total_range[1] = max(total_range[1], abs(dst));
            }

            float y1 = y;
            y = y - h;
            float y2 = y;
            y = y - h;
            float y3 = y;
            y = y - 3.0*h;

            set_source_rgba(0.0,0.0,0.0,0.1);
            move_to(total_range.x, y1);
            line_to(total_range.y, y1);
            move_to(total_range.x, y2);
            line_to(total_range.y, y2);
            move_to(total_range.x, y3);
            line_to(total_range.y, y3);
            stroke();

            const float R = h*0.5;
            set_source_rgb(color);
            circle(abs(src1), y1, R);
            circle(abs(src2), y2, R);
            circle(abs(dst), y3, R);
            fill();

            move_to(abs(src1),y1);
            line_to(abs(src1),0.0);

            move_to(abs(src2),y2);
            line_to(abs(src2),0.0);

            move_to(abs(dst),y3);
            line_to(abs(dst),0.0);
            stroke();
        }

        set_source_rgba(0.0,0.0,0.0,0.2);
        move_to(m.x, -1.0);
        line_to(m.x, 1.0);
        stroke();
	    float rd = scene_next_feature(m.x);
        set_source_rgb(vec3(0.0));
        circle(rd, 0.0, rdot);
        stroke();
    } else {
        float t = 0.0;
        for (int i = 0; i < (NUM_CSG_OPS + 1); ++i) {
            float dv = scene_next_feature(t);
            if (dv <= 0.0) {
                set_source_rgb(vec3(0.0));
                circle(t, 0.0, rdot);
                stroke();                
                break;
            }
            t = dv;
            if (t == infinity) break;
            float cf = float(i) / float(NUM_CSG_OPS + 1);
            set_source_rgb(hsl(cf,0.8,0.7));
            circle(t, 0.0, rdot);
            fill();
        }
    }
}

// implementation
//////////////////////////////////////////////////////////

vec2 aspect;
vec2 uv;
vec2 position;
vec2 query_position;
float ScreenH;

//////////////////////////////////////////////////////////

float det(vec2 a, vec2 b) { return a.x*b.y-b.x*a.y; }

//////////////////////////////////////////////////////////

vec3 hue(float hue) {
    return clamp(
        abs(mod(hue * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0,
        0.0, 1.0);
}

vec3 hsl(float h, float s, float l) {
    vec3 rgb = hue(h);
    return l + s * (rgb - 0.5) * (1.0 - abs(2.0 * l - 1.0));
}

vec4 hsl(float h, float s, float l, float a) {
    return vec4(hsl(h,s,l),a);
}

