  vec3 f = fract(x);
  f       = f*f*(3.0-2.0*f);
  float n = p.x + p.y*57.0 + 113.0*p.z;
  return mix(mix(mix( hash(n+0.0), hash(n+1.0),f.x),
   mix( hash(n+57.0), hash(n+58.0),f.x),f.y),
  mix(mix( hash(n+113.0), hash(n+114.0),f.x),
   mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
}

float fbm (vec3 p, vec3 speed) {
    float value = 0.0;
    float amplitud = .5;
    for (float i = 1.; i <= 6.; i++) {
        value += amplitud * noiseIQ(p);
        p *= 2.;
    	p += speed;
        amplitud *= .5;
    }
    return value;
}

mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,s,-s,c); }

float sdSphere (vec3 p, float r) { return length(p)-r; }
float sdCylinder (vec2 p, float r) { return length(p)-r; }
float sdCylinder (vec3 p, vec3 c) { return length(p.xz-c.xy)-c.z; }
float sdBox (vec3 p, vec3 b) { vec3 d = abs(p) - b; return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0)); }

vec3 moda (vec2 p, float count) {
    float an = TAU/count;
    float a = atan(p.y,p.x)+an/2.;
    float c = floor(a/count);
    a = mod(a,an)-an/2.;
    return vec3(vec2(cos(a),sin(a))*length(p),c);
}

float smin (float a, float b, float k) {
    float h = clamp(.5+.5*(b-a)/k, 0., 1.);
    return mix(b,a,h)-k*h*(1.-h);
}

