float map (vec3 pos) {
    float scene = 1.;

    // fire
    vec3 posNoisy = pos*2.;
    posNoisy.xz *= rot(iTime);
    vec3 timeNoisy = 5.*vec3(iTime*.6,iTime*-.9,iTime*.8);
    float noisy = fbm(posNoisy, timeNoisy);
    vec3 posFire = pos;
    vec3 plankSize = vec3(2.,.5,.1);
    posFire.x /= 2.;
    float height = 3.;
    posFire.y -= noisy*height-plankSize.y*2.;
    scene = sdSphere(posFire, noisy*3.);
    scene = max(scene, -sdSphere(posFire, .6));

    // text
    vec2 posText = pos.xy / 10.;
    vec3 crop = vec3(1000,1000,plankSize.z);
    posText.y /= 2.;
    posText.y -= .04;
    float text = getText1(posText);
    text = max(text, sdBox(pos, crop));
    scene = min(scene, text);
    posText *= 1.75;
    posText.y += .06;
    text = getText2(posText);
    text = max(text, sdBox(pos, crop));
    scene = min(scene, text);

    return scene;
}

