vec3 Fire(in vec3 q)
{
    vec3 s = vec3(q) - vec3(0.0,iTime*1.8, 0.0);
    s = fbm(s);
    return vec3(max((s.x)*0.9, 0.5*abs(q.x)), smin(s.y, q.y), q.z);
}

float map(in vec3 p)
{
    vec3 q = Fire(p);

    float sphere = sdSphere(q, 1.0);

    return sphere;
}

float intersect(in vec3 ro, in vec3 rd)
{
    float maxD = 5.0;
    int iterations = 4;
    float h = 1.0;
    float t = 0.0;

    for(int i = 0; i < iterations; i++)
    {
        if(h < 0.001 || t > maxD)
            break;
        h = map(ro+rd*t);
        t += h;
    }
    if( t>maxD ) t=-1.0;
    return t;

}

vec3 PostProcess(in vec2 fc)
{
    vec3 oColor = vec3(0.);

    int vertical = int(mod(fc.x, 6.0));
    if(vertical < 2) oColor.x = 1.0;
    else if(vertical >= 2 && vertical < 4) oColor.y = 1.0;
    else oColor.z = 1.0;


    float horizontal = (mod(fc.y, 6.0));
    oColor *= vec3(horizontal/6.0);

    return oColor;
}


