const uint A0 = 0x67452301u,
           B0 = 0xefcdab89u,
           C0 = 0x98badcfeu,
           D0 = 0x10325476u;


void MD5Round(inout uint A, inout uint B, inout uint C, inout uint D, in uint[16] M, uint i){
    uint F, g;
    if (i < 16u){
        F = (B & C) | ((0xffffffffu-B) & D);
        g = i;
    } else if (i < 32u) {
        F = (D & B) | ((0xffffffffu-D) & C);
        g = (5u*i + 1u) % 16u;
    } else if (i < 48u) {
        F = B ^ C ^ D;
        g = (3u*i + 5u) % 16u;
    } else {
        F = C ^ (B | (0xffffffffu-D));
        g = (7u*i) % 16u;
    }

    F = F + A + K[i] + M[g];
    A = D;
    D = C;
    C = B;
    B = B + (F << s[i]) | (F >> (32-s[i]));
}

uint[4] MD5( uint[16] M) {
    uint A = A0, B = B0, C = C0, D = D0;    

    for(uint i = 0u; i < 64u; i++) {
        MD5Round(A,B,C,D,M,i);
    }

    return uint[](A,B,C,D);
}

