// https://www.shadertoy.com/view/Xl2XWt
mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in vec3 up )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,up));
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

vec3 getColor(vec2 p) {

    vec3 camUp = vec3(0,-1,0);
    vec3 camTar = vec3(0.);
    vec3 camPos = vec3(0,0,1.25);

    mat3 camMat = calcLookAtMatrix(camPos, camTar, camUp);
    float focalLength = 2.;
    vec3 rayDirection = normalize(camMat * vec3(p, focalLength));

    CastRay castRay = CastRay(camPos, rayDirection);
    Hit hit = raymarch(castRay);

    #ifdef DEBUG
        return hit.normal * .5 + .5;
    #endif

    if ( hit.isBackground || ! hit.model.material.transparent) {
        return shadeSurface(hit);
    }

    return shadeTransparentSurface(hit);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;    
    vec2 m = iMouse.xy / iResolution.xy;

    time = iTime;
    time /= 4.;
    time = mod(time, 1.);

    p /= 1.5;

    vec3 color = getColor(p);

    fragColor = vec4(color,1.0);
}

// Cubic With Quadratic Segments
// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)

// set to 2,3,4 to see approximation with 2-4 segments
#define SEGMENTS 2

// 2 segments: matches value and first derivative at the end points,
// as well as a value in the middle with a sometimes barely visible
// discontinuity. can get bent fairly out of shape but looks plausible.

// 3 segments: matches seven values evenly spaced from end to end
// without matching derivatives. looks pretty clean except when derivatives
// at both ends assume high values with equal sign.

// 4 segments: matches nine values evenly spaced from end to end
// without matching derivatives. quite convincing even when strongly bent.

// ax^3 + bx^2 + cx + d
vec4 cubic_coeffs;
// f(x) = ax^3 + bx^2 + cx + d
float cubic(float x) {
    float x2 = x*x;
    float x3 = x2*x;
    return dot(cubic_coeffs, vec4(x3,x2,x,1.0));
}
// f'(x) = 3ax^2 + 2bx + c
float dcubic(float x) {
    float x2 = x*x;
    return dot(cubic_coeffs.xyz * vec3(3.0,2.0,1.0), vec3(x2,x,1.0));
}
// f''(x) = 6ax + 2b
float ddcubic(float x) {
    return dot(cubic_coeffs.xy * vec2(6.0,2.0), vec2(x, 1.0));
}
// f'''(x) = 6a
float dddcubic(float x) {
    return cubic_coeffs.x * 6.0;
}
// cubic from points at f(0),f(1),f'(0),f'(1)
vec4 cubic_from_points(float P, float Q, float U, float V) {
	float a = 2.0*(P - Q) + U + V;
	float b = (Q - P)*3.0 - 2.0*U - V;
	float c = U;
	float d = P;
	return vec4(a,b,c,d);
}

// approximate a cubic segment 
// f(x) = k0 + k1*x + k2*x^2 + k3*x^3
// with two quadratic segments
// g(x) = i0 + i1*x + i2*x^2
// in the interval (c-h, c+h) with matching first derivatives at f'(c-h) and f'(c+h)
void quadratic_from_cubic(vec4 cubic, float c, float h, out vec3 left, out vec3 right) {
    float k0 = cubic[0];
    float k1 = cubic[1];
    float k2 = cubic[2];
    float k3 = cubic[3];
    // 24 ops
    float c2 = c*c;
    float c2k3 = c2*k3;
    float h2k3 = h*h*k3;
    float ck3 = c*k3;
    float q0 = (c*(c2k3 + h2k3)) + k0;
    float q1 = (-3.0*c2k3 - h2k3) + k1;
    float q2 = 3.0*ck3 + k2;
    float w2 = 2.0*h*k3;
    float w0 = w2*c2;
    float w1 = -4.0*ck3*h;
    left = vec3(q0 - w0, q1 - w1, q2 - w2);
    right = vec3(q0 + w0, q1 + w1, q2 + w2); 
}

// approximate a cubic segment 
// f(x) = k0 + k1*x + k2*x^2 + k3*x^3
// with a quadratic segment
// g(x) = i0 + i1*x + i2*x^2
// in the interval (c-h, c+h)
vec3 quadratic_from_cubic(vec4 cubic, float c, float h) {
    float k0 = cubic[0];
    float k1 = cubic[1];
    float k2 = cubic[2];
    float k3 = cubic[3];
    // 14 ops
    float c2 = c*c;
    float c3 = c2*c;
    float h2 = h*h;
    return vec3(
		(c3 - c*h2)*k3 + k0,
        -(3.0*c2 - h2)*k3 + k1,
        3.0*c*k3 + k2);
}

vec3 quadratic_coeffs[SEGMENTS];
#if (SEGMENTS == 4)
float quadratic(float x) { 
    if (x < 0.25) {
    	return dot(quadratic_coeffs[0], vec3(x*x,x,1.0)); 
    } else if (x < 0.5) {
    	return dot(quadratic_coeffs[1], vec3(x*x,x,1.0)); 
    } else if (x < 0.75) {
    	return dot(quadratic_coeffs[2], vec3(x*x,x,1.0)); 
    } else {
        return dot(quadratic_coeffs[3], vec3(x*x,x,1.0)); 
    }
}
#elif (SEGMENTS == 3)
float quadratic(float x) { 
    if (x < 0.333) {
    	return dot(quadratic_coeffs[0], vec3(x*x,x,1.0)); 
    } else if (x < 0.667) {
    	return dot(quadratic_coeffs[1], vec3(x*x,x,1.0)); 
    } else {
        return dot(quadratic_coeffs[2], vec3(x*x,x,1.0)); 
    }
}
#else
float quadratic(float x) { 
    if (x < 0.5) {
    	return dot(quadratic_coeffs[0], vec3(x*x,x,1.0)); 
    } else {
    	return dot(quadratic_coeffs[1], vec3(x*x,x,1.0)); 
    }
}
#endif

void setup(float t) {

    cubic_coeffs = cubic_from_points(
        sin(t*0.57), 
        cos(t*0.37),
        sin(t*0.22), 
        cos(t*0.69));

#if (SEGMENTS == 4)
    quadratic_coeffs[0] = quadratic_from_cubic(cubic_coeffs.wzyx, 0.125, 0.125).zyx;
    quadratic_coeffs[1] = quadratic_from_cubic(cubic_coeffs.wzyx, 0.375, 0.125).zyx;
    quadratic_coeffs[2] = quadratic_from_cubic(cubic_coeffs.wzyx, 0.625, 0.125).zyx;
    quadratic_coeffs[3] = quadratic_from_cubic(cubic_coeffs.wzyx, 0.875, 0.125).zyx;
#elif (SEGMENTS == 3)
    quadratic_coeffs[0] = quadratic_from_cubic(cubic_coeffs.wzyx, 0.16667, 0.16667).zyx;
    quadratic_coeffs[1] = quadratic_from_cubic(cubic_coeffs.wzyx, 0.5, 0.16667).zyx;
    quadratic_coeffs[2] = quadratic_from_cubic(cubic_coeffs.wzyx, 0.83333, 0.16667).zyx;
#else
    vec3 q0, q1;
    quadratic_from_cubic(cubic_coeffs.wzyx, 0.5, 0.5, q0, q1);
	quadratic_coeffs[0] = q0.zyx;
    quadratic_coeffs[1] = q1.zyx;
#endif

}

