void paint() {
    float t = iTime;
    setup(t);
    float rdot = AAINV*3.0;

    translate(-0.5,0.0);

    set_source_rgb(vec3(1.0));
    clear();

    set_line_width_px(1.0);
    set_source_rgba(0.0,0.0,0.0,0.5);
    move_to(0.0,-1.0);
    line_to(0.0,1.0);
    move_to(1.0,-1.0);
    line_to(1.0,1.0);
    stroke();

    set_source_rgba(0.5,0.0,0.0,0.2);
    graph1D(cubic);
    set_source_rgb(vec3(0.0));
    stroke();
    graph1D(quadratic);
    set_source_rgb(0.0,0.0,1.0);
    stroke();

    #if 0
