void initRay(vec2 p){
    linesCol = pow(linesCol,vec3(GAMMA));
    linesBorderCol = pow(linesBorderCol,vec3(GAMMA));
    lineWidth = sin(0.2 * iTime)*0.01+0.015;
#ifdef VARIANT2 
    float ang = 0.12 * sin(iTime + WOBBLE * p.x);
    float d = firstRay.O.y * tan(ang);
    firstRay.O.x -= d; firstRay.O.y =0.;
    firstRay.D = vec2(sin(ang), cos(ang));
#endif    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float scaleFactor= 5.;//0.05*iMouse.y;
	vec2 p = scaleFactor*(fragCoord.xy-0.5*iResolution.xy) / iResolution.y;
	aaScale=0.75*scaleFactor/iResolution.y;

    initRay(p);

    vec2 z = vec2(0.);
	int nbrFold = fold(p, z);
    float parity = 1. - fract(float(nbrFold) * 0.5) * 2.;

	fragColor = drawIt(z, parity);
    fragColor.xyz = pow(fragColor.xyz, vec3(1./GAMMA));
}

// Fork of https://www.shadertoy.com/view/MlcXDB

#define NGON_NSIDES_MIN 3.
#define NGON_NSIDES_MAX 16.

#define time (.25 * iTime)

#define PI  3.14159
#define XI  (.5 * PI)
#define TAU (2. * PI)

// SDF stolen from https://www.shadertoy.com/view/MlcXDB
float sdf_ngon(vec2 uv, float radius, float nsides){
  float angle = atan(uv.y, uv.x) + XI;
  float split = TAU / nsides;
  return length(uv) * cos(split * floor(.5 + angle / split) - angle) - radius;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv =  (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec2  ngon_uv = uv - .25 * vec2(2. * cos(iTime), sin(2. * iTime));  // Displace!
          ngon_uv *= mat2(cos(time), sin(time), -sin(time), cos(time));  // Rotate!
    float ngon_radius = .5;
    float ngon_nsides = NGON_NSIDES_MIN + floor(mod(iTime, NGON_NSIDES_MAX - NGON_NSIDES_MIN + 1.));
    float ngon_antialias = 5. / iResolution.y;
    float ngon = smoothstep(ngon_antialias, .0, sdf_ngon(ngon_uv, ngon_radius, ngon_nsides));

    fragColor.rgb = vec3(ngon);
}

