const float zoomSpeed			= 3.0;	// how fast to zoom (negative to zoom out)
const float zoomScale			= 0.2;	// how much to multiply overall zoom (closer to zero zooms in)
const int recursionCount		= 4;	// how deep to recurse
const int glyphSize				= 3;	// width & height of glyph in pixels
const float curvature			= -6.0;	// time warp to add curvature

//////////////////////////////////////////////////////////////////////////////////
// Precached values and math

const float glyphSizeF = float(glyphSize);
const float glyphSizeLog = log(glyphSizeF);

const float e = 2.718281828459;
const float pi = 3.14159265359;
float RandFloat(int i) { return (fract(sin(float(i)) * 43758.5453)); }
int RandInt(int i) { return int(100000.0*RandFloat(i)); }

vec3 HsvToRgb(vec3 c) 
{
    float s = c.y * c.z;
    float s_n = c.z - s * .5;
    return vec3(s_n) + vec3(s) * cos(2.0 * pi * (c.x + vec3(1.0, 0.6666, .3333)));
}

//////////////////////////////////////////////////////////////////////////////////
// Color and image manipulation

float GetRecursionFade(int r, float timePercent)
{
    if (r > recursionCount)
        return timePercent;

    // fade in and out recusion
    float rt = max(float(r) - timePercent, 0.0);
    float rc = float(recursionCount);
    return rt / rc;
}

vec3 InitPixelColor() { return vec3(0); }
vec3 CombinePixelColor(vec3 color, float timePercent, int i, int r, vec2 pos, ivec2 glyphPos, ivec2 glyphPosLast)
{
    i = (i+r) + (glyphPosLast.y + glyphPos.y);

    vec3 myColor = vec3
    (
    	mix(-0.3, 0.3, RandFloat(i)),
    	mix(0.0, 0.8, RandFloat(i + 10)),
        mix(0.0, 0.8, RandFloat(i + 20))
    );

    // combine with my color
    float f = GetRecursionFade(r, timePercent);
    myColor.y = pow(myColor.y, 3.0);
    myColor.z = pow(myColor.z, 3.0);
    color += myColor*f;
    return color;
}

vec3 FinishPixel(vec3 color, vec2 uv)
{
    // color wander
    color.x += 0.01*iTime;

    // convert to rgb
    color = HsvToRgb(color);
    return color;
}

vec2 InitUV(vec2 uv)
{
    float theta = 4.0*pi/4.0;
	float c = cos(theta);
	float s = sin(theta);
	//uv = vec2((uv.x*c - uv.y*s), (uv.x*s + uv.y*c));

	// wave
	uv.x += 0.03*sin(7.0*uv.y + 0.17*iTime);
	uv.y += 0.03*sin(7.0*uv.x + 0.13*iTime);
	uv.x += 0.2*sin(2.0*uv.y + 0.31*iTime);
	uv.y += 0.2*sin(2.0*uv.x + 0.27*iTime);
    return uv;
}

//////////////////////////////////////////////////////////////////////////////////
// Fractal functions

ivec2 GetFocusPos(int i) { return ivec2(glyphSize/2); }

// get color of pos, where pos is 0-1 point in the glyph
vec3 GetPixelFractal(vec2 pos, int iterations, float timePercent)
{
	ivec2 glyphPosLast = GetFocusPos(-2);
	ivec2 glyphPos =     GetFocusPos(-1);
	vec3 color = InitPixelColor();

	for (int r = 0; r <= recursionCount + 1; ++r)
	{
        color = CombinePixelColor(color, timePercent, iterations, r, pos, glyphPos, glyphPosLast);
        if (r > recursionCount)
			return color;

        // update pos
        pos *= glyphSizeF;

        // get glyph and pos within that glyph
        glyphPosLast = glyphPos;
        glyphPos = ivec2(pos);

        // next glyph
		pos -= vec2(floor(pos));
	}
}

//////////////////////////////////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// use square aspect ratio
	vec2 uv = fragCoord;
	uv = fragCoord / iResolution.y;
	uv -= vec2(0.5*iResolution.x / iResolution.y, 0.5);
    uv = InitUV(uv);

    // time warp
    float time = iTime + curvature*pow(length(uv), 0.2);

	// get time 
	float timePercent = time*zoomSpeed;
	int iterations = int(floor(timePercent));
	timePercent -= float(iterations);

	// update zoom, apply pow to make rate constant
	float zoom = pow(e, -glyphSizeLog*timePercent);
	zoom *= zoomScale;

	// get offset
	vec2 offset = vec2(0);
    const float gsfi = 1.0 / glyphSizeF;
	for (int i = 0; i < 13; ++i)
		offset += (vec2(GetFocusPos(i)) * gsfi) * pow(gsfi,float(i));

	// apply zoom & offset
    vec2 uvFractal = uv * zoom + offset;

	// check pixel recursion depth
	vec3 pixelFractalColor = GetPixelFractal(uvFractal, iterations, timePercent);
    pixelFractalColor = FinishPixel(pixelFractalColor, uv);

	// apply final color
	fragColor = vec4(pixelFractalColor, 1.0);
}

// "Reflecting Machine" by dr2 - 2017
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float s);
float PrCylDf (vec3 p, float r, float h);
float PrFlatCylAnDf (vec3 p, float b, float r, float w, float h);
float SmoothBump (float lo, float hi, float w, float x);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec2 Rot2D (vec2 q, float a);

#define NBLK 12.

vec3 qnBlk[2], ltDir, vnBlk, qHit, qGear, vnCylOut, oblPos, oblSize;
vec2 qBlk;
float dstFar, tCur, bEdge, tCyc, tMov, bCylRad, bCylHt, dCylOut;
int idObj;
const int idGr = 1, idPln = 2, idConv = 3, idSup = 4, idAx = 5, idBas = 6,
   idWhl = 7, idSpl = 8;
const float pi = 3.14159;

float ObjDf (vec3 p)
{
  vec4 a4;
  vec3 q, bPos;
  float dMin, d, angRot, r, a;
  dMin = dstFar;
  q = p - vec3 (1.13 + bEdge, bEdge, 1.);
  r = length (q.yz);
  q.yz = Rot2D (q.yz, -0.5 * pi * tMov / 5.);
  a = (r > 0.) ? atan (q.z, - q.y) / (2. * pi) : 0.;
  q.yz = Rot2D (q.yz, 2. * pi * (floor (8. * a + 0.5)) / 8.);
  q.z = abs (q.z);
  d = max (min (min (abs (r - 1.01) - 0.1, r - 0.3),
     max (r - 1., dot (q.yz, vec2 (sin (0.8 * 2. * pi / 32.),
     cos (0.8 * 2. * pi / 32.))))), abs (q.x) - 0.03);
  if (d < dMin) { dMin = d;  idObj = idWhl;  qHit = q; }
  d = PrBoxDf (p - vec3 (0., 0.98, 1.), vec3 (1.12, 0.02, 0.1));
  d = min (d, PrBoxDf (p - vec3 (-1.018, 0.98, 0.51), vec3 (0.1, 0.02, 0.49 - bEdge)));
  if (d < dMin) { dMin = d;  idObj = idPln; }
  d = PrFlatCylAnDf (p - vec3 (-0.05, -0.21, 0.), 1., 0.2, 0.01, 0.1);
  if (d < dMin) { dMin = d;  idObj = idConv; }
  q = p - vec3 (-0.05, -0.21, 0.);  q.x = abs (q.x) - 1.;
  d = PrCylDf (q, 0.19, 0.12);
  if (d < dMin) { dMin = d;  idObj = idSpl;  qHit = q; }
  q = p - vec3 (0.65, -0.1, 1.);  q.x = abs (q.x) - 0.3;
  d = PrRoundBoxDf (q, vec3 (0.01, 1.04, 0.06), 0.02);
  d = min (d, PrRoundBoxDf (p - vec3 (-1.05, -0.1, 1.), vec3 (0.04, 1.04, 0.01), 0.02));
  q = p - vec3 (-0.05, -0.6, 0.);  q.xz = abs (q.xz) - vec2 (1., 0.2);
  d = min (d, PrRoundBoxDf (q, vec3 (0.04, 0.54, 0.01), 0.02));
  q = p - vec3 (-0.33, -0.6, 1.555);  q.x = abs (q.x) - 0.3;
  d = min (d, PrRoundBoxDf (q, vec3 (0.01, 0.54, 0.04), 0.02));
  if (d < dMin) { dMin = d;  idObj = idSup; }
  q = p - vec3 (0.65, bEdge, 1.);
  d = PrCylDf (q.yzx, 0.04, 0.62);
  d = min (d, PrCylDf (p - vec3 (0.95, -0.21, 0.), 0.03, 0.27));
  d = min (d, min (d, PrCylDf (p - vec3 (-1.05, -0.21, 0.55), 0.03, 0.82)));
  d = min (d, PrCylDf ((p - vec3 (-0.36, -0.21, 1.555)).yzx, 0.03, 0.51));
  if (d < dMin) { dMin = d;  idObj = idAx; }
  d = PrBoxDf (p - vec3 (0., -1.2, 0.9), vec3 (1.6, 0.05, 1.4));
  if (d < dMin) { dMin = d;  idObj = idBas; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

float GearWlDf (vec3 p, float rad, float wlThk, float tWid, float nt, float angRot, 
   bool bev, float d)
{
  p.xy = Rot2D (p.xy, angRot);
  p.xy = Rot2D (p.xy, floor (nt * atan (p.y, - p.x) / (2. * pi) + 0.5) * 2. * pi / nt);
  if (bev) p.xy *= 1.2 - 0.2 * p.z / wlThk;
  return max (d, - rad - 0.95 * (p.x - 2. * clamp (1.5 * tWid +
     0.5 * p.x * step (0., p.x) - abs (p.y), 0., tWid)));
}

float GearDf (vec3 p)
{
  vec3 q;
  float dMin, angRot, wlThk, tWid, nt, rad, d;
  dMin = dstFar / 0.3;
  angRot = 0.1 * pi * tMov;
  rad = 0.3;
  wlThk = rad / 7.;
  tWid = rad / 10.;
  nt = 20.;
  q = - (p - vec3 (-1.05, -0.21, 1.3));
  d = max (length (q.xy) - rad, abs (q.z) - wlThk);
  if (d < dMin) {
    d = GearWlDf (q, rad, wlThk, tWid, nt, angRot, true, d);
    if (d < dMin) { dMin = d;  qGear = q / rad; }
  } else dMin = min (dMin, d);
  q = (p - vec3 (-1.05 + 0.85 * rad, -0.21, 1.3 + 0.85 * rad)).yzx;
  d = max (length (q.xy) - rad, abs (q.z) - wlThk);
  if (d < dMin) {
    d = GearWlDf (q, rad, wlThk, tWid, nt, angRot + pi / nt, true, d);
    if (d < dMin) { dMin = d;  qGear = q / rad; }
  } else dMin = min (dMin, d);
  angRot = -0.1 * pi * tMov;
  rad = 0.39;
  wlThk = rad / 15.;
  tWid = rad / 16.;
  nt = 36.;
  q = (p - vec3 (0.1, bEdge, 1.)).yzx;
  d = max (length (q.xy) - rad, abs (q.z) - wlThk);
  if (d < dMin) {
    d = GearWlDf (q, rad, wlThk, tWid, nt, angRot, false, d);
    if (d < dMin) { dMin = d;  qGear = q / rad; }
  } else dMin = min (dMin, d);
  q = - (p - vec3 (0.1, -0.21, 1.555)).zyx;
  d = max (length (q.xy) - rad, abs (q.z) - wlThk);
  if (d < dMin) {
    d = GearWlDf (q, rad * 2./3., wlThk, tWid, nt * 2./3., angRot * 3./2., false, d);
    if (d < dMin) { dMin = d;  qGear = q / rad; }
  } else dMin = min (dMin, d);
  return 0.3 * dMin;
}

float GearRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 250; j ++) {
    d = GearDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 GearNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.0005, -0.0005, 0.);
  v = vec4 (GearDf (p + e.xxx), GearDf (p + e.xyy), GearDf (p + e.yxy), GearDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.0005, -0.0005, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec4 MvBlkPos (float t)
{
  vec3 p;
  float a;
  t = mod (t, tCyc);
  if (t < 20.) {
    a = 0.;
    p = vec3 (-1.018 + 2.118 * t / 20., bEdge, 0.);
  } else if (t < 25.) {
    a = 0.5 * pi * (t - 20.) / 5.;
    p = vec3 (1.1, bEdge + 1. * sin (a), 1. - 1. * cos (a));
  } else if ( t < 45.) {
    a = 0.5 * pi;
    p = vec3 (1.1 - 2.118 * (t - 25.) / 20., 1. + bEdge, 1.);
  } else if (t < 55.) {
    a = 0.5 * pi;
    p = vec3 (-1.018, 1. + bEdge, 1. - 1. * (t - 45.) / 10.);
  } else {
    t = 0.5 * (t - 55.);
    a = -0.5 * pi * t;
    p = vec3 (-1.018, 1. + bEdge - t * t, 0.);
  }
  return vec4 (p, a);
}

float MvBlkHit (vec3 ro, vec3 rd)
{
  vec4 a4;
  vec3 rm, rdm, u, v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  for (float k = 0.; k < NBLK; k ++) {
    a4 = MvBlkPos (tMov + tCyc * k / NBLK);
    rm = ro - a4.xyz;
    rdm = rd;
    rm.zy = Rot2D (rm.zy, a4.w);
    rdm.zy = Rot2D (rdm.zy, a4.w);
    v = rm / rdm;
    tp = bEdge / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = max (max (tm.x, tm.y), tm.z);
    df = min (min (tp.x, tp.y), tp.z);
    if (df > 0. && dn < min (df, dMin)) {
      dMin = dn;
      vnBlk = - sign (rdm) * step (tm.zxy, tm) * step (tm.yzx, tm);
      u = (v + dn) * rdm;
      qBlk = vec2 (dot (u.zxy, vnBlk), dot (u.yzx, vnBlk));
      vnBlk.zy = Rot2D (vnBlk.zy, - a4.w);
    }
  }
  return dMin;
}

float BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  dMin = dstFar;
  if (df > 0. && dn < df) {
    dMin = dn;
    qnBlk[0] = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    qnBlk[1] = - sign (rd) * step (tp, tp.zxy) * step (tp, tp.yzx);
  }
  return dMin;
}

void InCylHit (vec3 ro, vec3 rd)
{
  vec3 s;
  float a, b, w, ws, srdy;
  dCylOut = dstFar;
  vnCylOut = vec3 (0.);
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - bCylRad * bCylRad);
  if (w > 0.) {
    ws = sqrt (w);
    srdy = sign (rd.y);
    if (a > 0.) {
      dCylOut = (- b + ws) / a;
      s = ro + dCylOut * rd;
    } else s.y = bCylHt;
    if (abs (s.y) < bCylHt) vnCylOut.xz = - s.xz / bCylRad;
    else {
      dCylOut = (- srdy * ro.y + bCylHt) / abs (rd.y);
      vnCylOut.y = - srdy;
    }
  }
}

#define SQRT3 1.7320508

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./SQRT3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (SQRT3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec3 HexGrid (vec2 p)
{
  vec2 q;
  p -= HexToPix (PixToHex (p));
  q = abs (p);
  return vec3 (p, (SQRT3/2.) - q.x + 0.5 * min (q.x - SQRT3 * q.y, 0.));
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col;
  float sd, f;
  if (rd.y < 0.) {
    rd.y = 0.;
    rd = normalize (rd);
  }
  ro.xz += tCur;
  sd = max (dot (rd, ltDir), 0.);
  f = Fbm2 (0.04 * (rd.xz * (100. - ro.y) / max (rd.y, 0.001) + ro.xz));
  col = vec3 (0.15, 0.25, 0.5) + 0.1 * pow (sd, 16.) + 0.2 * pow (sd, 256.);
  col = mix (col, vec3 (0.9), clamp (3. * (f - 0.5) * rd.y + 0.1, 0., 1.));
  return col;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 vn, col, gCol, qh;
  vec2 qw, iw;
  float hy, hhy, hw, ww, f, b, aa, sRotH, sRotV;
  InCylHit (ro + vec3 (0., - bCylHt + 1.25, 0.), rd);
  if (vnCylOut.y == 0.) {
    ro += dCylOut * rd;
    vn = vnCylOut;
    hy = (ro.y - 1.75) / bCylHt;
    hhy = abs (hy) - 0.43;
    aa = atan (vn.x, - vn.z) /pi;
    sRotH = mod (64. * 0.5 * (1. + aa) + 0.5, 1.) - 0.5;
    hw = 0.29;
    ww = 0.28;
    qw = abs (vec2 (sRotH, hhy));
    if (qw.x < ww && qw.y < hw) {
      qw = abs (qw - 0.5 * vec2 (ww, hw)) - vec2 (0.44 * ww, 0.47 * hw);
      if (max (qw.x, qw.y) < 0.) col = 0.7 * SkyCol (ro, reflect (rd, vn));
      else col = vec3 (0.5, 0.5, 0.3) * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
         0.5 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    } else {
      col = vec3 (0.8, 0.7, 0.5);
      qw -= vec2 (ww, hw);
      if (abs (hy) > (1. - 0.85/16.) || max (qw.x, qw.y) < 0.02) {
        vn.xz = Rot2D (vn.xz, - pi * aa);
        if (abs (hy) > (1. - 0.85/16.)) {
          sRotV = (1. - abs (2. * SmoothBump (1. - 0.9/16., 0.97, 0.03, abs (hy)) - 1.));
        } else {
          sRotV = (1. - abs (2. * SmoothBump (hw, hw + 0.02, 0.02, abs (hhy)) - 1.)) * sign (hhy);
          vn.xz = Rot2D (vn.xz, 0.4 * pi * sign (sRotH) *
             (1. - abs (2. * SmoothBump (- (ww + 0.02), ww + 0.02, 0.04, sRotH) - 1.)));
        }
        vn.yz = Rot2D (vn.yz, -0.2 * pi * sRotV * sign (hy));
        vn.xz = Rot2D (vn.xz, pi * aa);
      } else {
        qw = 16. * vec2 (24. * (0.5 * aa), hy);
        col *= 1. - 0.3 * Noisefv2 (10. * qw);
        iw = floor (qw);
        if (2. * floor (iw.y / 2.) != iw.y) {
          qw.x += 0.5;
          iw = floor (qw);
        }
        qw = smoothstep (0.07, 0.08, abs (fract (qw + 0.5) - 0.5));
        col *= (0.8 + 0.2 * qw.x * qw.y);
      }
      col = col * (0.3 + 0.7 * max (dot (vn, ltDir), 0.)) +
         0.1 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    }
  } else if (vnCylOut.y > 0.) {
    ro += dCylOut * rd;
    b = 1. - smoothstep (-0.1, -0.01, rd.y) * smoothstep (0.4, 0.8, dCylOut / (1.6 * bCylRad));
    qh = HexGrid (4. * ro.zx);
    f = max (length (qh.xy) - 0.5, 0.) * b;
    vn = vec3 (0., Rot2D (vec2 (1., 0.), 4. * f * f));
    vn.zx = vn.z * vec2 (qh.x, - qh.y) / length (qh.xy);
    vn = VaryNf (50. * ro, vn, 0.2 * b);
    gCol = vec3 (0.7, 0.7, 0.8) * (1. - 0.1 * b * Noisefv2 (100. * ro.xz));
    col = gCol * (1. - min (0.2 * b * (1. - smoothstep (0.03, 0.06, qh.z)), 0.1));
    col = col * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       0.1 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    col *= 0.7 + 0.3 * smoothstep (0., 4., BlkHit (ro + oblPos, ltDir, oblSize));
  } else col = SkyCol (ro, rd);
  return col;
}

vec4 ObjCol (vec3 ro, vec3 vn)
{
  vec3 col, q;
  float spec, r, a, s;
  if (idObj == idWhl) {
    col = vec3 (0.7, 0.9, 0.3);
    q = ro - vec3 (1.13 + bEdge, bEdge, 1.);
    r = length (q.yz);
    q.yz = Rot2D (q.yz, -0.5 * pi * tMov / 5.);
    a = (r > 0.) ? atan (q.z, - q.y) / (2. * pi) : 0.;
    a = mod (64. * a + 0.5, 1.);
    if (r > 0.99) vn.yz = Rot2D (vn.yz, - sin (a - 0.5));
    if (r > 0.92) col *= 0.7 + 0.3 * SmoothBump (0.05, 0.95, 0.01, a);
    spec = 0.5;
  } else if (idObj == idGr) {
    col = vec3 (0.8, 0.7, 0.3);
    spec = 0.5;
  } else if (idObj == idSpl) {
    q = ro - vec3 (-0.05, -0.21, 0.);
    s = sign (q.x);
    q.x = abs (q.x) - 1.; 
    q.xy = Rot2D (q.xy, 0.1 * s * pi * tMov);
    a = atan (q.x, - q.y) / (2. * pi);
    a = mod (16. * a + 0.5, 1.);
    if (abs (ro.z) > 0.1) {
      col = vec3 (0.8, 0.8, 0.85);
      spec = 0.5;
    } else col = vec3 (0.4, 0.4, 0.3);
    col *= 0.5 + 0.5 * SmoothBump (0.05, 0.95, 0.01, a);
  } else if (idObj == idAx) {
    col = vec3 (0.8, 0.8, 0.85);
    spec = 0.5;
  } else if (idObj == idPln) {
    col = vec3 (0.3, 0.2, 0.);
    spec = 0.;
  } else if (idObj == idConv) {
    q = ro - vec3 (-0.05, -0.21, 0.);
    col = vec3 (0.8, 0.8, 0.2);
    if (abs (q.x) < 1. && abs (vn.y) > 0.99 && sign (vn.y) != sign (q.y))
       col *= 1. - 0.9 * SmoothBump (0.45, 0.55, 0.03,
       mod (10. * (q.x - sign (q.y) * mod (0.9 * tMov, 20.) * 0.1 * 2. * pi * 0.19), 1.));
  } else if (idObj == idSup) {
    col = vec3 (0.4, 0.3, 0.1);
  } else if (idObj == idBas) {
    q = ro;
    q.z -= 0.9;
    q.xz = abs (q.xz) - vec2 (1.55, 1.35);
    col = (max (q.x, q.z) > 0.) ? vec3 (0.2, 0.1, 0.) : vec3 (0., 0.1, 0.05);
    spec = 0.;
  }
  return vec4 (col, spec);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col, roo, rdo, q;
  float dstObj, dstGear, dstMvBlk, dstBlk, reflAtn;
  bool doRefl, hitOrig;
  oblPos = vec3 (0., -0.05, -0.9);
  oblSize = vec3 (1.6, 1.3, 1.4);
  col = BgCol (ro, rd);
  dstBlk = BlkHit (ro + oblPos, rd, oblSize);
  roo = ro;
  rdo = rd;
  if (dstBlk < dstFar) {
    tCyc = 57.;
    tMov = 2. * tCur;
    bEdge = 0.08;
    reflAtn = 1.;
    hitOrig = false;
    for (int nr = 0; nr < 3; nr ++) {
      doRefl = false;
      dstGear = GearRay (ro, rd);
      dstObj = ObjRay (ro, rd);
      if (dstGear < min (dstObj, dstFar)) {
        dstObj = dstGear;
        idObj = idGr;
      }
      dstMvBlk = MvBlkHit (ro, rd);
      if (dstMvBlk < min (dstObj, dstFar)) {
        vn = vnBlk;
        q.xy = abs (qBlk);
        doRefl = (max (q.x, q.y) < 0.85 * bEdge);
        if (doRefl) ro += rd * dstMvBlk;
      } else if (dstObj < dstFar) {
        q = ro + rd * dstObj;
        if (idObj == idGr) {
          if (abs (length (qGear.xy) - 0.38) < 0.17) {
            vn = GearNf (q);
            doRefl = true;
          }
        } else if (idObj == idBas) {
          vn = ObjNf (q);
          q.z -= 0.9;
          q.xz = abs (q.xz) - vec2 (1.55, 1.35);
          doRefl = (vn.y > 0. && max (q.x, q.z) < 0.);
        } else if (idObj == idPln) {
          vn = ObjNf (q);
          doRefl = (vn.y > 0.99);
        } else if (idObj == idWhl) {
          doRefl = (abs (length (qHit.yz) - 0.61) < 0.45);
          if (doRefl) vn = ObjNf (q);
        } else if (idObj == idSpl) {
          vn = ObjNf (q);
          doRefl = (abs (vn.z) > 0.99 && abs (length (qHit.xy) - 0.13) < 0.04);
        }
        if (doRefl) ro += rd * dstObj;
      }
      if (doRefl) {
        rd = reflect (rd, vn);
        ro += 0.01 * rd;
        reflAtn *= 0.9 * reflAtn;
      } else break;
    }
    if (dstMvBlk < min (dstObj, dstFar)) {
      dstObj = dstMvBlk;
      ro += rd * dstObj;
      vn = vnBlk;
      qBlk = abs (qBlk);
      col4 = (max (qBlk.x, qBlk.y) > 0.85 * bEdge) ? vec4 (0.3, 1., 0.3, 0.5) :
         vec4 (0.4, 0.4, 0.4, 0.1);
    } else if (dstObj < dstFar) {
      ro += rd * dstObj;
      vn = (idObj == idGr) ? GearNf (ro) : ObjNf (ro);
      col4 = ObjCol (ro, vn);
    } else if (reflAtn < 1.) col = BgCol (ro, rd);
    if (min (dstMvBlk, dstObj) < dstFar) {
      col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
           col4.w * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
      hitOrig = (reflAtn == 1.);
    }
    col *= reflAtn;
    if (! hitOrig) col *= vec3 (0.95, 1., 0.95) * (0.8 + 0.2 * dot (rdo, - qnBlk[1]));
    col = mix (col,  vec3 (1.) * (0.2 + 0.8 * max (dot (ltDir, qnBlk[0]), 0.) +
       0.5 * pow (max (dot (normalize (ltDir - rdo), qnBlk[0]), 0.), 128.)), 0.15);
    col = mix (col, BgCol (roo, reflect (rdo, qnBlk[0])), pow (1. - abs (dot (rdo, qnBlk[0])), 5.));
  }
  col = pow (clamp (col, 0., 1.), vec3 (0.8));
  return col;
}

