	vec2 fragToCenterPos = vec2(pos.x - 0.5*aspect, pos.y - 0.5);
	vec4 rayCol = rayColor(fragToCenterPos,fragCoord);

	float u, v, w;
	float c = 0.0;	

	vec4 triforceColor = vec4(1.0);
	fragColor = mix(rayCol, triforceColor, c);
}

