vec2 nangle2vec(float nangle){
    return vec2(cos(PI*2.*nangle), sin(PI*2.*nangle));
}

float ncos(float nangle){
    return cos(2.*PI*nangle);
}

float tri(float nangle){
    float phase = mod(nangle, 1.);
    return 2.*abs(phase - .5);
}
vec3 huecolor(float h){

    float hue_scale = mod(h, 1.)*6.;
    if(hue_scale < 1.){
        return vec3(1., (hue_scale - 0.), 0);
    }else if(hue_scale < 2.){
        return vec3(1. - (hue_scale - 1.), 1, 0);
    }else if(hue_scale < 3.){
        return vec3(0, 1, (hue_scale - 2.));
    }else if(hue_scale < 4.){
        return vec3(0, 1. - (hue_scale - 3.), 1.);
    }else if(hue_scale < 5.){
        return vec3((hue_scale - 4.), 0, 1.);
    }else{
        return vec3(1., 0, 1. - (hue_scale - 5.));
    }

}

float atan3(vec2 uv){
    float at2 = atan(uv.y, uv.x)/(2.*PI);
    if(at2 < 0.){
        at2 += 1.0;
    }
    return at2;
}

float moveyring(vec2 uv, float thickness){
    vec2 dir = normalize(uv);
    float nangle = atan3(dir);
    float center_off = abs(length(uv) - 1.0);
    float line_dist = center_off - thickness;
    vec2 grad = fgrad(line_dist);

    float cutoff_nangle = .5*ncos(iTime/32.);
    float cos_cutoff = cos(2.*PI*cutoff_nangle);
    float c = dir.x;
    vec2 cutoff_point = nangle2vec(cutoff_nangle); 

    if(c > cos_cutoff){
        line_dist = 
            min(length(uv - cutoff_point),
                length(uv - vec2(cutoff_point.x, -cutoff_point.y))) - thickness;
    }
    return line_dist;
}

vec2 uv_transform(vec2 uv, float thickness){
    vec2 a = uv;
    vec2 x_axis = nangle2vec(iTime/4.);
    uv.x = x_axis.x*a.x - x_axis.y*a.y;
    uv.y = x_axis.x*a.y + x_axis.y*a.x;
    uv *= (1. + 2.*thickness);
    return uv;
}

