  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.7;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 1.5 * pi * (u.y - 0.5) * tCur;
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

vec2 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  dMin = dstFar;
  if (df > 0. && dn < df) {
    dMin = dn;
    qnBlk[0] = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    qnBlk[1] = - sign (rd) * step (tp, tp.zxy) * step (tp, tp.yzx);
  }
  return vec2 (dMin, df);
}

float ObjDf (vec3 p)
{
  vec3 q;
  float d, dMin;
  dMin = dstFar;
  q = p;  q.y -= bSize.y - 0.15;
  dMin = PrSphDf (q, 0.15);
  idObj = 1;
  q = p;  q.y -= 0.5 * bSize.y;  q.xz = mod (q.xz, bSize.xz / 2.5) - bSize.xz / 5.;
  d = max (max (PrSphDf (q, 0.06), q.y), - PrBox2Df (p.xz, bSize.xz - 0.06));
  if (d < dMin) { dMin = d;  idObj = 2; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float BallHit (vec3 ro, vec3 rd)
{
  vec3 p, u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  rad = 0.2 * bSize.y;
  p = vec3 (0., - bSize.y + 2. * rad, 0.);
  u = ro - p;
  b = dot (rd, u);
  w = b * b - dot (u, u) + rad * rad;
  if (w > 0.) {
    d = - b - sqrt (w);
    if (d > 0. && d < dMin) {
      dMin = d;
      vnBall = (u + d * rd) / rad;
    }
  }
  return dMin;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 vc;
  vec3 vn, ltPos, ltDir, col, col1, col2;
  vec2 dBlock, w, iw, ww, q, b;
  float dstObj, dstBall, spec, c, sd, wLit, glit;
  HexVorInit ();
  dstBall = BallHit (ro, rd);
  if (dstBall < dstFar) {
    ro += dstBall * rd;
    rd = reflect (rd, vnBall);
    ro += 0.01 * rd;
  }
  dBlock = BlkHit (ro, rd, bSize);
  dstObj = ObjRay (ro, rd);
  wLit = 0.;
  glit = 0.;
  if (dstObj < dBlock.y) {
    ro += rd * dstObj;
    vn = ObjNf (ro);
    col = (idObj == 1) ? vec3 (1., 1., 0.9) : vec3 (0.9, 0.9, 0.7);
    col *= 0.6 - 0.4 * dot (rd, vn);
  } else if (dBlock.x < dstFar) {
    vn = qnBlk[1];
    ro += rd * dBlock.y;
    if (vn.y == 0.) {
      ltPos = vec3 (0., bSize.y, 0.);
      if (abs (vn.z) == 1.) {
        q = ro.xy;
        b = bSize.xy;
        sd = 1. + sign (vn.z);
      } else {
        q = ro.zy;
        b = bSize.zy;
        sd = 2. + sign (vn.x);
      }
      if (abs (q.y) < 0.95 * bSize.y) {
        b *= vec2 (1./2.5, 0.95/1.5);
        q += 0.5 * b;
        iw = floor (q / b);
        w = q - iw * b;
        ww = 0.5 * b - abs (w - 0.5 * b);
        wLit = 0.15 * (1. - smoothstep (0.1, 0.2, length (vec2 (1.5 * ww.x, abs (ro.y - 0.5 * bSize.y - 0.1)))));
        if (min (ww.x, ww.y) > 0.01) {
          vc = HexVor (32. * (w + 2. * iw * b + 11. * sd));
          vn.xz = (abs (vn.z) == 1.) ? vec2 (vc.y, 2. * sign (vn.z)) : vec2 (2. * sign (vn.x), vc.y);
          vn.y = - vc.z;
          vn = normalize (vn);
          c = 0.02 + 0.03 * vc.w + 0.04 * (mod (iw.x, 2.) - mod (iw.y, 2.)) + 0.02 * sd;
          col1 = HsvToRgb (vec3 (c, 1., 0.6));
          col2 = HsvToRgb (vec3 (c + 0.05, 1., 0.8));
          col = mix (col1, col2, smoothstep (0.2, 0.8, Fbm2 (vc.yz + 50. * vc.w)));
          col = mix (col, mix (col1, col2, 0.5), 1. - smoothstep (0.01, 0.05, vc.x));
          spec = 0.15;
          glit = 1.;
        } else {
          ww = smoothstep (0.002, 0.004, ww.xy);
          col = vec3 (0.6, 0.3, 0.) * (1. - 0.1 * min (ww.x, ww.y));
          spec = 0.1;
        }
      } else {
        if (q.y > 0.) {
          col = vec3 (0.7, 0.7, 0.8);
          spec = 0.1;
          vn = VaryNf (50. * ro, vn, 1.);
        } else {
          col = vec3 (0.4, 0.2, 0.1);
          spec = 0.1;
        }
      }
    } else if (vn.y > 0.) {
      ltPos = vec3 (0., bSize.y, 0.);
      col = 0.8 * mix (vec3 (0.8, 0.4, 0.2), vec3 (0.5, 0.25, 0.1),
         Fbm2 (vec2 (50., 5.) * ro.xz)) *
         (0.6 + 0.4 * smoothstep (0.03, 0.08, mod (10. * ro.x, 1.)));
      col *= 0.6 + 0.4 * smoothstep (0.05, 0.15, length (ro.xz) / bSize.x);
      spec = 0.1;
    } else {
      ltPos = vec3 (0., - bSize.y, 0.);
      col = vec3 (0.7, 0.7, 0.8) * (0.7 + 0.3 * smoothstep (0., 0.15, length (ro.xz) / bSize.x));
      spec = 0.1;
      vn = VaryNf (50. * ro, vn, 1.);
    }
    ltDir = normalize (ltPos - ro);
    if (glit > 0.) glit = 500. * step (0.01, max (0., dot (vn, ltDir))) *
       pow (max (0., dot (ltDir, reflect (rd, vn))), 16.) *
       pow (1. - 0.6 * abs (dot (normalize (ltDir - rd), VaryNf (1000. * ro, vn, 2.))), 8.);
    col = wLit + glit * vec3 (1., 1., 0.5) + col * (0.1 + 0.8 * max (dot (vn, ltDir), 0.)) +
       spec * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
  } else col = vec3 (0.2);
  return clamp (col, 0., 1.);  
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  dstFar = 5.;
  bSize = vec3 (2., 1., 2.);
  ro = vec3 (0., 0., -0.9 * bSize.z);
  rd = normalize (vec3 (uv, 3.));
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.7 * pi * mPtr.y;
  }
  el = clamp (el, -0.25 * pi, 0.25 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * rd;
  ro = vuMat * ro;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

