#define RGB_BLUE  vec3(.0, .5, 1.)

// I wonder if there's a shorter way to write this...
vec2 df_op_repeat(vec2 point, vec2 frequency){
    vec2 half_frequency = .5 * frequency;
    return mod(point - half_frequency, frequency) - half_frequency;
    //return frequency * (fract(point / frequency - .5) - .5);  // By ollj!
}

vec3 rgb_grid(vec2 uv, float aa){
    //uv.y += sin(uv.x + iTime);

    // This is the manual part. Instead of manually adding each scale (either unrolled or by looping through each scale),
    // perhaps there's a way do write a function that does this *without* loops!
    vec2 grid_scale0 = abs(df_op_repeat(uv, vec2(GRID_SCALE0_FREQ)));
    vec2 grid_scale1 = abs(df_op_repeat(uv, vec2(GRID_SCALE1_FREQ)));
    vec2 grid_scale2 = abs(df_op_repeat(uv, vec2(GRID_SCALE2_FREQ)));

    // This is the manual part. Instead of manually adding each scale (either unrolled or by looping through each scale),
    // perhaps there's a way do write a function that does this *without* loops!
    vec3 rgb = RGB_LIGHT;  // Background color!
    rgb = mix(rgb, RGB_GRAY, smoothstep(aa, .0, min(grid_scale0.x, grid_scale0.y)) * float(SCALE < MAX_GRID_SCALE0_SCALE));
    rgb = mix(rgb, RGB_BLUE, smoothstep(aa, .0, min(grid_scale1.x, grid_scale1.y)) * float(SCALE < MAX_GRID_SCALE1_SCALE));
    rgb = mix(rgb, RGB_DARK, smoothstep(aa, .0, min(grid_scale2.x, grid_scale2.y)) * float(SCALE < MAX_GRID_SCALE2_SCALE));
    return rgb;
}

