float 	t;			// time
vec3	ret_col;	// torus color
vec3	h; 			// light amount

#define I_MAX		40.
#define E			0.0001
#define FAR			8.5
#define MAXI		7.		// num torus
#define PI			3.14

void mainImage(out vec4 c_out, in vec2 f)
{
    t  = iTime*.125;
    vec3	col = vec3(0., 0., 0.);
	vec2 R = iResolution.xy,
          uv  = vec2(f-R/2.) / R.y;
	vec3	dir = camera(uv);
    vec3	pos = vec3(.0, .0, 7.0);

    pos.z = 4.5+3.*sin(t*3.);

    #ifdef	LOOKING_AROUND
    dir.zy *= mat2(cos(t*.5),sin(t*.5),-sin(t*.5),cos(t*.5) );
    dir.xy *= mat2(cos(1.57+t*.5),sin(1.57+t*.5),-sin(1.57+t*.5),cos(1.57+t*.5) );
    #endif

    vec2	inter = (march(pos, dir));
    if (inter.y <= FAR)
        col.xyz = ret_col*(1.-inter.x*.025);
    else
        col *= 0.;
    col += h*.005125;
    c_out =  vec4(col,1.0);
}

float	scene(vec3 p)
{  
    float	mind = 1e5;

    vec2	rot = vec2( cos(t), sin(t) );

	mind = loop_circle(p);

    return (mind);
}

#define VL(p) (1./((length(p)-.0)*(length(p)-.0) ) )

vec2	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec2	s = vec2(0.0, 0.0);

    dist.y = dist.x = length(p)-1.5;
    if (dist.x < E )
    {
        dist.y-=dist.x;
	    for (float i = -1.; i < I_MAX; ++i)
	    {
	    	p = pos + dir * dist.y;
	        dist.x = scene(p);
	        if (dist.x < length(p)-1.75)
	            dist.x = length(p)-1.75;
	        dist.y += dist.x;
	        if (dist.x < E || dist.y > FAR)
            {
                break;
            }
	        s.x++;
	    }
    }
    s.y = dist.y;
    return (s);
}

