float	mylength(vec2 p)
{
	float	ret;

    p = p*p*p*p;

    ret = (p.x+p.y);
    ret = pow(ret, 1./4.);

    return ret;
}

float sdTorus( vec3 p, vec2 t, float phase )
{
	vec2 q = vec2(length(p.xy)-t.x,p.z);
    float	var = (sin(iTime*1.+phase))*3.14;
    vec3	col;
    col = vec3(
        abs(sin(nrand(vec2(7.*phase*10., 7.*phase/3.14))*6.14+iTime*.125+0.00))
        ,
        abs(sin(nrand(vec2(7.*phase*10., 7.*phase/3.14))*6.14+iTime*.125+1.04))
        ,
        abs(sin(nrand(vec2(7.*phase*10., 7.*phase/3.14))*6.14+iTime*.125+2.08))
    );
    h += VL(p-.75*vec3(cos(var), sin(var), 0.)) * .125 * col;
    h += VL(p-.75*vec3(cos(3.14), sin(3.14), 0.)) * .125 * col;
    return min(min(
        (mylength(q)-t.y)
        +
        1e5*step( var , (atan(p.y,p.x))+.0 )
        ,
        length(p-.75*vec3(cos(var), sin(var), 0.))-.125
        )
        ,
        length(p-.75*vec3(cos(3.14), sin(3.14), 0.))-.125
        )
        ;
}

float	circle(vec3 p, float phase)
{
	float	ret;

    ret = sdTorus(p, vec2(.75, .0625), phase );

    return ret;
}

float	loop_circle(vec3 p)
{
	float	ret, old_ret;

    ret_col = vec3(0.);
    ret = 1e5;old_ret = 1e5;p*=1.;
    vec3 op = p;

    for(float i = 0.; i < MAXI; i++)
    {
        p = op;
        rotate(p.xz, i/2.+iTime*2.);
        rotate(p.zy, i/3.+iTime*2.);
        rotate(p.yx, i/1.+iTime*2.);
        old_ret = ret;
    	ret = min(old_ret,
        			circle(p, i/(7.))
        			);
       if (ret != old_ret && ret > 0.)
            ret_col = vec3(abs(sin(nrand(vec2(i*10., i/3.14))*6.14+t+0.00)),
                           abs(sin(nrand(vec2(i*10., i/3.14))*6.14+t+1.04)),
                           abs(sin(nrand(vec2(i*10., i/3.14))*6.14+t+2.08)));

        op*=.9;
    }
    ret = min(ret, length(p)-.1);
    return	ret;
}

// Utilities

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

    return normalize(vec3(
           march(pos+eps.xyy, dir).y - march(pos-eps.xyy, dir).y,
           march(pos+eps.yxy, dir).y - march(pos-eps.yxy, dir).y,
           march(pos+eps.yyx, dir).y - march(pos-eps.yyx, dir).y ));
}

// Based on slide on scanline algorithm in NILE presented here: 
//  "Programming and Scaling" (2011) https://www.youtube.com/watch?v=YyIQKBzIuBY&t=49m46s 
// It's not very fast but it's super clever and compact; may be useful

float ro(vec2 pos, vec2 P, vec2 Q) {
  return (Q.y - P.y)*(pos.x + 1.0 - (Q.x + P.x)/2.0);
}

vec2 gamma(vec2 pos, vec2 P) {
  return vec2(
    min(pos.x + 1.0, max(pos.x, P.x)),
    min(pos.y + 1.0, max(pos.y, P.y))
  );
}

vec2 omega(vec2 pos, float m, vec2 P) {  
  return vec2(
    1./m*(gamma(pos, P).y - P.y) + P.x,
       m*(gamma(pos, P).x - P.x) + P.y
  );
}

float coverage(vec2 pos, vec2 A, vec2 B) {
  float m =(A.y - B.y) / (A.x - B.x); 
  return ro(pos, gamma(pos, A), gamma(pos, omega(pos, m, A)))
       + ro(pos, gamma(pos, omega(pos, m, A)),gamma(pos, omega(pos, m, B)))
       + ro(pos, gamma(pos, omega(pos, m, B)), gamma(pos, B));
}

const float PI = 3.141592653589793238462643383;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // prepare the shape:
    const int ptsN = 32;
    vec2 pts[ptsN];
    for (int i = 0; i < ptsN; ++i) {
        float a = float(i*2)*PI/float(ptsN) + iTime/3.0 + float(i&1)*sin(iTime/2.0)*4.0;
        float R = float(4+4*(i&1))*iResolution.y/(3.0*5.0);
        pts[i] = iResolution.xy/2.0 + R*vec2(cos(a), sin(a));
    }

    // scanline algorithm:
    float sum = 0.0;
    for (int i = 0, j = ptsN - 1; i < ptsN; j = i++) {
      sum += coverage(fragCoord.xy, pts[i], pts[j]);
    }
    float v = min(1.0, abs(sum));

    fragColor = vec4(v*fragCoord.xy/iResolution.xy, (1.0-v)*fragCoord.y*0.5/iResolution.y, 1.0);
}


const vec3 BG_COLOR = vec3(0.0, 0.0, 1.0);
const vec3 MG_COLOR = vec3(1.0, 1.0, 0.5);
const vec3 FG_COLOR = vec3(0.0, 1.0, 0.75);

float sd_plane(vec3 p, float h);
float sd_sphere(vec3 p, float r);
float sd_box(vec3 p, float x, float y, float z);
float sd_torus(vec3 p, float r, float h);
float sd_cylinder(vec3 p, float r, float h);

vec3 twist(vec3 p, float r, float a);
vec3 repeat(vec3 p, float s);

float smin(float a, float b, float k);
float noise(vec3 p);

float map(vec3 p) {
    float d = 1000.0;
	p = twist(p, 1.5, 1.0);
    d = smin(d, sd_plane(p, -1.0), 0.0);
    //d = smin(d, sd_sphere(p, 1.5), sin(iTime));    
    //d = smin(d, sd_sphere(p, 1.5), 0.75);    
    //d = smin(d, sd_sphere(p - vec3(tan(iTime), tan(iTime), sin(iTime)), 1.5), 0.5);

    //vec3 q = repeat(p - vec3(0.0, -1.0, 0.0), 1.0);

	vec3 q = repeat(p - vec3(tan(iTime), tan(iTime), sin(iTime)), 1.0);
    d = smin(d, sd_sphere(q, 0.5), 0.0);

    d = smin(d, sd_sphere(p - vec3(tan(iTime), tan(iTime), sin(iTime)), 1.5), 0.5);

    d = d + noise((p + vec3(sin(iTime), tan(iTime), cos(iTime))) * 8.0) * 0.25;

    //d = smin(d, sd_torus(p - vec3(0.0, -0.5, 0.0), 2.0, 0.25), 0.0);

#if 0
    d = smin(d, sd_plane(p, -0.75), 0.0);

    d = smin(d, sd_sphere(p - vec3(0.0, -0.25, 0.0), 1.0 + sin(iTime) * 0.0625), 0.25);
    d = smin(d, sd_torus(p - vec3(0.0,-0.60, 0.0), 2.0, 0.1 + sin(iTime) * 0.025), 0.5);

	d += noise((p + vec3(0.0, iTime * 0.1, 0.0)) * 4.0) * 0.1;

    float height = sin(4.25 - abs(p.x * 0.5)) * 1.5 * max(0.0, p.z + 1.0);
    d = smin(d, sd_cylinder(repeat(p, 1.0) - vec3(0.0, -1.5, 0.0), 0.25, height), 0.5);
#endif

    return d;  
}

float sd_plane(vec3 p, float h) {
	return p.y - h;   
}

float sd_sphere(vec3 p, float r) {
    return length(p) - r;
}

float sd_box(vec3 p, float x, float y, float z) {
	vec3 d = abs(p) - vec3(x, y, z);
	return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float sd_torus(vec3 p, float r, float h) {
	vec2 q = vec2(length(p.xz) - r, p.y);
	return length(q) - h;
}

float sd_cylinder(vec3 p, float r, float h) {
	vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r, h);
	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

vec3 repeat(vec3 p, float s) {
    vec3 q = p;
    q.x = mod(q.x, s) - 0.5 * s;
    q.z = mod(q.z, s) - 0.5 * s;
    return q;
}

vec3 twist(vec3 p, float r, float a) {
	float c = cos(r * p.y + a);
	float s = sin(r * p.y + a);
	return vec3(p.x * c + p.z * s, p.y, p.x * -s + p.z * c);
}

float smin(float a, float b, float k) {
	float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
	return mix(b, a, h) - k * h * (1.0 - h);
}

float noise(vec3 p) {
	vec3 s = vec3(7.0, 157.0, 113.0);
	vec3 ip = floor(p);
	p -= ip;
	vec4 h = vec4(0.0, s.yz, s.y + s.z) + dot(ip, s);
	p = p * p * (3.0 - 2.0 * p);
	h = mix(fract(sin(h) * 43758.5453), fract(sin(h + s.x) * 43758.5453), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z) * 2.0 - 1.0;
}

vec3 compute_normal(vec3 p) {
	vec3 eps = vec3(0.0005, 0.0, 0.0);
    vec3 n;
    n.x = map(p + eps.xyy) - map(p - eps.xyy);
    n.y = map(p + eps.yxy) - map(p - eps.yxy);
    n.z = map(p + eps.yyx) - map(p - eps.yyx);
    n = normalize(n);
    return n;
}

float compute_ao(vec3 p, vec3 n) {
	float occ = 0.0;
	float sca = 1.0;
	for(int i = 0; i < 5; i++) {
		float hr = 0.01 + 0.12 * float(i) / 4.0;
		float d = map(p + hr * n);
		occ += -(d - hr) * sca;
		sca *= 0.95;
	}
	return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {    
    vec3 ro = vec3(0.0, 0.0, -3.5);
    vec3 rd;
    rd.xy = (fragCoord.xy / iResolution.y) * 2.0 - vec2(iResolution.x / iResolution.y, 1.0);
    rd.z = 1.0;
    rd = normalize(rd);

    vec3 color = BG_COLOR;
	//color.x = sin(iTime);
    //color.y = cos(iTime);
    vec3 color2 = BG_COLOR;
    color2.x = sin(iTime);
    color2.y = cos(iTime);

    float t = 0.0;
    float t_max = 7.5;
    for(int i = 0; i < 256; i++) {
        float d = map(ro + t * rd);
        t += d * 0.6;
        if(d < 0.002) {
            vec3 p = ro + t * rd;
            vec3 n = compute_normal(p);
            vec3 l = normalize(vec3(0.25, 1.0, -0.5));
            float dot_nl = clamp(dot(n, l), 0.0, 1.0);
            float ao = compute_ao(p, n);
            float fog = clamp(1.0 - t / t_max, 0.0, 1.0);
            float light = mix(dot_nl, ao, 0.5);

            color = mix(MG_COLOR, color2, light);
            color = mix(BG_COLOR, color, fog);
            break;
        }
        else if(t > t_max) {
            break;
        }
    }

    fragColor = vec4(color, 1.0);
}
