// The MIT License
//
// mouseclick left / right of image to invert
//
// exploring pyramid illusion / Vasarely's 'nested squares'
// https://twitter.com/kcimc/status/1089979094505271296
// https://michaelbach.de/ot/lum-pyramid/index.html
// https://journals.sagepub.com/doi/10.1068/p5383
//
// based on IQ's https://www.shadertoy.com/view/ldl3W8


#define ANIMATE

vec2 hash2( vec2 p )
{
	// texture based white noise
	return textureLod( iChannel0, (p+0.5)/256.0, 0.0 ).xy;

    // procedural white noise	
	//return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

vec3 voronoi( in vec2 x )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
	vec2 mg, mr;

    float md = 10.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 0.5 + 0.5*sin( iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
        }
    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    md = 8.0;
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 0.5 + 0.5*sin( iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;

        if( dot(mr-r,mr-r)>0.00001 )
        md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
    }

    return vec3( md, mr );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xx;

    vec3 c = voronoi( 8.0*p );

	// isolines
    vec3 col = vec3(c.x*1.3+0.2);//c.x*(0.5 + 0.5*sin(64.0*c.x))*vec3(1.0);
    if (iMouse.x > iResolution.x/2.0) col = vec3(1.0) - col;
    // borders	
    //col = mix( vec3(1.0,0.6,0.0), col, smoothstep( 0.04, 0.07, c.x ) );
    // feature points
	//float dd = length( c.yz );
	//col = mix( vec3(1.0,0.6,0.1), col, smoothstep( 0.0, 0.08, dd) );
	//col += vec3(1.0,0.6,0.1)*(1.0-smoothstep( 0.0, 0.04, dd));

	fragColor = vec4(col,1.0);
}

// "Escalator" by dr2 - 2019
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define AA  1   // optional antialiasing

float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrTorusDf (vec3 p, float ri, float rc);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
vec3 HexGrid (vec2 p);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Hashfv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltPos, qHit, vnBall;
float tCur, dstFar, stSpd, rdDome;
int idObj;
const int idStr = 1, idStrBd = 2, idStrWl = 3, idPil = 4, idPilB = 5, idBBase = 6,
   idFnc = 7, idDome = 8;
const float pi = 3.14159, sqrt3 = 1.73205;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, stRad, stHt, stWid, wThk, d, s, a, az;
  dMin = dstFar;
  stRad = 12.;
  stHt = 5.;
  stWid = 2.5;
  wThk = 0.1;
  az = atan (p.z, - p.x);
  q = p;
  s = rdDome;
  qq.xz = mod (64. * vec2 (az, asin (q.y / length (q))) / (2. * pi) +
     vec2 (0., 0.5), vec2 (1.));
  if (q.y < 0.99 * rdDome) s -= 0.5 * max (SmoothBump (0.45, 0.55, 0.05, qq.x),
     SmoothBump (0.45, 0.55, 0.05, qq.z));
  d = - 0.8 * PrSphDf (q, s);
  DMINQ (idDome);
  p.y -= stHt;
  qq = p;
  qq.z = abs (qq.z) - 3.;
  qq.xz = Rot2D (qq.xz, 0.25 * pi);
  a = atan (qq.z, - qq.x) / pi;
  qq = vec3 (25. * a, p.y, length (qq.xz) - stRad);
  q = qq;
  q.xy = Rot2D (q.xy, 0.25 * pi);
  d = max (max (q.y - 0.5 + abs (mod (q.x + stSpd * sign (p.z) * tCur, 1.) - 0.5), -1. - q.y),
     abs (q.z) - stWid);
  d = 0.7 * max (d, abs (p.y) - stHt);
  DMINQ (idStr);
  q = qq;
  q.xy -= vec2 (-0.5 * stRad, stHt - wThk);
  d = PrBoxDf (q, vec3 (1., wThk, stWid));
  DMINQ (idStr);
  q = qq;
  q.xy -= vec2 (0.5 * stRad, - stHt + wThk);
  d = PrBoxDf (q, vec3 (1., wThk, stWid));
  DMINQ (idStr);
  q = p;
  q.z = abs (q.z);
  q -= vec3 (- stRad, stHt - wThk, 1.5);
  d = PrRoundBoxDf (q, vec3 (stWid + 0.05, 0.07, 1.5), 0.05);
  DMINQ (idStrBd);
  q = p;
  q.z = abs (q.z);
  q -= vec3 (0.7, - stHt + 0.05, stRad + 3.);
  d = PrRoundBoxDf (q, vec3 (0.7, wThk, stWid), 0.05);
  DMINQ (idStrBd);
  q = qq;
  q.xy = Rot2D (q.xy, 0.25 * pi);
  q.z = abs (q.z);
  q.yz -= vec2 (0.6, stWid + 0.1);
  d = PrRoundBox2Df (q.yz, vec2 (1.6, 0.05), 0.05);
  d = max (max (d, p.x), 3. - abs (p.z));
  q.xy = Rot2D (qq.xy, 0.25 * pi);
  d = max (d, - max (PrBox2Df (vec2 (mod (24. * a + 0.25, 0.5) - 0.25, q.y - 1.2),
     vec2 (0.125, 0.7)), 0.89 - dot (normalize (vec2 (p.x, abs (p.z))), vec2 (-0.527, 0.85))));
  d = max (max (d, abs (p.y) - stHt - 2.6), - p.y - stHt);
  DMINQ (idStrWl);
  q = p;
  q.x = abs (q.x + stRad);
  q.xy -= vec2 (stWid + 0.1, stHt + 1.2);
  d = PrBoxDf (q, vec3 (0.1, 1.4, 3.));
  d = max (d, - PrBox2Df (vec2 (q.y - 0.1, mod (p.z + 0.4, 0.8) - 0.4), vec2 (1., 0.2)));
  DMINQ (idStrWl);
  q = p;
  q.z = abs (q.z);
  q -= vec3 (0.7, - stHt + 0.955, stRad + 3.);
  q.z = abs (q.z) - stWid - 0.1;
  d = PrBoxDf (q, vec3 (0.7, 0.955, 0.1));
  DMINQ (idStrWl);
  q = p;
  q.x -= - stRad;
  q.xz = abs (q.xz) - vec2 (1.5, 2.3);
  d = PrCylDf (q.xzy, 0.4, stHt);
  DMINQ (idPil);
  q.y -= - stHt + 0.2;
  d = PrCylDf (q.xzy, 0.6, 0.2);
  DMINQ (idPilB);
  q = p;
  q.y -= - stHt + 0.1;
  d = PrCylDf (q.xzy, 0.5 * stRad, 0.1);
  DMINQ (idBBase);
  q = p;
  q.y -= - stHt + 1.5;
  q.y = abs (q.y) - 0.5;
  d = PrTorusDf (q.xzy, 0.08, 22.);
  q = p;
  q.y -= - stHt + 1.;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (32. * az / (2. * pi)) + 0.5) / 32.);
  q.x += 22.;
  d = min (d, PrCylDf (q.xzy, 0.08, 1.));
  DMINQ (idFnc);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float BallHit (vec3 ro, vec3 rd, float rad)
{
  float b, d, w;
  d = dstFar;
  b = dot (rd, ro);
  w = b * b - dot (ro, ro) + rad * rad;
  if (w > 0.) {
    d = - b - sqrt (w);
    if (d > 0. && d < dstFar) vnBall = (ro + d * rd) / rad;
    else d = dstFar;
  }
  return d;
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.05, 0.5);
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

vec4 FloorCol (vec2 p)
{
  vec4 col4;
  vec3 qh;
  float f;
  qh = HexGrid (p);
  f = Hashfv2 (17.3 * PixToHex (p));
  col4 = mix (vec4 (0.4, 0.1, 0.2, 0.2), vec4 (0.25, 0.1, 0., 0.1),
     Fbm2 (vec2 (3., 1.) * Rot2D (qh.xy, 2. * pi * floor (6. * f) / 6.) + 37.3 * f)) *
     (1. - 0.2 * smoothstep (0.03, 0.06, qh.z));
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, ror, ltDir;
  vec2 b;
  float dstObj, dstBall, vdl, s, sh, att;
  bool isRefl;
  dstBall = BallHit (ro + vec3 (0., 10.4, 0.), rd, 12.);
  dstObj = ObjRay (ro, rd);
  isRefl = false;
  ror = ro + dstBall * rd;
  if (dstBall < min (dstObj, dstFar) && ror.y > 0.) {
    isRefl = true;
    ro = ror;
    rd = reflect (rd, vnBall);
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == idDome) {
      col4 = (qHit.y < 0.99 * rdDome && length (qHit) > rdDome - 0.1) ?
         vec4 (0., 0., 0.4, -1.) : vec4 (0.1, 0.7, 0.1, 0.1);
    } else if (idObj == idStr) {
      col4 = vec4 (0.8, 0.8, 0.85, 0.4);
      s = 1.;
      if (vn.y > 0.99) {
        b = ro.xz - vec2 (0., 3. * sign (ro.z));
        s = mod (4. * length (b), 1.);
        col4 *= 0.4 + 0.6 * SmoothBump (0.2, 0.8, 0.05, s);
        vn.xz = 0.3 * normalize (b) * (SmoothBump (0.75, 0.95, 0.05, s) -
           SmoothBump (0.05, 0.25, 0.05, s));
        vn.y = sqrt (1. - dot (vn.xz, vn.xz));
        col4 = mix (vec4 (0., 0.1, 0., 0.), col4, step (0.007,
           abs (mod (25. * sqrt (2.) * atan (ro.z - 3. * sign (ro.z), - ro.x) / pi +
           0.15 * sign (ro.z), 1.) - mod (- stSpd * tCur, 1.))));
      } else if (abs (vn.y) < 0.1) {
        col4 = mix (0.9 * col4, vec4 (0.1, 0.8, 0.1, 0.5), SmoothBump (0.3, 0.7, 0.05,
           mod (2. * qHit.z, 1.)));
      } else {
        col4 *= 0.7;
      }
    } else if (idObj == idStrBd) {
      col4 = vec4 (0.8, 0.8, 0.85, 0.4);
      if (qHit.y > 0.) {
        if (ro.y < 5. || abs (ro.z) > 1.5) {
          col4 *= 0.4 + 0.6 * SmoothBump (0.2, 0.8, 0.05, (mod (4. * 
             ((ro.y < 5.) ? qHit.z : qHit.x), 1.)));
          col4 = mix (col4, vec4 (0.8, 0.1, 0.1, 0.3), step (0.,
             (-1.05 + ((ro.y < 5.) ? 0.4 - qHit.x : qHit.z - 0.4))));
        }
      } else col4 *= 0.7;
    } else if (idObj == idStrWl) {
      col4 = vec4 (0.8, 0.8, 0.2, 0.5);
      if (qHit.y > 2.2) col4 = mix (vec4 (0.1, 0.1, 0.2, 0.1), col4,
         smoothstep (0.04, 0.06, abs (qHit.z)));
      else if (abs (vn.y) < 0.01) vn = VaryNf (32. * ro, vn, 1.);
    } else if (idObj == idPil) {
      col4 = vec4 (0.8, 0.8, 0.2, 0.5);
      s = mod (2. * qHit.y + ((length (qHit.xz) > 0.) ? atan (qHit.z, - qHit.x) / pi : 0.), 1.);
      vn.y = 0.3 * (1. - SmoothBump (0.2, 0.8, 0.1, s)) * sign (s - 0.5);
      vn.xz *= sqrt (1. - vn.y * vn.y);
    } else if (idObj == idPilB) {
      col4 = vec4 (0.8, 0.8, 0.2, 0.5);
    } else if (idObj == idBBase) {
      col4 = vec4 (0.5, 0.2, 0.1, 0.1);
      vn = VaryNf (4. * qHit, vn, 1.);
    } else if (idObj == idFnc) {
      col4 = vec4 (0.6, 0.5, 0.1, 0.1);
    }
  } else {
    ro += (- ro.y / rd.y) * rd;
    s = length (ro.xz) - 15.1;
    if (abs (s) < 2.55 && ro.x > 0.) col4 = vec4 (0.8, 0.8, 0.85, 0.4) *
       (0.4 + 0.6 * SmoothBump (0.05, 0.95, 0.02, mod (s + 0.5, 1.)));
    else col4 = FloorCol (ro.xz);
    vn = vec3 (0., 1., 0.);
  }
  ltDir = normalize (ltPos);
  att = 0.4 + 0.6 * smoothstep (0.5, 0.8, dot (normalize (ltPos - ro), ltDir));
  if (col4.a >= 0.) {
    sh = min (ObjSShadow (ro, ltDir), att);
    vdl = dot (vn, ltDir);
    col = col4.rgb * (0.2 + 0.1 * max (- vdl, 0.) +  step (0., vdl) * 0.8 * sh * vdl * vdl) +
       col4.a * smoothstep (0.8, 0.9, sh) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  } else col = col4.rgb;
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, ltEl, ltAz, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = -0.5 * pi;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= 0.7 * pi * mPtr.y;
  } else {
    az += 0.4 * (floor (0.3 * tCur) + smoothstep (0., 0.1, mod (0.3 * tCur, 1.)));
  }
  el = clamp (el, -0.4 * pi, 0.01 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rdDome = 80.;
  stSpd = 1.5;
  ro = rdDome * vuMat * vec3 (0., 0.1, -0.95);
  ro.y = max (ro.y, 0.1);
  zmFac = 4. / (1. - 0.5 * SmoothBump (0.25, 0.75, 0.25, mod ((az + 0.5 * pi) / (2. * pi), 1.)));
  dstFar = 2. * rdDome;
  ltEl = -0.3 * pi * (1. + 0.3 * sin (0.2 * pi * tCur));
  ltAz = 0.5 * pi * cos (0.3 * pi * tCur);
  ltPos = vec3 (0., 0., 0.95 * rdDome);
  ltPos.yz = Rot2D (ltPos.yz, ltEl);
  ltPos.xz = Rot2D (ltPos.xz, ltAz);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (col, vec3 (0.8)), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec3 HexGrid (vec2 p)
{
  vec2 q;
  p -= HexToPix (PixToHex (p));
  q = abs (p);
  return vec3 (p, 0.5 * sqrt3 - q.x + 0.5 * min (q.x - sqrt3 * q.y, 0.));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

//Definiowanie stałej wartości, stała pkts jako stała, ciągła ilość wierzchołków, która wynosi 4, która będzie używana do definiowania tablic
const int pkts = 4;
float r = 50.0;

//Tworzenie fukcji zawierającej informacje czy dany piksel o przekazanych koordynatach zawiera się między bokami powstałymi na wierzchołkach
bool zawiera(vec2 pixelcoord, vec2 polot[pkts]){
    int is = 1;
    float distx[pkts];
    float disty[pkts];
    //prawy bok i lewostronne ograniczenie koordynaty x
    distx[0] = polot[0].x-polot[1].x;
    disty[0] = polot[0].y-polot[1].y;
    if (pixelcoord.x > (pixelcoord.y - polot[0].y)/disty[0]*distx[0]+polot[0].x) is=0;
    //lewy bok i prawostronne ograniczenie koordynaty x
    distx[2] = polot[2].x-polot[3].x;
    disty[2] = polot[2].y-polot[3].y;
    if (pixelcoord.x < (pixelcoord.y - polot[2].y)/disty[2]*distx[2]+polot[2].x) is=0;
    //dolny bok i odgórne ograniczenie koordynaty y
    distx[1] = polot[1].x-polot[2].x;
    disty[1] = polot[1].y-polot[2].y;
    if (pixelcoord.y < (pixelcoord.x - polot[1].x)/distx[1]*disty[1]+polot[1].y) is=0;
    //górny bok i oddolne ograniczenie koordynaty y
    distx[3] = polot[3].x-polot[0].x;
    disty[3] = polot[3].y-polot[0].y;
    if (pixelcoord.y > (pixelcoord.x - polot[3].x)/distx[3]*disty[3]+polot[3].y) is=0;

    //Zwracanie logicznej wartości całkowitej zmiennej
    return bool(is);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Przeniesienie punktu (0,0) na środek ekranu
    vec2 zero = iResolution.xy/2.0;
    //Definicja środków okręgów
    vec2 punkt[pkts];
     punkt[0] = zero + vec2(100.0, 100.0);
     punkt[1] = zero + vec2(100.0, -100.0);
     punkt[2] = zero + vec2(-100.0, -100.0);
     punkt[3] = zero + vec2(-100.0, 100.0);
    //polot jest to zmienna zawierająca pozycje wierzchołków
    vec2 polot[pkts];
    //Definiowanie kolorów
    vec3 col1 = vec3(0.1, 0.2, 0.9);
    vec3 col2 = vec3(0.0, 1.0, 0.0);
    vec3 col3 = vec3(0.0, 0.0, 0.0);
    vec3 col4 = vec3(0.8, 0.1, 0.1);
    vec3 col5 = vec3(0.6, 0.6, 0.6);
    vec3 pixel;
    //Domyślne zamalowanie tła
    pixel = col1;


    //Wyrysowanie torór ruchu wierzchołków
    for (int i=1; i<=pkts; i++)
    {
        float len = length(fragCoord - punkt[i-1]);
        if (len < 5.0 ){
            pixel=col2;
        }
        if (len > (r-1.0) && len < (r+1.0)) {pixel = col3;}
    }

    //Wyrysowanie wierzchołków
    for (int i=1; i<=pkts; i++)
    {
        polot[i-1].x = punkt[i-1].x-sin(iTime/float(i)*2.0)*r;
        polot[i-1].y = punkt[i-1].y-cos(iTime/float(i)*2.0)*r;
        float len=length(fragCoord - polot[i-1]);
        if (len < 5.0) {pixel = col4;}
    }

    //Sprawdzenie, czy piksel należy do czworokąta
    if (zawiera(fragCoord, polot)) {pixel = col5;}

    //Rysowanie
    fragColor = vec4(pixel, 1.0);
}

