// A list of useful distance function to simple primitives, and an example on how to
// do some interesting boolean operations, repetition and displacement.
//

#define AA 1 // make this 1 is your machine is too slow

//------------------------------------------------------------------

float sdPlane(vec3 p)
{
    return p.y;
}

float sdBox(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float sdEllipsoid(in vec3 p, in vec3 r) // approximated
{
    float k0 = length(p / r);
    float k1 = length(p / (r * r));
    return k0 * (k0 - 1.0) / k1;
}

float sdHexPrism(vec3 p, vec2 h)
{
    vec3 q = abs(p);
#if 1
    const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
    p = abs(p);
    p.xy -= 2.0 * min(dot(k.xy, p.xy), 0.0) * k.xy;
    vec2 d = vec2(
        length(p.xy - vec2(clamp(p.x, -k.z * h.x, k.z * h.x), h.x)) * sign(p.y - h.x),
        p.z - h.y);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
#endif
#if 0    
    float d1 = q.z-h.y;
    float d2 = max((q.x*0.866025+q.y*0.5),q.y)-h.x;
    return length(max(vec2(d1,d2),0.0)) + min(max(d1,d2), 0.);
#endif
#if 0
    return max(q.z-h.y,max((q.x*0.866025+q.y*0.5),q.y)-h.x);
#endif
}

// vertical
float sdCylinder(vec3 p, vec2 h)
{
    vec2 d = abs(vec2(length(p.xz), p.y)) - h;
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdCappedCylinder(vec3 p, vec2 h)
{
    vec2 d = abs(vec2(length(p.xz), p.y)) - h;
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

// arbitrary orientation
float sdCylinder(vec3 p, vec3 a, vec3 b, float r)
{
    vec3 pa = p - a;
    vec3 ba = b - a;
    float baba = dot(ba, ba);
    float paba = dot(pa, ba);
#if 0    
    float ibal = inversesqrt(baba);
    float x = length(pa-ba*paba*ibal*ibal) - r;
    float y = (abs(paba-baba*0.5)-baba*0.5)*ibal;
    return min(max(x,y),0.0) + length(max(vec2(x,y),0.0));
#else
    float x = length(pa * baba - ba * paba) - r * baba;
    float y = abs(paba - baba * 0.5) - baba * 0.5;
    float x2 = x * x;
    float y2 = y * y * baba;
    float d = (max(x, y) < 0.0) ? -min(x2, y2) : (((x > 0.0) ? x2 : 0.0) + ((y > 0.0) ? y2 : 0.0));
    return sign(d) * sqrt(abs(d)) / baba;
#endif
}

//------------------------------------------------------------------

vec2 opU(vec2 d1, vec2 d2)
{
    return (d1.x < d2.x) ? d1 : d2;
}

float opUnion(float d1, float d2) { return min(d1, d2); }

float opSubtraction(float d1, float d2) { return max(-d1, d2); }

float opIntersection(float d1, float d2) { return max(d1, d2); }

float opSmoothUnion(float d1, float d2, float k)
{
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0 - h);
}

float opSmoothSubtraction(float d1, float d2, float k)
{
    float h = clamp(0.5 - 0.5 * (d2 + d1) / k, 0.0, 1.0);
    return mix(d2, -d1, h) + k * h * (1.0 - h);
}

float opSmoothIntersection(float d1, float d2, float k)
{
    float h = clamp(0.5 - 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) + k * h * (1.0 - h);
}

vec3 opTwist(vec3 p)
{
    float c = cos(10.0 * p.y + 10.0);
    float s = sin(10.0 * p.y + 10.0);
    mat2 m = mat2(c, -s, s, c);
    return vec3(m * p.xz, p.y);
}
vec3 opRot(vec3 p, float angle)
{
    angle = angle * 3.1415 / 180.0;
    float c = cos(angle);
    float s = sin(angle);
    mat2 m = mat2(c, -s, s, c);
    return vec3(m * p.zy, p.x);
}
vec3 opCheapBend(vec3 p)
{
    float angle = -180.0 * 3.1415 / 180.0;
    float c = cos(angle * p.y);
    float s = sin(angle * p.y);
    mat2 m = mat2(c, -s, s, c);
    return vec3(m * p.xy, p.z);
}

//------------------------------------------------------------------

#define ZERO (min(iFrame, 0))

//------------------------------------------------------------------

vec2 map(in vec3 pos)
{
    float body = sdEllipsoid(pos - vec3(0, 0.5, 0), vec3(0.18, 0.5, 0.5));
    float box = sdBox(pos, vec3(0.13, 1, 1));
    body = opSmoothIntersection(box, body, 0.01);

    float nose1 = sdCappedCylinder((pos - vec3(0, 0.89, 0.3)).xzy, vec2(0.125, 0.3));
    float nose2 = sdCappedCylinder((pos - vec3(0, 0.89, 0.46)).xzy, vec2(0.132, 0.005));
    nose1 = opSmoothUnion(nose1, nose2, 0.01);
    body = opSmoothUnion(body, nose1, 0.03);

    float nares = 9999.0;
    float nares1 = sdHexPrism((pos - vec3(-0.1, 0.9, 0.4)).zyx, vec2(0.03, 0.06));
    float nares1_1 = sdCylinder((pos - vec3(-0.1, 0.9, 0.4)).yxz, vec2(0.015, 0.1));
    nares1 = opSubtraction(nares1_1, nares1);
    nares = opSmoothUnion(nares, nares1, 0.0);
    float nares2 = sdHexPrism((pos - vec3(-0.1, 0.9, 0.5)).zyx, vec2(0.03, 0.06));
    float nares2_1 = sdCylinder((pos - vec3(-0.1, 0.9, 0.5)).yxz, vec2(0.015, 0.1));
    nares2 = opSubtraction(nares2_1, nares2);
    nares = opSmoothUnion(nares, nares2, 0.0);

    float ear1 = sdBox(pos - vec3(0, 0.9, -0.28), vec3(0.05, 0.18, 0.09));
    float ear2 = sdBox(pos - vec3(0, 0.9, 0.28), vec3(0.05, 0.18, 0.09));
    float ear3 = sdBox(pos - vec3(0, 0.885, 0), vec3(0.05, 0.1, 0.3));
    body = opSmoothUnion(body, ear1, 0.0);
    body = opSmoothUnion(body, ear2, 0.0);
    body = opSmoothUnion(body, ear3, 0.0);

    float eyes = 9999.0;
    float eye1 = sdHexPrism((pos - vec3(-0.1, 0.86, 0.13)).zyx, vec2(0.08, 0.06));
    float eyeball1 = sdCylinder((pos - vec3(-0.1, 0.86, 0.13)).yxz, vec2(0.055, 0.1));
    eye1 = opSubtraction(eyeball1, eye1);
    eyes = opSmoothUnion(eyes, eye1, 0.0);
    float eye2 = sdHexPrism((pos - vec3(-0.1, 0.76, -0.11)).zyx, vec2(0.06, 0.06));
    float eyeball2 = sdCylinder((pos - vec3(-0.1, 0.76, -0.11)).yxz, vec2(0.045, 0.1));
    eye2 = opSubtraction(eyeball2, eye2);
    eyes = opSmoothUnion(eyes, eye2, 0.0);

    float face1 = sdCappedCylinder((pos - vec3(-0.1, 0.5, 0)).yxz, vec2(0.19, 0.045));
    body = opSmoothUnion(body, face1, 0.0);

    float face2 = sdCappedCylinder((pos - vec3(-0.1, 0.6, -0.26)).yxz, vec2(0.08, 0.045));
    body = opSmoothUnion(body, face2, 0.0);

    float mouth = sdCappedCylinder((opCheapBend(opRot(pos - vec3(-0.14, 0.24, 0.12), 105.0))).xyz, vec2(0.01, 0.24));
    // body = opSmoothUnion(body, mouth, 0.0);

    vec2 res = opU(vec2(sdPlane(pos), 1.0), vec2(body, 2.0));
    res = opU(res, vec2(eyes, 3.0));
    res = opU(res, vec2(mouth, 3.0));
    res = opU(res, vec2(nares, 3.0));
    return res;
}

vec2 castRay(in vec3 ro, in vec3 rd)
{
    float tmin = 1.0;
    float tmax = 20.0;

#if 1
    // bounding volume
    float tp1 = (0.0 - ro.y) / rd.y;
    if (tp1 > 0.0)
        tmax = min(tmax, tp1);
    float tp2 = (1.6 - ro.y) / rd.y;
    if (tp2 > 0.0)
    {
        if (ro.y > 1.6)
            tmin = max(tmin, tp2);
        else
            tmax = min(tmax, tp2);
    }
#endif

    float t = tmin;
    float m = -1.0;
    for (int i = 0; i < 64; i++)
    {
        float precis = 0.0004 * t;
        vec2 res = map(ro + rd * t);
        if (res.x < precis || t > tmax)
            break;
        t += res.x;
        m = res.y;
    }

    if (t > tmax)
        m = -1.0;
    return vec2(t, m);
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow(in vec3 ro, in vec3 rd, in float mint, in float tmax)
{
    float res = 1.0;
    float t = mint;
    for (int i = ZERO; i < 16; i++)
    {
        float h = map(ro + rd * t).x;
        res = min(res, 8.0 * h / t);
        t += clamp(h, 0.02, 0.10);
        if (res < 0.005 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal(in vec3 pos)
{
#if 0
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
#else
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for (int i = ZERO; i < 4; i++)
    {
        vec3 e = 0.5773 * (2.0 * vec3((((i + 3) >> 1) & 1), ((i >> 1) & 1), (i & 1)) - 1.0);
        n += e * map(pos + 0.0005 * e).x;
    }
    return normalize(n);
#endif
}

float calcAO(in vec3 pos, in vec3 nor)
{
    float occ = 0.0;
    float sca = 1.0;
    for (int i = ZERO; i < 5; i++)
    {
        float hr = 0.01 + 0.12 * float(i) / 4.0;
        vec3 aopos = nor * hr + pos;
        float dd = map(aopos).x;
        occ += -(dd - hr) * sca;
        sca *= 0.95;
    }
    return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox(in vec2 p)
{
    // filter kernel
    vec2 w = fwidth(p) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0 * (abs(fract((p - 0.5 * w) * 0.5) - 0.5) - abs(fract((p + 0.5 * w) * 0.5) - 0.5)) / w;
    // xor pattern
    return 0.5 - 0.5 * i.x * i.y;
}

vec3 render(in vec3 ro, in vec3 rd)
{
    vec3 col = vec3(0.7, 0.9, 1.0) + rd.y * 0.8;
    vec2 res = castRay(ro, rd);
    float t = res.x;
    float m = res.y;
    if (m > -0.5)
    {
        vec3 pos = ro + t * rd;
        vec3 nor = calcNormal(pos);
        vec3 ref = reflect(rd, nor);

        // material
        col = vec3(247.0 / 255.0, 100.0 / 255.0, 100.0 / 255.0); //0.45 + 0.35 * sin(vec3(0.05, 0.08, 0.10) * (m - 1.0));
        if (m < 1.5)
        {
            float f = checkersGradBox(5.0 * pos.xz);
            col = 0.3 + f * vec3(0.1);
        }
        if (m > 2.5)
        {
            col = vec3(72.0 / 255.0, 59.0 / 255.0, 53.0 / 255.0);
        }

        // lighitng
        float occ = calcAO(pos, nor);
        vec3 lig = normalize(vec3(-0.4, 0.7, -0.6));
        vec3 hal = normalize(lig - rd);
        float amb = clamp(0.5 + 0.5 * nor.y, 0.0, 1.0);
        float dif = clamp(dot(nor, lig), 0.0, 1.0);
        float bac = clamp(dot(nor, normalize(vec3(-lig.x, 0.0, -lig.z))), 0.0, 1.0) * clamp(1.0 - pos.y, 0.0, 1.0);
        float dom = smoothstep(-0.2, 0.2, ref.y);
        float fre = pow(clamp(1.0 + dot(nor, rd), 0.0, 1.0), 2.0);

        dif *= calcSoftshadow(pos, lig, 0.02, 2.5);
        dom *= calcSoftshadow(pos, ref, 0.02, 2.5);

        float spe = pow(clamp(dot(nor, hal), 0.0, 1.0), 16.0) *
                    dif *
                    (0.04 + 0.96 * pow(clamp(1.0 + dot(hal, rd), 0.0, 1.0), 5.0));

        vec3 lin = vec3(0.0);
        lin += 1.30 * dif * vec3(1.00, 0.80, 0.55);
        lin += 0.30 * amb * vec3(0.40, 0.60, 1.00) * occ;
        lin += 0.40 * dom * vec3(0.40, 0.60, 1.00) * occ;
        lin += 0.50 * bac * vec3(0.25, 0.25, 0.25) * occ;
        lin += 0.25 * fre * vec3(1.00, 1.00, 1.00) * occ;
        col = col * lin;
        col += 9.00 * spe * vec3(1.00, 0.90, 0.70);

        col = mix(col, vec3(0.8, 0.9, 1.0), 1.0 - exp(-0.0002 * t * t * t));
    }

    return vec3(clamp(col, 0.0, 1.0));
}

mat3 setCamera(in vec3 ro, in vec3 ta, float cr)
{
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(sin(cr), cos(cr), 0.0);
    vec3 cu = normalize(cross(cw, cp));
    vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 mo = iMouse.xy / iResolution.xy;
    float time = 30.0 + iTime * 0.1;

    vec3 tot = vec3(0.0);
#if AA > 1
    for (int m = ZERO; m < AA; m++)
        for (int n = ZERO; n < AA; n++)
        {
            // pixel coordinates
            vec2 o = vec2(float(m), float(n)) / float(AA) - 0.5;
            vec2 p = (-iResolution.xy + 2.0 * (fragCoord + o)) / iResolution.y;
#else
    vec2 p = (-iResolution.xy + 2.0 * fragCoord) / iResolution.y;
#endif

            // camera
            vec3 ro = vec3(2.3 * cos(0.1 * time + 6.0 * mo.x), 1.0 + 2.0 * mo.y, 0.5 + 2.3 * sin(0.1 * time + 6.0 * mo.x));
            vec3 ta = vec3(0.0, 0.4, 0.0);
            // camera-to-world transformation
            mat3 ca = setCamera(ro, ta, 0.0);
            // ray direction
            vec3 rd = ca * normalize(vec3(p.xy, 2.0));

            // render
            vec3 col = render(ro, rd);

            // gamma
            col = pow(col, vec3(0.4545));

            tot += col;
#if AA > 1
        }
    tot /= float(AA * AA);
#endif

    fragColor = vec4(tot, 1.0);
}

