const float frequency = 60.0; //times per screen width

float oscillator(vec2 uv, vec2 offset)
{
   	vec2 coord = uv - vec2(0.5,0.5) - offset;
    coord *= 2.0 * G_PI * vec2(1.0,iResolution.y/iResolution.x);

    return  cos(length(coord*frequency)- 5.0*iTime);
}


