    float acc = 0.0;

    for(int i = 0; i<= oscillatorsNum; i++ )
    {

       vec2 offsetA = vec2(float(i) * 0.25/frequency,0.0);


        if( i % 2 == 0 && i != 0)// even odd check to stay in the center
         	offsetA = +vec2(float(i/2) * 0.5/frequency,0.0);
        else           
       		offsetA = -vec2(ceil(float(i)/2.0) * 0.5/frequency,0.0);

       acc += oscillator(uv,offsetA); 
    }    
  	acc /= float(oscillatorsNum+1);
    acc = acc * 0.5 + 0.5;


    // Output to screen
    vec3 resC = (vec3(1,0,0.0)*(1.0 - acc) + vec3(0,0,1)*acc - vec3(0.5,0,0.5))*2.0; 
    fragColor = vec4(resC,1.0);

}

