void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //UV MAP
    vec2 uv = fragCoord/iResolution.xy;

    //CURTAIN MOVEMENT
	float p = (sin((iTime * 1.3f) + ((uv.x + uv.y))) + 1.f) / 2.f; 

    //DRAW SIDE CURTAINS
    if(uv.x - (p * 0.08f) < 0.15f || uv.x + (p * 0.08f) > 0.85)
    {
        //CURTAIN COLOR
        float w = 0.f;   

        //CHECK DIRECTION/SIDE
        if(uv.x > 0.5f)
    	{
            //RIGHT
    		uv.x = ((uv.x) + (p * 0.08f));
    		uv.y = ((uv.y) + (p * 0.03f));
        	w = mod(1.f - uv.x, 0.05f) * 20.f;
    	}
    	else      
    	{
            //LEFT
    		uv.x = ((uv.x) - (p * 0.08f));
    		uv.y = ((uv.y) - (p * 0.03f));
        	w = mod(uv.x , 0.05f) * 20.f;
    	}

        //SET COLOR
        vec3 col = vec3(w, 0, 0);  
    	fragColor = vec4(col, 1.0);
       	return;
    }

    //ADD MOVIE OFFSET
    uv.y += sin(24.1f * iTime) * 0.001f;

    //DRAW SCREEN
    if(uv.y > 0.2f && uv.y < 0.8f)
    {    

        //SCREEN FLICKER
        float v = ((sin(iTime * 20.f) * 0.175f + 1.f) / 2.f) + 0.2f;     
        float d = 2.4f * pow(abs(uv.x - 0.5f), 2.f) + pow(abs(uv.y - 0.5f), 2.f);     
        v = (v * 0.5f) - (v * sqrt(d) * 0.5f);         
		fragColor = vec4(vec3(v, v, v), 1.0);

        //TRIANGLE 
        float tx = (sin(iTime * 5.f) * 0.2f);
        float ty = (sin(iTime * 2.1f) * 0.2f);
        if(0.5f - uv.y - ty > 2.25f * abs(uv.x + tx - 0.5) && (uv.y + ty) > 0.45)
        {
            fragColor += fragColor;
        }      

        //LIGHT
        float md = pow(abs(uv.x + tx - 0.5f), 2.f) + pow(abs(uv.y + ty - 0.5f), 2.f);
        float ti = iTime * 4.f;
        if(md > sin(ti) && md < sin(ti + 0.5f))
        {
            fragColor += (fragColor * 1.f);
        }

        return;
    }

    //BACKGROUND
    fragColor = vec4(vec3(pow(abs(0.5f - uv.y), 3.f) * 0.5f, 0, 0), 1.0);       
    return;           
}

