void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    bool boolean_mask = true;
    vec3 color = vec3(1.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv*=41.0; // cells tall.
    uv.x += .5;
    uv.y += .5;

    vec2 pixel_uv = floor(uv);

    if(abs(pixel_uv.y) > 10.0) {
        // borders
        boolean_mask = (abs(pixel_uv.y) <= 15.0) && (rand(pixel_uv) < clamp(0.0, 1.0, .5+sin(iTime*.5)));

    } else {

        // Scroll the banner
        pixel_uv.x += floor(iTime*10.0);

        // [ 0-19] is heart
        // [20-49] is text
        pixel_uv.x = mod(pixel_uv.x, 50.0);
        pixel_uv.y += floor(sin(pixel_uv.x*6.28318/50.0)*0.0);

        if(pixel_uv.x < 20.0)
            // shift heart [-10,9]
            boolean_mask = heart(pixel_uv-vec2(10,0));
        else {
            // shift text so that it fits nicely.
            boolean_mask = renderText(pixel_uv+vec2(-20,7));
        }
    }

    pixel_uv = rotate(pixel_uv, -3.14159/4.0);
    float t = iTime*.5;
    int id = int(t);
    color = mix(
        getColor(floor(uv), id),
        getColor(floor(uv), id+1),
        smoothstep(0.0,1.0, fract(t)));

    vec2 subpixel_uv = fract(uv);
    float led = 1.0-length(subpixel_uv-.5)*2.0;
    fragColor = vec4(1);
    fragColor *= boolean_mask?1.0:0.05;
    fragColor *= led*2.0;
    fragColor.rgb *= color;
}

