    float a =       dot(rayDir, rayDir);
    float b = 2.0 * dot(diff  , rayDir);
    float c =       dot(diff  , diff  ) - sphereRadius * sphereRadius;

    float di = b*b - 4.0*a*c;

    if (di >= 0.0)
    {
        float sdi = sqrt(di);
        float den = 1. / (2.0 * a);
        float t1  = (-b - sdi) * den;
        float t2  = (-b + sdi) * den;

        float t = -1.0;
        float dir = 1.0;
        if (t1 > 0.0)
        	t = t1;
        else if (t2 > 0.0)
        {
            t = t2;
            dir = -1.0;
        }
        else
            return t;

        intersecPt = rayPos + t * rayDir;

        normal     = normalize(intersecPt - spherePos) * dir;

        return t;
    }

    return -1.0;
}

//##############################################################################################

//cette fonction similaire à reySphere est utilisé pour la sphere 4, 
//laquelle nous souhaitons l'intersection la plus éloignée (non visible par la caméra)
float raySphere4(vec3 rayPos ,vec3 rayDir, vec3 spherePos, float sphereRadius,
                out vec3 intersecPt, out vec3 normal)
{

    vec3 diff = rayPos - spherePos;

    float a =       dot(rayDir, rayDir);
    float b = 2.0 * dot(diff  , rayDir);
    float c =       dot(diff  , diff  ) - sphereRadius * sphereRadius;

    float di = b*b - 4.0*a*c;

    if (di >= 0.0)
    {
        float sdi = sqrt(di);
        float den = 1. / (2.0 * a);
        float t2  = (-b + sdi) * den;

        float dir = 1.0;

        // As we found the value of t, we can insert it into equation (1) to find
        // the position of the first intersection point encountered along the ray.
        intersecPt = rayPos + t2 * rayDir;

        normal     = normalize(intersecPt - spherePos) * dir;

        //on prend l'intersection la plus éloignée
        return t2;
    }

    return 0.0;
}
//##############################################################################################

//calcul point d'intersection entre deux sphère
float raySphereIntersec(vec3 rayPos, vec3 rayDir, vec3 spos1, vec3 spos2, float srad1, float srad2, out vec3 intersecPt, out vec3 normal, out int subObjectId)
{ 
    vec3 intersecPt1;
	vec3 normal1;
    vec3 intersecPt2;
	vec3 normal2;

    float dist1 = raySphere(rayPos, rayDir, spos1, srad1, intersecPt1, normal1);

    float dist2 = raySphere(rayPos, rayDir, spos2, srad2, intersecPt2, normal2);

     //le rayon passe par les deux sphères (condition nécessaire d'intersection)
    if ((dist1 > 0.0) && (dist2 > 0.0)) {

        float disS1 = distance(intersecPt1,csgSphere2Pos);

        //le point de la sphère 1 est inclu dans la sphère 2
        if (disS1 <= csgSphere2Radius) {

            intersecPt = intersecPt1;
            subObjectId = 1;
            normal = normal1; 
            return dist1;
        }

        float disS2 = distance(intersecPt2,csgSphere1Pos);

        //le point de la sphère 2 est inclu dans la sphère 1
        if (disS2 <= csgSphere1Radius) {
          	intersecPt = intersecPt2;
            subObjectId = 2;
            normal = normal2; 	
            return dist2;
         } 
    }

    return -1.0;
}

//##############################################################################################


float rayCSG( vec3 rayPos, vec3 rayDir, out vec3 intersecPt, out vec3 normal, out int subObjectId)
{
    vec3 intersecPt1;
	vec3 normal1;
    vec3 intersecPt3;
	vec3 normal3;
    vec3 intersecPt4;
	vec3 normal4;
    int subid;
    float dist;


    //on cherche si le rayon traverse l'intersection entre la sphère 1 et 2
    float distintersec = raySphereIntersec(rayPos, rayDir, csgSphere1Pos, csgSphere2Pos, csgSphere1Radius, csgSphere2Radius, intersecPt1, normal1, subid);

    //on cherche si le rayon traverse la sphère 3
	float dist3 = raySphere(rayPos, rayDir, csgSphere3Pos, csgSphere3Radius, intersecPt3, normal3);


    //Si il y a une intersection avec la sphère 3 OU entre la sphère 1 et 2
    if ((dist3 > 0.0)) {
        //intersection avec la sphère 3
        intersecPt = intersecPt3;
        subObjectId = 3;
        normal = normal3; 
        dist = dist3;

        //intersection aussi avec la sphère 1  et 2
        if (distintersec > 0.0) {
            float discam1 = distance(cameraPos, intersecPt1);
       		float discam3 = distance(cameraPos, intersecPt3);

            //on prend le point d'intersection le plus proche de la caméra
            if ((discam3 > discam1)) {
            	intersecPt = intersecPt1;
            	subObjectId = subid;
            	normal = normal1; 
            	dist = distintersec;
        	}
        }
    } else { 
        // intersection avec la sphère 1 et 2 uniquement
   		if (distintersec > 0.0) {     
                intersecPt = intersecPt1;
            	subObjectId = subid;
            	normal = normal1; 
            	dist = distintersec;
        } else {
        	return -1.0;
        }
     }

     //distance entre le point d'intersection et l'origine de la sphere4
     float disS34 = distance(intersecPt,csgSphere4Pos);


     //le point d'intersection de la sphere n'appartient pas à la sphere 4
     if (disS34 > csgSphere4Radius) {

        // on retourne la distance
        return dist;
     }  

    //on calcule le point d'intersection avec la sphere 4 le plus loin
    float dist4 = raySphere4(rayPos, rayDir, csgSphere4Pos, csgSphere4Radius, intersecPt4, normal4);

     //on calcule la distance entre le point d'intersection de la sphère 4 et les centres des autres sphères
     float dis43 = distance(intersecPt4,csgSphere3Pos);
     float disS41 = distance(intersecPt4,csgSphere1Pos);
     float disS42 = distance(intersecPt4,csgSphere2Pos);

     //le point d'intersection de la sphère 4 doit être inclu dans la sphère 3 OU la sphère 1 et 2 
     if ((dist4 > 0.0) && ((dis43 <= csgSphere3Radius) || ((disS41 <= csgSphere1Radius) && (disS42 <= csgSphere2Radius))) ) {
          intersecPt = intersecPt4;
          subObjectId = 4;
          normal = normal4; 
          return dist4;
     }

    return -1.0;

}


//----------------------------------------------------------------------------------------------

// NOTE: planeNormal must be a unit-vector!
float rayPlane(    vec3 rayPos    ,     vec3 rayDir, vec3 planePos, vec3 planeNormal,
               out vec3 intersecPt, out vec3 normal)
{


    float den = dot(planeNormal, rayDir);

    if (abs(den) <= 0.000001)	// To avoid numerical instabilities we consider the ray to be 
        return -1.0;			// parallel if the angle between the normal and the ray is
        						// ALMOST zero.

    float t = dot(planeNormal, planePos - rayPos) / den;

    // As we found the value of t, we can insert it into equation (1) to find
    // the position of the first intersection point encountered along the ray.
    intersecPt = rayPos + t * rayDir;

    // NOTE: The normal to be returned has to be pointing 'towards' the rayPos position.
    //       So, the angle between the ray direction and the normal must > 90°, so the
    //       dot-product should be negative.
    //       This can be expressed by taking the 'sign' of den
    normal = -sign(den) * planeNormal;

    return t;
}

//----------------------------------------------------------------------------------------------

void computeCameraRayFromPixel( in vec2 pixCoord, out vec3 rayPos, out vec3 rayDir)
{


    float focal = 1.0 / tan(radians(cameraFovY) / 2.0);

    // Compute camera three main axis (X,Y,Z) expressed in world space
    //
    //  NOTES: - First, we compute the Z camera unit vector that goes from the camera position
    //           and look toward the camera target.

    vec3 cz = normalize(cameraTarget - cameraPos);


    vec3 up = vec3(0,1,0);							// perfect up vector
    vec3 cx = normalize(cross(-up, cz));

    //         - Finally, we can find the true cy by applying: cy = cross-product(cz, cx)

    vec3 cy = normalize(cross( cz, cx));


    // by the same value: iResolution.y

    vec2 pt = (2.*pixCoord - iResolution.xy) / iResolution.y;

    //origine du rayon, la caméra
    rayPos = cameraPos;

    //direction du rayon
    rayDir = normalize(pt.x * cx - pt.y * cy + focal * cz);
}

//----------------------------------------------------------------------------------------------

// trouve l'intersection la plus proche qu'à le rayon avec les objets
float computeNearestIntersection(vec3 rayPos, vec3 rayDir, in int incId,
                                 out int objectId, out vec3 intersecI, out vec3 normalI)
{
    // Set the default value when no intersection is found: we hit the 'sky'
    float minDist  = FLT_MAX;
          objectId = skyId;

    // Test la sphere
    vec3 intersecS, normalS;
    float distS = raySphere(rayPos, rayDir, spherePos, sphereRadius, intersecS, normalS);
    //float distS = raySphere(rayPos, rayDir, csgSphere1Pos, csgSphere1Radius, intersecS, normalS);
    if ((distS > 0.0) && (distS < minDist))
    {
        objectId  =  sphereId;
        minDist   =     distS;
        intersecI = intersecS;
        normalI   =   normalS;
    }

    // Test intersection avec l'objet CSG
    vec3 intersecC, normalC;
    float distC =  rayCSG(rayPos, rayDir,intersecC, normalC, subObjectIdC);
    if ((distC > 0.0) && (distC < minDist))
    {
        objectId  =   csgId;
        minDist   =   distC;
	    intersecI =   intersecC;
    	normalI   =   normalC;
    }

   	 // Test le plan
    vec3 intersecP, normalP;
    float distP =  rayPlane(rayPos, rayDir,  planePos,  planeNormal, intersecP, normalP);
    if ((distP > 0.0) && (distP < minDist))
    {
       	objectId  =   planeId;
       	minDist   =     distP;
	   	intersecI = intersecP;
    	normalI   =   normalP;
    }


    if (objectId == skyId)
        minDist = -1.0;

    return minDist;
}

