vec3 getSphereColorAtPoint(vec3 pt)
{
    return sphereCol;
}

//----------------------------------------------------------------------------------------------

// pt is assumed to be on the plane surface
vec3 getPlaneColorAtPoint(vec3 pt)
{
    // As the plane is textured with a checkboard pattern, we need to define
    // a 'texture coordinate frame'.


    vec3 worldX = vec3(1,0,0);
    vec3 axisX  = normalize(worldX - dot(worldX, planeNormal) * planeNormal);

    // We then find the plane Y-axis thanks to the cross-product
    // properties with orthonormal basis
    vec3 axisY  = normalize(cross(planeNormal, axisX));

    // Now, find the coordinate of the input point according to this texture coordinate frame
    vec3 diff = pt - planePos; 
    float u = dot(diff, axisX);
    float v = dot(diff, axisY);

    // Finally, apply the checkboard pattern by using this very concise formula:
    return mod(floor(u * 0.5) + floor(v * 0.5), 2.0) < 1.0  ? planeCol1 : planeCol2;
}

//----------------------------------------------------------------------------------------------
vec3 getCSGColorAtPoint(vec3 pt)
{
    if (subObjectIdC == 1)
        return csgCol1;
    if (subObjectIdC == 2)
        return csgCol2;
    if (subObjectIdC == 3)
        return csgCol3;
    if (subObjectIdC == 4)
        return csgCol4;
}


//----------------------------------------------------------------------------------------------

vec3 getObjectColorAtPoint(int objectId, vec3 pt, out Material objectMat)
{
    if (objectId == sphereId)
    {
        objectMat = sphereMat;
        return getSphereColorAtPoint(pt);
    }
    else if (objectId == planeId)
    {
        objectMat = planeMat;
        return getPlaneColorAtPoint(pt);
    } 
    else if (objectId == csgId) 
    {
        objectMat = csgMat;
        return getCSGColorAtPoint(pt);
    }

    return skyCol;
}

//----------------------------------------------------------------------------------------------

float getShadowAtPoint(in vec3 I, in vec3 normalI, in Material objectMat, in vec3 L, in float Ldist)
{
    int object;
    vec3 vec1, vec;
    float dist = computeNearestIntersection(I, L, -1, object, vec1, vec);
    if (dist<Ldist && dist>0.0) {
        return 0.5;
    } else {
        return 1.0;
    }
}

//----------------------------------------------------------------------------------------------

vec3 computePhongShading(vec3 objectCol, Material objectMat, vec3 N, vec3 L, vec3 R, vec3 V, float shadowFactor)
{    
    vec3 ambiant  = objectMat.Ka * ambiantCol;
    vec3 diffuse  = objectMat.Kd *  objectCol * lightCol *     max(dot(N,L), 0.);
    vec3 specular = objectMat.Ks *  lightCol * pow(max(dot(R,V), 0.), objectMat.Kn);

    vec3 phongCol = ambiant + shadowFactor*diffuse + shadowFactor*specular;

    return phongCol;
}

//##############################################################################################

vec4 RaycastAtPixelCoord(vec2 pixCoord) 
{
	// Compute the ray to be casted through the pixel towards the 3D scene
    vec3 rayPos, rayDir;
    computeCameraRayFromPixel(pixCoord, rayPos, rayDir); 

    ShadeInfo infos[MAX_NB_BOUNCES];

    int nbBounces = 0;

    // Test ray-objetcs intersections and find the nearest one
    // (with its associated intersection point and normal at the object surface)
    int  objectId;
    vec3 intersecI, normalI;

    vec3 col;
    vec3 spec;

    do
    {	

    	float distI = computeNearestIntersection(rayPos, rayDir, -1, objectId, intersecI, normalI);

    	// Apply the shading to the points that are on the sphere surface and seen by the camera
    	if (distI <= 0.0)
    	{

            infos[nbBounces].shadedcol = skyCol;
        	infos[nbBounces].Ks = 0.0;
        	break;

        }

        // unit-vector going from the surface point toward the light
	    vec3 L = normalize(lightPos - intersecI);

        // unit-vector of the reflection direction of the light at the surface point
    	vec3 R = 2.0 * dot(normalI, L) * normalI - L;


        // unit-vector going from the surface point toward the camera 
        vec3 V = -rayDir;

        vec3 RayRefr = 2.0 * normalI * dot(normalI, V) - V;



        // Get the diffuse color at the intersection point
        Material objectMat;
        vec3 objectCol = getObjectColorAtPoint(objectId, intersecI, objectMat);

        vec3 I = intersecI + normalI*eps;

        float Ldist = length(lightPos-I);

        float shadowfactor = getShadowAtPoint(I, normalI, objectMat, L, Ldist);

        // Apply the Phong shading to compute the color
        // of the surface point as seen from the camera

        col = computePhongShading(objectCol, objectMat, normalI, L, R, V, shadowfactor);

        infos[nbBounces].shadedcol = col;
        infos[nbBounces].Ks = objectMat.Ks;

        //bounce from the surface towards the reflect direction of the ray

        rayDir = RayRefr;

        rayPos = I;

        nbBounces++;
    }
    while (nbBounces < MAX_NB_BOUNCES);

    vec3 resCol = vec3(0);

    for (int i=0; i<=nbBounces; i++)
    {
        resCol = infos[nbBounces-i].Ks*resCol + infos[nbBounces-i].shadedcol;
    }

    //resCol = vec4(computePhongShading(objectCol, objectMat, normalI, L, R, V, shadowfactor), 1);

    return vec4(resCol, 1);

    //return vec4(computePhongShading(resCol, objectMat, normalI, L, R, V, shadowfactor), 1);; 
}


