vec2 noise2(vec2 location, vec2 delta) 
{
    const vec2 c = vec2(12.9898, 78.233);
    const float m = 43758.5453;
    return vec2(
        fract(sin(dot(location + delta, c)) * m),
        fract(sin(dot(location + vec2(delta.y, delta.x), c)) * m)

	);
}

