const vec3 uLightDir = vec3(0.0, 1.0, 0.0);
const vec3 uLightColor = vec3(5.0,6.0,12.0);
const float uScale = 4.;

struct Material {
    vec3 albedo;
    float roughness;
    float metallic;
};

struct Scene {
    int type;
    float dist;
    Material material;
};

struct DirLight {
    vec3 dir;
    vec3 color;
};

const Material kNoMaterial = Material(vec3(0.0), 0.0, 0.0);

mat2 Rotate(float r) { return mat2(cos(r), sin(r), -sin(r), cos(r)); }
vec3 Repeate(vec3 p, vec3 r) {
//    r*=uScale;
    return mod(p, r) - 0.5 * r;
}

Material MixMaterial(Material a, Material b, float m) {
    return Material(
        mix(a.albedo, b.albedo, m),
        mix(a.roughness, b.roughness, m),
        mix(a.metallic, b.metallic, m)
    );
}

float NormalDistributionFunctionGGXTR(vec3 n, vec3 m, float a)
{
    float a2 = a * a;
    float NdotM = max(0.0, dot(n, m));
    float NdotM2 = NdotM * NdotM;
    float denom = (NdotM * (a2 - 1.0) + 1.0);
    denom = 3.14 * (denom * denom);
    return a2 / denom;
}

float GeometryGGX(float NdotV, float a)
{
    float k = (a + 1.0) * (a + 1.0) / 8.0;
    return NdotV / (NdotV * (1.0 - k) + k);
}

float GeometrySmith(float NdotV, float NdotL, float a)
{
    float g1 = GeometryGGX(NdotL, a);
    float g2 = GeometryGGX(NdotV, a);
    return g1 * g2;
}

vec3 FresnelSchlick(vec3 v, vec3 h, vec3 F0)
{
    float VdotH = max(0.0, dot(v, h));
    return F0 + (1.0 - F0) * pow(1.0 - VdotH, 5.0);
}

vec3 Shade(vec3 n, vec3 v, DirLight l, in Material material)
{
    vec3 F0 = mix(vec3(0.04), material.albedo, material.metallic);
    vec3 h = normalize(v + l.dir);
    float roughness = material.roughness * material.roughness;
    float NdotL = max(0.0, dot(n, l.dir));
    float NdotV = max(0.0, dot(n, v));
    float D = NormalDistributionFunctionGGXTR(n, h, roughness);
    vec3 F = FresnelSchlick(v, h, F0);
    float G = GeometrySmith(NdotV, NdotL, roughness);
    vec3 Kd = (1.0 - F) * (1.0 - material.metallic);
    vec3 radiance = l.color;
    vec3 num = D * F * G;
    float denom = 4.0 * NdotL * NdotV;
    vec3 specularBRDF = num / max(denom, .01);

    return (material.albedo * 0.3) + ((Kd * material.albedo / 3.14 + specularBRDF) * radiance * NdotL);
}

Scene SceneUnion(Scene a, Scene b) {
    if (a.dist < b.dist) return a;
    return b;
}

float Box(vec3 p, vec3 s) {
    vec3 d = abs(p) - (s * uScale);
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

// iq's
float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - (h * uScale);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float Ring(vec3 p, float radius) {
    float outer = sdCappedCylinder(p, vec2(radius, 0.025));
    float inner = sdCappedCylinder(p, vec2(radius-0.05, 1.0));
    return max(outer, -inner);
}

Scene GetOuterBox(vec3 p) {
    p.xz *= Rotate(-iTime * .4);
    p.xy *= Rotate(0.615472907);
    p.yz *= Rotate(0.785398);
    float border = 0.95;
    float scene = Box(p, vec3(1.0));
    float boxOut = scene;
    float remove = Box(p, vec3(2.0, border, border));
    remove = min(remove, Box(p, vec3(border, 2.0, border)));
    remove = min(remove, Box(p, vec3(border, border, 2.0)));
    scene = max(scene, -remove);
    vec3 p2 = p;
    vec3 p3 = p;
    p2.xy *= Rotate(3.14 / 2.0);
    p3.yz *= Rotate(3.14 / 2.0);
    float rings = 100.;
    float ringRadius = 1.1;
    rings = min(rings, Ring(Repeate(p, uScale*vec3(0.0, 1.95, 0.0)), ringRadius));
    rings = min(rings, Ring(Repeate(p2, uScale*vec3(0.0, 1.95, 0.0)), ringRadius));
    rings = min(rings, Ring(Repeate(p3, uScale*vec3(0.0, 1.95, 0.0)), ringRadius));
    rings = max(rings, boxOut);
    return Scene(2, min(scene, rings), Material(vec3(3.0), 0.8, 0.0));
}

Scene GetInnerContent(vec3 p) {
    vec3 pmin = p;
    vec3 pmax = p;
    pmin.xz *= Rotate(-iTime * .4);
    pmin.xy *= Rotate(0.615472907);
    pmin.yz *= Rotate(0.785398);
    pmax.xz *= Rotate(iTime * 0.8 + sin(0.2 * iTime + p.y * 0.1));
    pmax.yz *= Rotate(iTime * 0.8 + sin(0.2 * iTime + p.y * 0.1));
    pmax.xy *= Rotate(iTime * 0.8 + sin(0.2 * iTime + p.y * 0.1));
    float clip = Box(pmin, vec3(0.85));
    vec3 pmod = Repeate(pmax + vec3(0, iTime * 0.5, 0), vec3(0.4) * 2.0);
    float dispSize = ((sin(p.y) + sin(p.z) + sin(p.x) + iTime * 5.0) * 0.15);
    float scene = Box(pmod, vec3(0.1 + 0.2 * sin(dispSize + p.y * 0.19)));

    scene = max(scene, clip);

    return Scene(1, scene, Material(vec3(0.0, 0.0, 0.01), 0.1, 1.0));
}

Scene GetInnerContent2(vec3 p) {
    vec3 pmin = p;
    vec3 pmax = p;
    pmin.xz *= Rotate(-iTime * .4);
    pmin.xy *= Rotate(0.615472907);
    pmin.yz *= Rotate(0.785398);
    pmax.xz *= Rotate(iTime * 0.8 + sin(0.2 * iTime + p.y * 0.1));
    pmax.yz *= Rotate(iTime * 0.8 + sin(0.2 * iTime + p.y * 0.1));
    pmax.xy *= Rotate(iTime * 0.8 + sin(0.2 * iTime + p.y * 0.1));
    float clip = length(pmin)-1.1*uScale;
    vec3 pmod = Repeate(pmax + vec3(0, iTime * 0.5, 0), vec3(0.4) * 2.0);
    float dispSize = ((sin(p.y) + sin(p.z) + sin(p.x) - iTime * 5.0) * 0.15);
    float scene = Box(pmod, vec3(0.2 + 0.8 * sin(dispSize - p.y * 0.19)));

    scene = max(scene, clip);

    return Scene(1, scene, Material(vec3(0.0, 0.01, 0.0), 0.1, 1.0));
}

Scene GetFloor(vec3 p) {
    return Scene(1, Box(p + vec3(0.0, 2.75, 0.0) * uScale, vec3(1, 1.0,1)) - 0.1, Material(vec3(.2,0,0.0), 0.3, 0.));
}

Scene MixScene(Scene a, Scene b, float m) {
    return Scene(
        int(mix(float(a.type), float(b.type), m)),
        mix(a.dist, b.dist, m),
        MixMaterial(a.material, b.material, pow(m, 5.0))
    );    
}

uniform float uFactor;

Scene GetScene(vec3 p) {
    Scene scene = Scene(0, 100., kNoMaterial);

    scene = SceneUnion(scene, Scene(3, 9.0 - p.y, Material(vec3(1), 1.0, 0.0)));
    scene = SceneUnion(scene, GetOuterBox(p));
    scene = SceneUnion(scene, MixScene(GetInnerContent2(p), GetInnerContent(p), abs(sin(0.2*iTime + p.y * 0.01))));
    scene = SceneUnion(scene, GetFloor(p));

    return scene;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(0,.01);
    return normalize(GetScene(p).dist - vec3(
            GetScene(p - e.yxx).dist,
            GetScene(p - e.xyx).dist,
            GetScene(p - e.xxy).dist
        ));
}

Scene RayMarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for (int i = 0; i < 80; ++i) {
        vec3 p = (ro + rd * t);
        Scene scene = GetScene(p);
        if (scene.dist < .01) {
            return Scene(scene.type, t, scene.material);
        }
        t += scene.dist * 0.9;
        if (t > 200.) break;
    }

    return Scene(0, 100., kNoMaterial);
}

vec3 Trace(vec3 ro, vec3 rd) {
    vec3 color = vec3(0.0);
    vec3 skyColor = vec3(0.0);
    float atten = 1.0;
    vec3 point = ro + rd;
    for (int i = 0; i < 2; ++i) {
        Scene scene = RayMarch(ro, rd);
        if (scene.type == 1) {
            vec3 p = ro + rd * scene.dist;
            vec3 n = GetNormal(p);
            vec3 v = normalize(-rd);
            DirLight l = DirLight(uLightDir, uLightColor);
            color += vec3(1.0, 0.2, 0.2) * atten * Shade(n, v, l, scene.material);
            rd = normalize(reflect(rd, n));
            ro = p + rd * 0.1;
            atten *= 0.2;
        } else if (scene.type == 2) {
            vec3 p = ro + rd * scene.dist;
            vec3 n = GetNormal(p);
            vec3 v = normalize(-rd);
            DirLight l = DirLight(uLightDir, uLightColor);
            color += atten * Shade(n, v, l, scene.material);
            if (i == 0) {
               scene = RayMarch(p + n * 0.03, n);
                if (scene.type != 0) {
                    color = mix(color, color * clamp(scene.dist * 10.0 / (uScale * uScale), 0.0, 1.0), 0.7);
                }
            }
            break;
        } else {
            break;
        }
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ar = vec2(iResolution.x/iResolution.y, 1.0);
    vec2 uv = (fragCoord.xy/iResolution.xy-.5) * ar;
    vec3 ro = vec3(0,-0.2,-5) * uScale;
    vec3 rd = normalize(vec3(uv, 1.2) * uScale);
    vec3 color = vec3(0.0);
    float mx = (iMouse.x/iResolution.x * 2.0 - 1.0) * 3.14;

    ro.xz *= Rotate(mx);
    rd.xz *= Rotate(mx);

    float offset = 0.005;

    color = Trace(ro, rd);

    color = mix(color, color * 0.35, max(0.0, length(2. * uv/ar)));

    // tone mapping
    color = color / (color + 1.0);

    // gamma correction
    color = pow(color, vec3(1.0 / 2.2));


    fragColor = vec4(color, 1.0);
}

#define t iTime*.5
#define sat(s) clamp(s, 0., 1.)
#define spow(v, p) sat(pow(v, p))

const int MAX_ITER = 100;
const float MAX_DIST = 40.;

float vmax(vec3 v) { return max(max(v.x, v.y), v.z); }
float vmin(vec3 v) { return min(min(v.x, v.y), v.z); }

float sphere(vec3 p, float s) { return length(p) - s; }
float box(vec3 p, vec3 s) { return vmax(abs(p)-s); }

vec2 repeat(vec2 p, float n) { return mod(p, n) - .5*n; }
vec3 scale(vec3 p, vec3 n) { return (p/n) * n; }

struct ray {
  vec3 o, d;
};
struct material {
  vec3 d, s;
  float sh;
};

float dist(vec3 p) {
  vec3 sp = p;

  sp.xz = repeat(p.xz, 1.5);
  sp.y *= .25 + .05*sin(4.*cos(t)+p.z);

  vec3 sp2 = sp;
  sp2 *= 1. + .1*sin(3.*t+fract(10.*p.y+20.*p.z));

  float b = box(sp, vec3(.8, 1.9,.8));
  float ss = sphere(sp, .996+.025*sin(p.x+p.y+t*3.));
  float ss2 = sphere(sp, .1);
  float s = min(max(-ss, b), ss2);

  float pl = p.y + 2.;
  return min(s, pl);
}

vec2 march(ray r) {
  float d = 0.;
  for(int i = 0; i<MAX_ITER; i++) {
    float h = dist(r.o+r.d*d);
    if(h<0.0001*d || d>MAX_DIST) return vec2(d, i);
    d+=h;
  }
  return vec2(d, MAX_ITER);
}

vec3 normal(vec3 p) {
  vec2 e=vec2(0,0.01);
  return normalize(dist(p) - vec3(dist(p-e.yxx), dist(p-e.xyx), dist(p-e.xxy)));
}

vec3 shade(vec3 p, vec3 eye, material m, vec3 light) {
  vec3 l = normalize(light-p);
  vec3 n = normal(p);
  vec3 v = normalize(eye-p);
  vec3 rf = normalize(reflect(-l, n));
  float dotln = dot(n, l);
  float dotrv = dot(rf, v);
  vec3 phong;
  if (dotln <= 0.0) { phong = vec3(0); }
  else if (dotrv <= 0.0) { phong = m.d * dotln; }
  else phong = m.d * dotln + m.s * pow(dotrv, m.sh);

  ray r;
  r.o = p;
  r.d = normalize(light-p);
  vec2 lm = march(r);
  if(length(light-p) > lm.x) phong *= .7;

  return phong;
}

ray camera(vec2 uv, vec3 eye, vec3 target, float zoom) {
  ray r;
  r.o = eye;
  vec3 f = normalize(target - eye);
  vec3 s = normalize(cross(vec3(0,1,0), f));
  vec3 u = cross(f,s);
  r.d = normalize(f*zoom + uv.x*s + uv.y*u);
  return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy/iResolution.xy;
  vec2 vguv = uv * (1.0 - uv);
  float vg = pow(20.*vguv.x*vguv.y, .5);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1.);

  vec3 eye = vec3(0,0,0);
  eye.xz += 4.*vec2(sin(cos(t*.02)*8.), cos(sin(t*.03)*12.));
  vec3 target = vec3(0,-2,0);

  ray r = camera(uv, eye, target, .2+.0125*sin(sin(t*.9)*3.));
  vec2 m = march(r);
  if(m.x>MAX_DIST) {
    fragColor = vec4(0);
    return;
  }

  vec3 p = r.o+r.d*m.x;

  float halo = spow(3.*m.y/float(MAX_ITER), 3.);
  float att =  spow(1. - m.x/(MAX_DIST), 10.);
  vec3 light = vec3(0,1,0);
  light.xz += 2.*vec2(sin(t*.5), cos(t));

  material ma;
  ma.d = vec3(.3,.3,1);
  ma.s = vec3(1);
  ma.sh = 10.;
  material mb;
  mb.d = vec3(.1,.4,.3);
  mb.s = vec3(1);
  mb.sh = 10.;
  vec3 la = shade(p, eye, ma, light);
  vec3 lb = shade(p, eye, mb, -light);
  vec3 color = att * (la+lb);
  color *= .1+.9*halo;

  color *= vec3(vg);
  fragColor = vec4(pow(color, vec3(1./2.2)), 1.);
}

