vec2 sc,e=vec2(.00035,-.00035);float t,tt; vec3 newPos;

float mx(vec3 p){return max(max(p.x,p.y),p.z);}
float bo(vec3 p,vec3 r){return mx(abs(p)-r);}

vec2 fb( vec3 p)
{
  vec2 h,t=vec2(.8*bo(p,vec3(1,.5,6)),5);
  t.x=min(t.x,.8*bo(p-vec3(1,0,0),vec3(.2,1,1.5)));
  t.x=min(t.x,.8*bo(p+vec3(1,0,0),vec3(.2,1,1.5)));
  h=vec2(.8*bo(abs(p)-vec3(.25,0,1.7),vec3(.15,1,1.4)),3);
  t=(t.x<h.x)?t:h;
  return t;
}

mat2 r2(float r){ return mat2(cos(r),sin(r),-sin(r),cos(r));}

vec2 mp( vec3 p)
{
  vec2 t;
  p.z=mod(p.z+tt*10.,50.)-25.;//clones it along z
  p.yx*=r2(sin(p.z*0.05+tt)*2.);//rotate it along z
  newPos=p;
  newPos.z=mod(p.z+tt*10.,25.)-12.5;
  float att=clamp(length(p)-1.5,3.,13.);
  for(int i=0;i<4;i++){
    newPos=abs(newPos)-vec3(0.5+att*0.3,0.5+att*0.1,2);
    newPos.yx*=r2(abs(cos(p.z*0.05*float(i))));
  }
  t=fb(newPos);
  t.x=max(t.x,bo(p,vec3(15,15,23)));
  return t;
}

vec2 tr( vec3 ro,vec3 rd )
{
  vec2 h,t=vec2(.1);
  for(int i=0;i<128;i++){
    h=mp(ro+rd*t.x);
    if(h.x<.0001||t.x>60.) break;
    t.x+=h.x;t.y=h.y;
  }
  if(t.x>60.) t.x=0.;
  return t;
}

float noise(vec3 p){
  	vec3 ip=floor(p);p-=ip; 
    vec3 s=vec3(7,157,113);
    vec4 h=vec4(0.,s.yz,s.y+s.z)+dot(ip,s);
    p=p*p*(3.-2.*p); 
    h=mix(fract(sin(h)*43758.5),fract(sin(h+s.x)*43758.5),p.x);
    h.xy=mix(h.xz,h.yw,p.y);
    return mix(h.x,h.y,p.z); 
}

vec3 sky(vec3 rd){return clamp(vec3(.4,.4,.5)-rd.y*.3,0.,1.);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  tt=mod(iTime,100.);
  vec3 ro=vec3(0,0,-10),
  cw=normalize(vec3(sin(tt)*10.,0,cos(tt)*3.)-ro),
  cu=normalize(cross(cw,vec3(0,1,0))),
  cv=normalize(cross(cu,cw)),
  rd=normalize(mat3(cu,cv,cw)*vec3(uv,.5)),
  co,fo,ld=normalize(vec3(-.3,0,.3));co=fo=sky(rd);
  sc=tr(ro,rd);t=sc.x;

  if(t>0.){
    vec3 po=ro+rd*t,
    no=normalize(e.xyy*mp(po+e.xyy).x+
    e.yyx*mp(po+e.yyx).x+
    e.yxy*mp(po+e.yxy).x+
    e.xxx*mp(po+e.xxx).x),al=vec3(.5);
    if(sc.y<5.) al=vec3(1,.5,0);
    //LIGHTING MICRO ENGINE
    float dif=max(0.,dot(no,ld)),
    aor=t/50.,
    ao=exp2(-2.*pow(max(0.,1.-mp(po+no*aor).x/aor),2.)),
    spo= exp2(5.0+3.0*noise(newPos/vec3(.2,.4,.6))),
    fresnel=pow(1.+dot(no,rd),4.);
    vec3 sss=vec3(.5)*smoothstep(0.,1.,mp(po+ld*0.4).x/0.4),
    spec=vec3(2)*pow(max(0.,dot(no,normalize(ld-rd))),spo)*spo/32.;
    co=mix(spec+al*(0.8*ao+.2)*(dif+sss),sky(rd),fresnel);
    co=clamp(co,0.,1.);
    co=mix(co,fo,1.-exp(-.00002*t*t*t));
  }
  fragColor = vec4(pow(co,vec3(0.45)),1);
}

vec2 random2( vec2 p ) 
{
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=fragCoord.xy/iResolution.xy;
    float n=8.;
    vec3 color=vec3(0.);
    vec2 fst=fract(uv*n);
    vec2 ist=floor(uv*n);
    float m_dist=1.;

    for(int y=-1;y<=1;y++)
    {
 		for(int x=-1;x<=1;x++)
    	{
        	vec2 neighbor=vec2(x,y);  
            vec2 point=random2(ist+neighbor);
            point=0.5+0.5*sin(iTime*5.+6.2831*point);
            vec2 diff=neighbor+point-fst;
            float dist=length(diff);
            m_dist=min(m_dist,dist);
    	}
    }

    color+=m_dist;

    color+=1.-smoothstep(0.,0.1,m_dist);

    fragColor= vec4(color,1.);
}

// Single pole highpassed triangular dither.
// Inspired by: https://www.airwindows.com/pauldither/

// EDIT 2: Added blue noise for fun
// Regular bluenoise dither is clearly perceptually superior,
// but the difference between highpassed noises is much more subtle.


// change the simulated bit depth here
#define BIT_DEPTH 2

// uncomment this to get RGB dither
//#define CHROMA

// uncomment this to use the video as demo
//#define DEMO_VIDEO


/*
	the gradient demo is laid out like
	white noise
	highpassed white noise
	blue noise
	highpassed blue noise

	while the video demo is laid out like
	white noise | highpassed white noise
	blue noise  | highpassed blue noise
*/


#define saturate(x) clamp(x, 0.0, 1.0)
#define remap(x, a, b) (((x) - (a)) / ((b) - (a)))


// iq's integer hash II
// https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;  // GLIB C
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}


// Divvy up the 1024x1024 noise for demonstration purposes
// however you get animated blue noise is fine so long as you can
// reliably get the previous frame's noise. Using golden ratio to animate the noise
// didn't seem to play nice with highpass though.
#define NOISE_TILE_SIZE 64
vec3 bluenoise(int idx, ivec2 coord)
{
    const int s = NOISE_TILE_SIZE;
    idx %= 1024 / s;
	coord = ivec2(idx * s, (idx / s) * s) + coord % s;
    return texelFetch(iChannel0, coord, 0).rgb;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // demo image
#ifdef DEMO_VIDEO
    vec3 col = texture(iChannel1, uv).rgb;
#else
    vec3 col = vec3(uv.x);
#endif

    // (2^8)-1 = 255, etc.
    const float lsb = exp2(float(BIT_DEPTH)) - 1.0;

    // generate one noise using current frame as seed,
    // and one noise using previous frame as seed.
    vec3 n1 = hash(uvec3(fragCoord.xy, iFrame));
    vec3 n2 = hash(uvec3(fragCoord.xy, iFrame-1));

    vec3 b1 = bluenoise(iFrame  , ivec2(fragCoord));
    vec3 b2 = bluenoise(iFrame-1, ivec2(fragCoord));

    vec3 dither;
    vec3 uni;
    if (uv.y > 0.5)
    {
#ifdef DEMO_VIDEO
        bool split = uv.x < 0.5;
#else   
        bool split = uv.y > 0.75;
#endif
        uni = n1;
    	dither = split ? 
            // normal tpdf
            n1 + n2 - 1.0 :
        	// highpass tpdf
        	n1 - n2;
    }
    else 
    {
#ifdef DEMO_VIDEO
        bool split = uv.x < 0.5;
#else   
        bool split = uv.y > 0.25;
#endif
        // EDIT 3: fixed edgecase solution for blue noise,
        // which was lerping to uniform white noise instead
        // of uniform blue noise.
        uni = b1;
    	dither = split ? 
            // normal tpdf
            b1 + b2 - 1.0 :
        	// highpass tpdf
        	b1 - b2;
    }

    // EDIT 1: added hornet's edgecase solution for completeness
    const float lobit = 0.5 / lsb;
    const float hibit = (lsb - 0.5) / lsb;
    vec3 lo = saturate(remap(col, vec3(0.0), vec3(lobit)));
    vec3 hi = saturate(remap(col, vec3(1.0), vec3(hibit)));
    dither = mix(uni - 0.5, dither, min(lo, hi));

#ifndef CHROMA
    dither = vec3(dither.r);
#endif

    // apply dither
    col += dither / lsb;

    // quantise
    col = round(col * lsb) / lsb;

    // output to screen
    fragColor = vec4(col,1.0);
}

