void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float faze = cos(iTime / 1.227);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(faze + uv.xyx+vec3(0,2,4));

    float iWidth = iResolution.x * faze;
    float iHeight = iResolution.y * faze;

    if (int(fragCoord.x) % int(iWidth) == 0 && fragCoord.x > 1.0 - faze) col = vec3(1.0 - col.x,1.0-col.y,1.0-col.z);
	if (int(iResolution.x - fragCoord.x) % int(iWidth) == 0 && fragCoord.x > 1.0 - faze) col = vec3(1.0 - col.x,1.0-col.y,1.0-col.z);
	if (int(fragCoord.y) % int(iHeight) == 0 && fragCoord.y > 1.0 - faze) col = vec3(1.0 - col.x,1.0-col.y,1.0-col.z);
	if (int(iResolution.y - fragCoord.y) % int(iHeight) == 0 && fragCoord.y > 1.0 - faze) col = vec3(1.0 - col.x,1.0-col.y,1.0-col.z);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159

#define REP(p, r) (mod(p + r/2.,r) - r/ 2.)

float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

mat2 rot(float a)
{
  float ca = cos(a);
  float sa = sin(a);
  return mat2(ca,-sa,sa,ca);
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) - r
         + min(max(d.x,max(d.y,d.z)),0.0); 
}

float map(vec3 p)
{
  vec3 cp = p;
  float dist = 1000.;

  float time = iTime * .25;
 p *= .36;
  p.zx *= rot(-time * .25);

  p.xz *= rot(p.z*1.1);

  for(float it = 0.; it < 2.; it += 1.)
{
  p.xz *= rot(sin(p.y + time + (fract(sin(it * 2369.)))) * PI / (it + 1.) * .5);

//  p.y += p.x * .125;
  p.zy *= rot(time+PI);
vec3 ap = max(vec3(0.),abs(p)) - 1.;
float cu = length(ap)-.5 + min(max(ap.x,max(ap.y,ap.z)), 0.);
  dist  =smin(dist, cu , .25);

}

  p = cp;
  p.xz *= rot(-time * .5);


  float rad = 5.;
  float wi = .5;
  float sph = length(p) - rad + wi /2.;

  dist = -smin(sph,-dist,.15);

  sph = length(p) - rad - wi /2.;

  dist = -smin(-sph,-dist,.15);

  sph = distance(cp, vec3(0.,0.,-5.)) - 2.;

  dist = -smin(sph,-dist,.3);

  return dist;
}

float ray(inout vec3 cp, vec3 rd, out float cd)
{
  float st = 0.;
  for(;st < 1.; st += 1. /128.)
  {
    cd = map(cp);
    if(cd < .01)
    {
      break;
    }
    cp += rd * cd * .75;
  }

  return st;
}

vec3 normal(vec3 p)
{
  vec2 e = vec2(.01,.0);

  return normalize(vec3(
  map(p - e.xyy) - map(p + e.xyy),
  map(p - e.yxy) - map(p + e.yxy),
  map(p - e.yyx) - map(p + e.yyx)
));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);
  uv *= 1.4;

  vec3 eye = vec3(0.,0.,-10.);
  vec3 rd = normalize(vec3(uv, 1.));
  vec3 cp = eye;

  float cd;
  float st = ray(cp, rd, cd);

  fragColor = vec4(0.);
  if(cd < .01)
  {
    vec3 ld = normalize(vec3(0.,-1.,1.));
    ld.xz *= rot(iTime * .1);

    vec3 norm = normal(cp);
    float li = dot(ld, norm);

    ld.zy *= rot(iTime * .25);
    float li2 = dot(normalize(vec3(1.,0.,1.)), norm);


    float f = pow(max(li,li2), 2.);
    f = sqrt(f);
    vec4 col = vec4(norm, 0.);

    col.xy *= rot(iTime * .5);
    col.yz *= rot(iTime * .75);
    col.xz *= rot(iTime * .125);
    col = abs(col);
    fragColor = mix(vec4(0.), col * 1.5, f);
  }

}

