////////////////////////////////////////////////////////////////////////////////
//
// Created 2019 by Matthew Arcus
//
// Simple 2D Voronoi. Examine the 16 cells nearest to the nearest grid point.
// This allows us to let the cell point range outside the cell, up to 1.0
// in each direction from the centre (we should keep it to about 0.75 to
// avoid glitches, but with 1.0 we only get occasional misalignments).
//
// For drawing the lines, taking the minimum distance to the boundary lines
// of the two closest points seems to avoid most of the more serious glitches
// (see iq's various Voronoi shaders for the proper way to do this).
//
// Mouse-x: zoom
// Mouse-y: line width
// 'g': show grid
// 'v': show voronoi cells
// 'p': show cell points
// 'x': only check nearest point for line
//
////////////////////////////////////////////////////////////////////////////////

// Wang Hash, used to seed RNG
uint ihash(uint a) {
  a = (a ^ 61U) ^ (a >> 16);
  a *= 9U;
  a = a ^ (a >> 4);
  a = a * 0x27d4eb2dU;
  a = a ^ (a >> 15);
  return a;
}

// Note that we convert from float to integer to unsigned
uint ihash(ivec2 p) {
  uint i = uint(p.x), j = uint(p.y);
  return ihash((i << 16) ^ j);
}

uint xorshift(uint value) {
  // Xorshift*32
  // Based on George Marsaglia's work: http://www.jstatsoft.org/v08/i14/paper
  value ^= value << 13;
  value ^= value >> 17;
  value ^= value << 5;
  return value;
}

uint seed = 1U;
uint rng() {
  //seed = lcg(seed);
  seed = xorshift(seed);
  return seed;
}

// Uniform random float in [0,1)
float rand() {
  return float(rng())/pow(2.0,32.0);
}

const int CHAR_G = 71;
const int CHAR_H = 72;
const int CHAR_P = 80;
const int CHAR_V = 86;
const int CHAR_X = 88;

bool keypress(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
  return v * mix( vec3(1.0), rgb, s);
}

vec2 perp(vec2 r) {
  return vec2(-r.y,r.x);
}

float segment(vec2 p, vec2 a, vec2 b) {
  vec2 pa = p - a;
  vec2 ba = b - a;
  float h = dot(pa, ba) / dot(ba, ba);
  //h = clamp(h, 0.0, 1.0);
  float d = length(pa-ba*h);
  return d;
}

float bisectordistance(vec2 pos, vec2 a, vec2 b) {
  vec2 centre = 0.5*(a+b);
  vec2 r = perp(a-centre);
  return segment(pos,centre-r,centre+r);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float scale = 1.0;
  float gridsize = 4.0; // Cells per unit distance
  float lwidth = 0.05;
  float speed = 0.5;

  if (iMouse.x != 0.0) {
    scale *= 2.0*iMouse.x/iResolution.x;
    lwidth *= 2.0*iMouse.y/iResolution.y;
  }
  vec2 pos  = (2.0*fragCoord - iResolution.xy)/ iResolution.y;
  pos *= scale;
  pos *= gridsize;
  vec2 grid = round(pos); // Nearest grid point
  // vec4(p.x,p.y,distance,color);
  vec4 minpoint[3] = vec4[](vec4(1e8),vec4(1e8),vec4(1e8));   // Keep 3 closest points in here.
  // And iterate through 16 cells around the grid point.
  for (float i = -2.0; i <= 1.0; i++) {
    for (float j = -2.0; j <= 1.0; j++) {
      vec2 cell = grid + vec2(i,j);
      seed = ihash(ivec2(cell)); // Seed RNG for this cell
      vec2 p = vec2(rand(),rand());
      vec2 offset = p*(speed*iTime);
      float K = 4.0;
      offset = mod(offset,K);
      offset = min(offset,K-offset);
      offset -= 0.25*K;
      vec2 cellpoint = cell+vec2(0.5)+offset;
      float d = distance(cellpoint,pos);
      vec4 celldata = vec4(cellpoint,d,rand());
      // Insertion sort for 3 closest points
      if (d < minpoint[2].z) {
	if (d >= minpoint[1].z) {
	  minpoint[2] = celldata;
	} else {
	  minpoint[2] = minpoint[1];
	  if (d >= minpoint[0].z) {
	    minpoint[1] = celldata;
	  } else {
	    minpoint[1] = minpoint[0];
	    minpoint[0] = celldata;
	  }
	}
      }
    }
  }
  vec3 basecolor = vec3(0.8);
  if (!keypress(CHAR_V)) {
    float d = 1e8;
    d = min(d,bisectordistance(pos,minpoint[0].xy,minpoint[1].xy));
    if (!keypress(CHAR_X)) d = min(d,bisectordistance(pos,minpoint[0].xy,minpoint[2].xy));
    basecolor = hsv2rgb(minpoint[0].w,1.0,1.0);
    basecolor = mix(vec3(0.2),basecolor,smoothstep(lwidth,1.25*lwidth,d));
  }

  if (keypress(CHAR_G)) {
    // Draw grid
    vec2 gridpos = pos-floor(pos); // Distance to cell wall
    gridpos = min(gridpos,1.0-gridpos);
    basecolor = mix(vec3(0.5),basecolor,
                    smoothstep(0.02,0.025,min(gridpos.x,gridpos.y)));
  }
  if (!keypress(CHAR_P)) {
    basecolor = mix(vec3(0),basecolor,
                    smoothstep(1.25*lwidth,1.5*lwidth,distance(pos,minpoint[0].xy)));
  }
  fragColor = vec4(basecolor,1);
}

#define noise(x) fract(sin(dot(vec2(x), vec2(12.9898, 78.233)))* 43758.5453)
#define combine2D(d1,d2) d1 < d2 ? d1 : d2

// numbers 0123456789
int c[] = int[](31599,9362,29671,29391,23497,31183,31215,29257,31727,31695);
float getBit(int num,int bit)
{
	return float( num>>bit & 1);
}

float Sprite3x5(int sprite,vec2 p)
{
	float bounds = float(all(lessThan(p,vec2(3,5))) && all(greaterThanEqual(p,vec2(0,0))));
	return getBit(sprite,int((2.0 - p.x) + 3.0 * p.y)) * bounds;
}

float Digit(int n,vec2 p)
{
	n = int(mod(float(n),10.0));
    return Sprite3x5(n == 0 ? c[0] : n == 1 ? c[1] : n == 2 ? c[2] : n == 3 ? c[3] : n == 4 ? c[4] : n == 5 ? c[5] : n == 6 ? c[6] : n == 7 ? c[7] : n == 8 ? c[8] : n == 9 ? c[9] : 0,p);
}

// noise and fbm function from https://www.shadertoy.com/view/Xd3GD4
//-----------------------------------------------------------------------------
vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			 dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise2d( in vec2 p )
{
	const float K1 = 0.366025404; // (sqrt(3)-1)/2;
	const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y)*K1 );

	vec2 a = p - i + (i.x+i.y)*K2;
	vec2 o = (a.x>a.y) ? vec2(1.0,0.0) : vec2(0.0,1.0);
	vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

	vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

	return dot( n, vec3(70.0) );
}

float fbm(vec2 uv)
{
	float f;
	mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
	f  = 0.5000*noise2d( uv ); uv = m*uv;
	f += 0.2500*noise2d( uv ); uv = m*uv;
	f += 0.1250*noise2d( uv ); uv = m*uv;
	f += 0.0625*noise2d( uv ); uv = m*uv;
	f = 0.5 + 0.5*f;
	return f;
}

float dBox2d(vec2 p, vec2 b) {
	return max(abs(p.x) - b.x, abs(p.y) - b.y);
}

float sdQuads( in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p3, in vec2 p )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p3 - p2;
    vec2 e3 = p0 - p3;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;
    vec2 v3 = p - p3;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    vec2 pq3 = v3 - e3*clamp( dot(v3,e3)/dot(e3,e3), 0.0, 1.0 );

    float s = sign( e0.x*e3.y - e0.y*e3.x );
    vec2 d = min( min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                 	   vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) )),
                       vec2( dot( pq3, pq3 ), s*(v3.x*e3.y-v3.y*e3.x) ));

	return -sqrt(d.x)*sign(d.y);
}

vec3 buildingTex(vec2 uv)
{
    float n = 7.9;
    uv.x -= 0.093;
    vec2 st = uv;
    vec3 col = vec3(0.0);
    if(uv.x>-0.55 && uv.x<0.35){
         st = fract(uv * n);
         col = vec3(1.0-smoothstep(0.009,0.01,dBox2d(st, vec2(0.5,0.4))))*vec3(-0.3);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);

    // bg color
    vec3 col = vec3(0.65,0.65,0.7);
    vec3 bcol = vec3(0.55,0.55,0.6);
    vec3 mcol = vec3(0.25,0.25,0.3);
    vec3 fcol = vec3(0.6,0.6,0.6);

    // smoke
    float smokeD = dBox2d(uv+vec2(1.0,0.0),vec2(0.01,0.8))*fbm(uv - vec2(sin(iTime*0.1)*1.5,iTime*0.2));
    col = mix( col, (vec3(0.95)+vec3(smokeD))*col, 1.0-smoothstep(0.13,0.15,abs(smokeD)) );

    // fake camera shake
    uv.y += fbm(vec2(0.0,iTime*0.3))*0.1;

    vec2 uvRef = uv;
    // bottom layer
    uvRef.x += iTime*0.5;
	float n = floor(uvRef.x/0.5);
    uvRef.y -=0.8;
	uvRef.y += noise(n)*0.3;
	uvRef.x = mod(uvRef.x,1.4)-0.7;

    float nn = floor(uv.y/1.1);
    float bl0 = sdQuads(vec2(0.1,0.1),vec2(-0.1,0.15),vec2(-0.1,-1.5),vec2(0.1,-1.5),uvRef);
    float bl1 = sdQuads(vec2(0.5,-0.05),vec2(0.1,0.05),vec2(0.1,-1.5),vec2(0.5,-1.5),uvRef);
    float bl2 = sdQuads(vec2(-0.1,0.05),vec2(-0.3,-0.1),vec2(-0.3,-1.5),vec2(-0.1,-1.5),uvRef);
    float bl3 = sdQuads(vec2(-0.3,-0.3),vec2(-0.6,-0.3),vec2(-0.6,-1.5),vec2(-0.3,-1.5),uvRef);
    float blD = combine2D(bl0,bl1);
    float blD1 = combine2D(bl2,bl3);
    float blD2 = combine2D(blD,blD1);
    col = mix( col, bcol, 1.0-smoothstep(0.009,0.01,blD2) );

    uvRef = uv;

    uvRef.x += iTime*0.5;
    n = floor(uvRef.x/1.2);
    uvRef.y += 0.7;
	uvRef.y += noise(n)*0.8;
    uvRef.x = mod(uvRef.x,0.4)-0.2;

    float buildingD = dBox2d(uvRef,vec2(0.005,0.5));
    col = mix( col, bcol, 1.0-smoothstep(0.2,0.21,abs(buildingD)) );

    uvRef = uv;
    // middle layer
	uvRef.x += iTime*0.6;
	n = floor(uvRef.x/0.5);
    uvRef.y -=0.5;
	uvRef.y += noise(n)*0.3;
	uvRef.x = mod(uvRef.x,1.4)-0.7;

    float ml0 = sdQuads(vec2(0.1,-0.2),vec2(-0.1,-0.25),vec2(-0.1,-1.5),vec2(0.1,-1.5),uvRef);
    float ml1 = sdQuads(vec2(0.5,0.1),vec2(0.1,-0.05),vec2(0.1,-1.5),vec2(0.5,-1.5),uvRef);
    float ml2 = sdQuads(vec2(-0.1,-0.2),vec2(-0.3,-0.15),vec2(-0.3,-1.5),vec2(-0.1,-1.5),uvRef);
    float ml3 = sdQuads(vec2(-0.3,-0.3),vec2(-0.65,-0.3),vec2(-0.65,-1.5),vec2(-0.3,-1.5),uvRef);
    float mlD = combine2D(ml0,ml1);
    float mlD1 = combine2D(ml2,ml3);
    float mlD2 = combine2D(mlD,mlD1);
    col = mix( col, mcol, 1.0-smoothstep(0.009,0.01,(mlD2)) );

    uvRef = uv;
    // top layer
    uvRef.x += iTime*1.5;
    n = floor(uvRef.x/3.0);
    uvRef.y += 0.5;
	uvRef.y += noise(n)*1.1;
    uvRef.x = mod(uvRef.x,1.5)-0.75;

    buildingD = dBox2d(uvRef,vec2(0.3,0.5));
    col = mix( col, fcol+buildingTex(uvRef), 1.0-smoothstep(0.2,0.21,(buildingD)) );

    // meter
    uv = ( fragCoord.xy /iResolution.xy ) * vec2(128,64);
	vec2 cpos = vec2(112.5,56.3);
	float dc = Digit(int(fract(iTime)*10.0),floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(int(fract(iTime)*20.0),floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(int(fract(iTime)*30.0),floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(int(fract(iTime)*40.0),floor(uv-cpos));
    col = mix(col,vec3(0.0),dc );

    cpos = vec2(112.0,57.0);
	dc = Digit(int(fract(iTime)*10.0),floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(int(fract(iTime)*20.0),floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(int(fract(iTime)*30.0),floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(int(fract(iTime)*40.0),floor(uv-cpos));
   	col = mix(col,vec3(1.0),dc );

    // result
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   vec2 position = uv;

    float time = iTime*5.0;

    float color = 0.0;
    color += sin( position.x * cos( time / 15.0 ) * 80.0 ) + cos( position.y * cos( time / 15.0 ) * 10.0 );
    color += sin( position.y * sin( time / 10.0 ) * 40.0 ) + cos( position.x * sin( time / 25.0 ) * 40.0 );
    color += sin( position.x * sin( time / 5.0 ) * 10.0 ) + sin( position.y * sin( time / 35.0 ) * 80.0 );
    color *= sin( time / 10.0 ) * 0.5;


    // Time varying pixel color
    vec3 col = vec3( color, color * 0.5, sin( color + time / 3.0 ) * 0.75 );

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359
#define degToRad (PI * 2.0) / 360.0

float c_0 = 31599.0;
float c_1 = 9362.0;
float c_2 = 29671.0;
float c_3 = 29391.0;
float c_4 = 23497.0;
float c_5 = 31183.0;
float c_6 = 31215.0;
float c_7 = 29257.0;
