    col = mix( col, vec3(0.5,0.95,0.9), 1.0-smoothstep(0.006,0.01,abs(ad)) );

    col = line( col, vec2(0.6, 0.8), vec2(0.6, -0.05), uv, vec2(0.003,2.0), vec4(bcol,2.0) );
    col = line( col, vec2(-0.6, 0.8), vec2(-0.6, -0.05), uv, vec2(0.003,2.0), vec4(bcol,2.0) );

