	dc += Digit(fract(iTime)*50.0,floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(10.0,floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(fract(iTime)*70.0,floor(uv-cpos));
	cpos.x += 3.5;
	dc += Digit(fract(iTime)*80.0,floor(uv-cpos));
	cpos.x += 3.5;
