// Update 1: Changed isoline drawing code to code from fabrice's comment
// Update 2: Fixed sign of distance function

const vec3 c = vec3(1.,0., -1.);
const float pi = acos(-1.);

// Distance to circle segment
float circlesegment(vec2 x, float r, float p0, float p1)
{
    float p = atan(x.y, x.x);
    vec2 philo = vec2(max(p0, p1), min(p0, p1));
    if((p < philo.x && p > philo.y) || (p+2.*pi < philo.x && p+2.*pi > philo.y) || (p-2.*pi < philo.x && p-2.*pi > philo.y))
    	return abs(length(x)-r);
    return min(
        length(x-r*vec2(cos(p0), sin(p0))),
        length(x-r*vec2(cos(p1), sin(p1)))
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy-.5;

    float d = circlesegment(uv-.4*c.xy,.1, pi/8., 15.*pi/8.);
    d = min(d, circlesegment(uv+.4*c.xy,.1, -pi/2., pi/2.));
    d = abs(d)- .02;
    float e = 1.5/iResolution.y;

    vec3 col = cos(iTime+uv.xyx+vec3(0,2,4)) * smoothstep(.1+e, 0., abs( fract(20.*d) - .2 ) ) + vec3(1,0,0) * smoothstep(e, -e,d);
    fragColor = vec4(col,1.0);
}

// Thanks to @FrabriceNeyret2 for all the help and code factoring :)

vec2  size = vec2(.4,.25);
const float 
    border = .01,
        PI = 3.14159265359;

#define R             iResolution
// Anti alisaing
#define S(v)          smoothstep(2./R.y, 0., v)
#define circle(U,r)   S( length(U)-(r) )
#define rot(a)        mat2(cos(a),-sin(a), sin(a),cos(a))

float box( vec2 U,  vec2 r){
    U = S( abs(U) - .5*r );
    return U.x*U.y;
}

void mainImage( out vec4 O, vec2 U ){
    // Normalized Coords * square rotation from center of box aka actually rotating everything 
	U = ( U/R.xy - .5) * rot (PI * sin(iTime / 8.) ); 

    // M = mask;
    // M.x = rect without border
    // M.y = rect with border
    vec2 M = vec2(0), D;
    for (float t, i=0.; i<5.; i++) {
        // Rotation from center of page
        t = iTime + 2.*PI/5.*i;
        D = .2 * vec2( sin(t), cos(t)*1.5 );
        // A new box will only add if M.y has not been already set to white by previous boxes.
        // Making it so M.x(which is smaller) does not add itself onto previous boxes borders
        M += (1.-M.y)* vec2( box(U+D, size), box(U+D, size+border) );
    }

    O = mix( vec4(1), vec4(.9,.3,.3,1) , circle(U,.27 ));
	// This mixes M.x with M.y where M.y has the squares as black and
	// and M.x has its square as white(mixed witht the circle)
    // So when they add, the only part that is black in both is the border
    // O = mix( vec4(1), O*M.x, M.y);

    // This other version does the same but instead M.x becones -1 to 0 and when multiplied my M.y
    // Every black pixel which is now -1 that is outside M.y squares becomes 0
    // And when adding +1. It becomes white.
    // Basically, making everything outside M.y white
    O = 1. + (O*M.x - 1.) * M.y;
}

#define MAX_DISTANCE   50.0
#define MIN_DISTANCE   0.01


mat3 rMatrix;
mat3 ballMatrix;
vec2 uv;

mat3 rotateMat(float roll, float pitch, float yaw)
{
    float cp = cos(pitch);
    float sp = sin(pitch);
    float sr = sin(roll);
    float cr = cos(roll);
    float sy = sin(yaw);
    float cy = cos(yaw);

    return mat3(cp * cy, (sr * sp * cy) - (cr * sy), (cr * sp * cy) + (sr * sy),
                cp * sy, (sr * sp * sy) + (cr * cy), (cr * sp * sy) - (sr * cy),
                -sp, sr * cp, cr * cp);
}

// smooth min from iq
float smoothmin( float a, float b)
{
    float h = max( 1.0-abs(a-b), 0.0 );
    return min( a, b ) - h*h*h*(1.0/6.0);
}

//------------------------------------------------------------
//
//------------------------------------------------------------
float sdPlane(vec3 p)
{    
    // the sound texture is 512x2
    int tx = int(length(p) * 5.0);

    float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;


    float dist;
    if (length(p) < 4.0)
    {
        dist = p.y + (1.0 - fft * 1.0);
    }
   	else
    {
        dist = p.y + (1.0 - fft * -2.0);
    }
    return dist;
}


//------------------------------------------------------------
//
//------------------------------------------------------------
float sdBox( vec3 p, vec3 size)
{
  vec3 d = abs(p) - size;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0);
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

//------------------------------------------------------------
//
//------------------------------------------------------------
float sceneDf(vec3 p)
{
    int tx = int(uv.x*512.0);
    float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;

    float dist = sdPlane(p);

    vec3 sp = ballMatrix * p/2.0;
    float shapeDist = mix(sdBox(sp, vec3(wave)), sdTorus(sp, vec2(1.0 * wave, 0.5 * wave)),
                         4.0 - wave * 4.0);

    dist = smoothmin(dist, shapeDist);
    return dist;
}

//------------------------------------------------------------
//
//------------------------------------------------------------
vec3 calcNormal(vec3 p)
{
    float e = 0.02f;

    vec3 normal;
    normal.x = sceneDf(vec3(p.x + e,p.y,p.z)) - sceneDf(vec3(p.x - e, p.y, p.z));
    normal.y = sceneDf(vec3(p.x,p.y + e,p.z)) - sceneDf(vec3(p.x, p.y - e, p.z));
    normal.z = sceneDf(vec3(p.x,p.y,p.z + e)) - sceneDf(vec3(p.x, p.y, p.z - e));

    return normalize(normal);
}


//------------------------------------------------------------
//
//------------------------------------------------------------
float calcShadow(vec3 pt, vec3 dir)
{
    float shade = 1.0;
	float t = 0.001;
    while(t < 6.0)
    {
        float dist = sceneDf(pt + dir * t);
        t += dist;
        shade = smoothmin(shade, 4.0 * dist/t);
        if (dist <= 0.0001){
            break;
        }
    }
    return clamp(shade, 0.0, 1.0);
}

//------------------------------------------------------------
//
//------------------------------------------------------------
vec3 processLighting(vec3 baseColor, vec3 eyePos, vec3 surfacePoint)
{
    vec3 normal = calcNormal(surfacePoint);
    vec3 diffuseDir = normalize(vec3(sin(iTime),-1.0,cos(iTime)));

    float shaded = calcShadow(surfacePoint, -diffuseDir);

    float diffDot = clamp(dot(-diffuseDir, normal), 0.0, 1.0);
    vec3 diffuse = (diffDot * vec3(0.0,1.0,1.0)) * shaded;

    vec3 fillDir = normalize(-diffuseDir * vec3(-1.0, 0.0, -1.0));
    float fillDot = clamp(dot(fillDir, normal), 0.0, 1.0);
    vec3 fill = (fillDot * vec3(1.0,1.0,0.4));

    vec3 color = vec3(0.1, 0.1, 0.1);
    surfacePoint *= rMatrix;

    vec3 material = vec3(1.0 - 0.5*sin(surfacePoint.x), 1.0 - 0.5*cos(iTime), 1.0 - 0.5*sin(surfacePoint.z));
    vec3 material2 = normal;
    if (length(surfacePoint) > 4.0){
        material = diffuseDir * vec3(sin(iTime)*1.5, 1.2, cos(iTime)*1.5);
    }
    else{
        material = mix(material , material2 , clamp(surfacePoint.y + 1.0, 0.0, 1.0));
    }
    return material * (diffuse + fill);
}

//------------------------------------------------------------
//
//------------------------------------------------------------
vec3 marchRay(vec3 eyePos,vec3 dir, vec2 uv)
{
    vec3 color =  vec3(0.8) + dir;
    float t = 0.0001;
    while(t < MAX_DISTANCE)
    {
        vec3 currentPoint = eyePos + (t * dir);
        float dist = sceneDf(currentPoint);
        if(dist <= MIN_DISTANCE)
        {
            color = processLighting(vec3(0.1, 0.1, 0.1), eyePos, currentPoint);
            break;
        }
        t += dist;
    }
    return color;
}



