void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // create pixel coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;

    rMatrix = rotateMat(0.0, iTime, 0.0);
    ballMatrix = rotateMat(iTime, 0.0, iTime);
     // camera	
	vec3 eye = vec3(0.0,  3.0, 11.0);
    vec3 lookat = vec3( 0.0, -0.2, 0.0 );
    // camera matrix
    vec3 forward = normalize( lookat - eye );
    vec3 right = normalize( cross(forward,vec3(0.0,1.0,0.0) ) );
    vec3 up = normalize( cross(right, forward));

    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    // create view ray
    vec3 dir = normalize( p.x*right + p.y*up + 3.5*forward );

    vec3 color = marchRay(eye, dir, uv);
    color = pow(color, vec3(1.0/2.2));
    fragColor = vec4(color,1.0);
}

const float Pi = 3.1415926535;

vec2 hash22(vec2 st){
    st = vec2(dot(st, vec2(127.1, 311.7)), dot(st, vec2(269.5, 183.3)));
    return fract(sin(st) * 43758.5453123);
}


float hash21(vec2 v) {
    return fract(sin(dot(v, vec2(12.9898, 78.233))) * 43758.5453123);
}

float noise21(vec2 uv) {
	vec2 f = fract(uv);
	vec2 i = floor(uv);
	f = f * f * (3. - 2. * f);
	return mix(
		mix(hash21(i), hash21(i + vec2(1,0)), f.x),
		mix(hash21(i + vec2(0,1)), hash21(i + vec2(1,1)), f.x), f.y);
}

vec2 noisyMove(vec2 id, float time) {
	return vec2(
		noise21(20. * hash22(id) + time),
		noise21(-20. * hash22(id) + time));
}


vec3 voronoi(vec2 x) {
    vec2 ix = floor(x);
    vec2 fx = fract(x);
    float w = .03;

    float mdist = 8.;
    vec2  dir   = vec2(0);
    vec3  col   = vec3(0);

    for(int j = -1; j <= 1; j++) {
        for(int i =- 1; i <= 1; i++) {
            vec2 next = vec2(i, j);
            vec2 pt = noisyMove(ix + next, .3 * iTime);
            vec2  diff = vec2(next) - fx + pt;
        	float weight = dot(pt, pt) * .5 + .5;
            float dist = length(diff) * pow(weight, .3);

    		vec3 bcol = mix(vec3(.3, .8, .1), vec3(.9, .95, .1), hash21(ix + next));

            float h = smoothstep(0., 1., 0.5 + .5*(mdist - dist)/w );
	    	mdist = mix(mdist, dist, h);
			dir   = mix(dir,   diff, h);
           	col   = mix(col,   bcol, h);
        }
	}

    float angle = atan(dir.y, dir.x) / Pi;
    angle = .5 * angle + .5;
    float light = .5 + .5 * sin((angle - .1) * 2. * Pi);

    col = mix(col, vec3(.07, .1, .05), 1.2 * mdist);
    return col + .3 * pow(1. - mdist, 2.) * (2. * light - 1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y;
    vec3 col = voronoi(4. * uv);
    fragColor = vec4(col, 1);
}

