const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;



/**
 * Signed distance function for a sphere centered at the origin with radius 1.0;
 */
float sphereSDF(vec3 samplePoint, float r) {
    return length(samplePoint) - r;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); 
}

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); 
}


float displacement( vec3 p )
{
    //return sin(1.5*p.x)*sin(1.2*p.y)*sin(1.1*p.z);
    return (sin(10.0*p.x)/16.0) * (sin(32.0*p.y+iTime*2.0)*2.0) ;
}

float opDisplace( float v, in vec3 p )
{
    float d1 = v;
    float d2 = displacement(p);
    return d1+d2;
}



/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 samplePoint) {

    float s1 = sphereSDF(samplePoint+vec3(.0,.0,.3), 1.0);
    float s2 = sphereSDF(samplePoint+vec3(.5+(sin(iTime/2.)*1.5), .2+(sin(iTime/2.)*1.), -.5), 0.7);    

    float d = opSmoothUnion( s2, s1, 0.5); 
    //float d = opSmoothSubtraction( s2, s1, 0.1); 

    return d;
    //return opDisplace( d, samplePoint);
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0 * sin(iTime),
                          2.0,
                          4.0 * cos(iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(2.0 * sin(0.37 * iTime),
                          2.0 * cos(0.37 * iTime),
                          2.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);    
    return color;
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, 5.0);
    float dist = shortestDistanceToSurface(eye, dir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * dir;

    vec3 K_a = vec3(0.1, 0.1, 0.3);
    vec3 K_d = vec3(0.1, 0.5, 0.5);
    vec3 K_s = vec3(0.5, 0.2, 0.1);
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    //fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sum  = 0.0, 
          seed = 0.0;
    float time = sin(iTime * .5) * 2.0;

    for (int i = 0; i < 100; i ++)
    {
        seed =  mod(seed * 106.0 + 1283.0, 6075.0);
        sum  += (iResolution.x + iResolution.y) / 600.0 / distance(fragCoord.xy, vec2(
            iResolution.x / 2.0 + mod(seed, iResolution.x) * cos(-time - seed) * time,
            iResolution.y / 2.0 + mod(seed, iResolution.y) * sin(seed  + time) * time
        ));
    }
    fragColor = mix(
        vec4(fragCoord.xy / iResolution.xy, 0.5 + 0.5 * sin(time), 1.0), 
        vec4(sum, 0.0, sum, sum), 0.6
    );
}

const float EPSILON = 0.001f;

struct intersection {
    bool hit;
    vec3 point;
    vec3 normal;
};

mat4 lookAt(vec3 eye, vec3 target, vec3 up) {
	vec3 z = normalize(eye - target);
    vec3 x = normalize(cross(up, z));
    vec3 y = normalize(cross(z, x));
    mat4 translation = mat4(
        vec4(1.0, 0.0, 0.0, eye.x),
    	vec4(0.0, 1.0, 0.0, eye.y),
        vec4(0.0, 0.0, 1.0, eye.z),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
    mat4 rotation = transpose(
        mat4(
            vec4(x, 0.0),
            vec4(y, 0.0),
            vec4(z, 0.0),
            vec4(0.0, 0.0, 0.0, 1.0)
        )
    );
    return translation * rotation;
}

vec3 getRayDir(mat4 camera, float fov, vec2 pixel) {
    vec2 p = 2.0 * ((pixel / iResolution.xy) - vec2(0.5, 0.5));
    vec2 h = vec2(
        tan(fov / 2.0) * (iResolution.x / iResolution.y), 
        tan(fov / 2.0)
    );
    vec3 pCam = vec3(p * h, -1.0);
	return normalize((inverse(camera) * vec4(pCam, 0.0)).xyz);
}

float unionSDF(float d1, float d2) {
    return min(d1,d2);
}

float subtractionSDF(float d1, float d2) {
    return max(-d1,d2);
}

float intersectionSDF(float d1, float d2, float k) {
    return max(d1,d2);
}

float smoothUnionSDF(float d1, float d2, float k) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float smoothSubtractionSDF(float d1, float d2, float k) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h);
}

float smoothIntersectionSDF(float d1, float d2, float k) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}

float sphereSDF(vec3 point) {
    return length(point) - 1.0f;
}

float torusSDF(vec3 point) {
    vec2 t = vec2(1.5f, 0.2f);
	vec2 q = vec2(length(point.xy) - t.x, point.z);
    return length(q) - t.y;
}

float sceneSDF(vec3 point) {
	//return torusSDF(point - vec3(0.0f, 0.0f, 5.0f));
    return smoothUnionSDF(
        sphereSDF(point - vec3(0.0f, 0.0f, 5.0f + cos(iTime))),
        torusSDF(point - vec3(0.0f, 0.0f, 5.0f)),
        1.25f
    );
}

vec3 estimateNormal(vec3 p) {
	return normalize(vec3(
    	sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
    	sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
    	sceneSDF(vec3(p.x, p.y, p.z + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

intersection rayMarch(vec3 origin, vec3 direction, float tMin, float tMax) {
    for (float t = tMin; t < tMax; ) {
        vec3 p = origin + t * direction;
    	float d = sceneSDF(p);
        if (d < EPSILON)
            return intersection(true, p, estimateNormal(p));
        t += abs(d);
    }
    return intersection(false, vec3(0.0f, 0.0f, 0.0f), vec3(0.0f, 0.0f, 0.0f));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 eye = vec3(5.0, 0.0, 0.0);
    vec3 target = vec3(0.0, 0.0, 5.0);
    vec3 up = vec3(0.0, 1.0, 0.0);
    float fov = radians(45.0);

    mat4 camera = lookAt(eye, target, up); 
    vec3 rayDir = getRayDir(camera, fov, fragCoord);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime + uv.xyx + vec3(0,2,4));

    // Output to screen
    intersection itsc = rayMarch(eye, rayDir, 1.0, 100.0);
    float diff = clamp(dot(itsc.normal, normalize(eye - itsc.point)), 0.0, 1.0);
    if (itsc.hit)
    	fragColor = vec4(col * diff, 1.0);
    else
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
}

float noise(float seed)
{
    return fract(sin(19776.15*seed)*1429.14+sin(0.3*iTime));
}

float smootherstep(float start, float end, float u)
{
    float p  = (u-start)/(end-start);
    p =clamp(p, 0., 1.);
    return 0.5*p*(p*(5.-4.*p)+1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.*fragCoord - iResolution.xy) / iResolution.y;
    float r = length(uv);
    float a = atan(uv.y,uv.x);
    vec3 color ;
    //circle
    float f = 1.-smootherstep(.9, 1., r);
    color = mix(color, vec3(1.,.6,0.), f);
    //crown
    f = (1.-smoothstep(1., 1.03, r+0.1*noise(a)))*smoothstep(0.7, 1., r)*step(0.4,noise(a*r));
    color = mix(color, vec3(1.,.4,0.), f);
    //spots 1
    float l =length(uv-0.3);
    f = noise(sin(0.001*l+0.01*a));
	f*= (1.-smoothstep(.9,1.05,length(uv)));
    f = step(0.5,f);
    color = mix(color, vec3(1.,.2,0.), f);
    //spots 2
    l =length(uv+0.4);
    f = noise(sin(0.001*l+0.01*a));
	f*= (1.-smoothstep(0.8,1.3,length(uv)));
    f = step(0.85,f);
    color = mix(color, vec3(1.,.1,0.), f);
    //spots 
    l =length(uv+0.4);
    f = noise(sin(0.021*l+0.01*a));
	f*= (1.-smoothstep(0.,1.2,length(uv-0.3)));
    f = step(0.7,f);
    color = mix(color, vec3(1.,.05,0.), f);

    fragColor = vec4(color,1.);
}

