vec2 clearmiddle;
vec2 zero;

//funkcja przemieszczająca środek koła z którego wycinany jest księżyc i obracająca dookoła tego środka drugie koło
void setmiddle(float time){
    bouncermiddle = zero+vec2(sin(2.0*PI*time)*dist, cos(2.0*PI*time)*dist);
    clearmiddle = bouncermiddle+vec2(sin(2.0*PI*(time*60.0+time))*(gap-ballwidth),cos(2.0*PI*(time*60.0+time))*(gap-ballwidth));
}

//funkcja ustalająca ruch piłeczki
void setball(float time){
    float halfsec = floor(time*120.0);
    float sec = floor(time*60.0);
    float partsec = mod(time*60.0, 1.0);
    float halfasec = mod(time*60.0, 0.5);
    ballpos.x = (zero.x + sin(2.0*PI*sec/60.0)*(ballwidth+balldiff-balldiff*abs(partsec-0.5)*2.0));
    ballpos.y = (zero.y + cos(2.0*PI*sec/60.0)*(ballwidth+balldiff-balldiff*abs(partsec-0.5)*2.0));
}

//funkcja określająca, czy dany piksel jest częścią odbijającego księżyca
bool isBouncer (vec2 pos, float minmod) {
    setmiddle(minmod);
    if (length(pos - bouncermiddle) < r && length(pos - clearmiddle) > r2) { return true; } else { return false; }    
}

//funkcja określająca, czy dany piksel jest częścią piłeczki
bool Ball(vec2 pos, float time){
    setball(time);
    if (length(pos - ballpos) < ballwidth) return true; else  return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    zero = vec2(iResolution.x/2.0, iResolution.y/2.0); //określenie środka ekranu (punkt zero)
    gap = (r2-dist)+width; //wyznacznie odległośi między środkami okręgów
    r = dist+width; //wyznaczenie średnicy okręgu wysującego księżyc
    ballmaxdist = dist - ballwidth; //maksymalna odległość piłeczki od punktu zero
    balldiff = ballmaxdist*2.0 - ballwidth;
    //utworzenie kolorów
    vec3 bgCol = vec3(1.0, 0.0, 0.0);
    vec3 pixel = bgCol;
    vec3 col1 = vec3(0.5, 1.0, 0.75);
    vec3 col2 = vec3(0.0, 0.2, 0.9);
    vec3 col3 = vec3(0.19, 0.44, 0.27);
    if (length(zero - fragCoord) < ballwidth) pixel = col3; //miejscowienie zielonej kulki na środku
    float t = iTime; //pobranie czasu 
    t = mod(t, 60.0)/60.0; //przypisanie zmniennej t cząstki upłyniętej minuty
    if (isBouncer(fragCoord, t)) {pixel = col1;} //miejscowienie odbijającego księżyca
    if (Ball(fragCoord, t)) {pixel = col2;} //miejscowienie piłki
    fragColor = vec4(pixel,1.0); //rysowanie
}

