/* discontinuous pseudorandom uniformly distributed in [-0.5, +0.5]^3 */

#define HASHSCALE4 vec4(.1031, .1030, .0973, .1099)
vec4 hash43(vec3 p)
{
	vec4 p4 = fract(vec4(p.xyzx)  * HASHSCALE4);
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
vec3 random3(vec3 p3)
{
    vec4 v = hash43(p3);
    return (v.xyz - .5) * log(1.0-v.w) * 0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}

float simplex3d_fractal(vec3 m)
{
    float l = 2.0;
    float il = 1.0/l;
    float frequency = 1.0;
    float amplitude = 1.0;
    float result = 0.0f;
    for (int i = 0; i < 9; i++)
    {
    	result += simplex3d(m*frequency)*amplitude;
        frequency *= l;
        amplitude *= il;
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy/iResolution.x;
	vec3 p3 = vec3(p, iTime*0.025);

	float value;

	if (p.x <= 0.6) {
		value = simplex3d(p3*32.0);
	} else {
		value = simplex3d_fractal(p3*8.0+8.0);
	}

	value = 0.5 + 0.5*value;
	value *= smoothstep(0.0, 0.005, abs(0.6-p.x)); // hello, iq :)

	fragColor = vec4(
			vec3(value),
			1.0);
	return;
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float add_Capsule(vec3 p,vec3 a,vec3 b, float r){
    vec3 ab = b-a;
    vec3 ap = p-a;
    float t = dot( ab, ap ) / dot( ab, ab);
    t = clamp (t,0.,1.);
    vec3 c = a +t*ab;
    float d = length(p -c) - r ;
    return d;

}

float GetDist(vec3 p){
	vec4 s = vec4(0, 1, 6, 1);
    float sphereDist = length (p - s.xyz)-s.w;
    float planeDist = p.y;
    float d = min (sphereDist, planeDist);
    return d;
}

float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.;
    for (int i=0; i< MAX_STEPS; i++){
        vec3 p = ro +rd*dO;
        float ds = GetDist(p);
        dO += ds;
        if (dO>MAX_DIST || ds<SURF_DIST) break;
        }
    return dO; 
}

vec3 GetNormal(vec3 p){
	float d = GetDist(p);
    vec2 e = vec2(.01,0);
    //
    vec3 n = d - vec3(
       //p -vec3(.01,0,0) GLSL is that the properties can be combined in any order you want,
       //which makes it easy to cast and mix values and is called swizzle.
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));
    return normalize(n);
}

float GetLight(vec3 p){
    vec3 lightPos = vec3 (0, 5, 6);
    lightPos.xz += vec2(sin(iTime),cos(iTime))*2.;
    vec3 l = normalize (lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n,l),0.,1.);
    float d = RayMarch(p+n*SURF_DIST*2.,l);
    if (d<length(lightPos-p)) dif *= .1;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates to 0 with a resolution of 5 
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	// Set color to black
    vec3 col = vec3(0);
    //
    vec3 ro = vec3(0, 1, 0);
    //
    vec3 rd = normalize(vec3 (uv.x, uv.y, 1));

    float d = RayMarch(ro,rd);
    // set point light long d 
    vec3 p = ro + rd * d;
    // dif is difuse
    float dif = GetLight(p);

    //d/=6.;
    //col = vec3(d);
    col = vec3 (dif);

    // Send output to color named col to screen
    fragColor = vec4(col,1.0);
}

float map(float value, float t)
{
    return value / 1.0 * t;
}
float map2(float value, float fromSource, float toSource, float fromTarget, float toTarget)
{
    return (value - fromSource) / (toSource - fromSource) * (toTarget - fromTarget) + fromTarget;
}
float N21(vec2 p)
{
    p = fract(p * vec2(8356.655414, 645.8778));
    p += dot(p, p + 72.724);
    return fract(p.x * p.y);
}
vec2 N22(vec2 p)
{
    float x = N21(p);
    float y = N21(p + x);
    return vec2(x, y);
}
vec2 GetPos(vec2 id)
{
    vec2 n = N22(id) * iTime;
    return sin(n) * .4;
}
vec3 HeartScene(vec2 uv, vec3 color)
{    
    float beat = abs(sin(iTime * 0.3));
    uv.x *= map2(sin(iTime * 0.6), -1.0, 1.0, 0.7, 1.5);
    uv.y *= map2(cos(iTime * 0.7), -1.0, 1.0, 1.0, 1.1);
    uv.y -= sqrt(abs(uv.x * 0.7)) * 0.5;

    float c = smoothstep(0.3, map(beat, .29), length(uv));
    if(c > 0.0) color += c;

    return color;
}
vec3 DepressionScene(vec2 uv)
{

    vec3 color = vec3(1.0);
    color = vec3(uv.x + sin(iTime)) * vec3(0.2, 0.6, 0.9);
    color *= HeartScene(uv, color);
    color *= mod(gl_FragCoord.x * gl_FragCoord.y, 2.0);
    return color;
}
float GenerateFlake(vec2 uv)
{
    uv *= 4.0;
    uv.y += iTime / 2.;
    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);
    //if(gv.x > 0.49 || gv.y > 0.49) color = vec3(1, 0, 0);


    vec2 p = GetPos(id);
    float d = length(gv - p);
    float flake = smoothstep(0.1, 0.0, d) / (d * 100.);
    return flake;
}
vec3 RotatingGradientBG(vec2 uv, vec3 color)
{
    float cosTime = cos(iTime);
    float sinTime = sin(iTime);
    vec2 rotUV = uv * mat2(cosTime, -sinTime, sinTime, cosTime);
    color *= vec3(rotUV, 0.8);
    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y * 2.;

    vec3 color = vec3(1.0);
    float fade = clamp(5.0 * sin(iTime * 0.4), -1.0, 1.0);

    if(fade >= 0.0)
    {
        color *= RotatingGradientBG(uv, color);
        color *= HeartScene(uv, color);
        color += GenerateFlake(uv);
        color = color * abs(fade);
    }
    if(fade < 0.0)
    {
        color = DepressionScene(uv);
        color = color * abs(fade);
    }
    fragColor = vec4(color, 1.0);
}

