    vec3 lightPosition = getlightDirection1() + vec3(0,0,10);

    vec3 lightDirection1 = normalize(lightPosition);

    vec3 lightDirection2 = -1.0*lightDirection1;

    float lightWeight1 = max(0.0,dot(lightDirection1, normal));

