vec2 getV2form2Line(vec3 line1, vec3 line2){
    vec2 v2;

	//((b1c2-b2c1)/(a1b2-a2b1)，(a2c1-a1c2)/(a1b2-a2b1))

    v2.x = (line1.y*line2.z-line2.y*line1.z)/(line1.x*line2.y-line2.x*line1.y);
    v2.y = (line2.x*line1.z-line1.x*line2.z)/(line1.x*line2.y-line2.x*line1.y);

	return v2;
}

vec3 getLineABCfrom2Point(vec2 p1, vec2 p2){
    vec3 lineABC;
    lineABC.x = p2.y - p1.y; //A

	lineABC.y = p2.x - p1.x; //B

	lineABC.z = p2.x * p1.y - p2.y * p1.x; //C

    return lineABC;

}

vec3 getV3from3v2and2v3(vec3 v3a, vec3 v3b, vec2 v2p, vec2 v2a, vec2 v2b){
    vec3 v3;

    float ap = distance(v2a,v2p);
    float pb = distance(v2b,v2p);
    v3 = v3a + (v3b-v3a)*ap/(ap+pb);
	return v3;
}

float getPointZonTriangle(vec2 v2a, vec2 v2b, vec2 v2c, vec3 v3a, vec3 v3b, vec3 v3c, vec2 p){
	float z;

    vec3 lineab = getLineABCfrom2Point(v2a, v2b);
    vec3 linecp = getLineABCfrom2Point(v2c, p);

    vec2 v2ab = getV2form2Line(lineab, linecp);

    vec3 v3ab = getV3from3v2and2v3(v3a, v3b, v2ab, v2a, v2b);

    vec3 v3p = getV3from3v2and2v3(v3c, v3ab, p, v2c, v2ab);

    return v3p.z;
}

bool isTriangleV2clockwise(vec2 p1, vec2 p2, vec2 p3){
    bool isTriangleV2clockwise = false;

    if ((p2.x - p1.x) *(p3.y - p1.y) -(p3.x - p1.x) *(p2.y-p1.y) < 0.0){  
		isTriangleV2clockwise = true;
    } 

    return isTriangleV2clockwise;

}

