const float zNear = 1.0;
const float zFar  = 400.0;


float map(in vec3 m) {

	vec3 sphereCenter = vec3(0.0, 0.0, 4.5);
    float radius = 4.0;

    float displacementSize = 0.8 + cos(iTime*0.25)* 0.4;
    float displacement 		= sin(displacementSize*m.x)*sin(displacementSize*m.y)*sin(displacementSize*m.z) * 0.05;

    return length(m - sphereCenter) - radius + displacement; 
}



vec3 computeNormalMap(in vec2 UV)
{
    float scale = 0.05;
    vec2 eps = vec2(0.005, 0.0);

    float hx1 = texture(iChannel0, UV + eps.xy).x;
    float hx2 = texture(iChannel0, UV - eps.xy).x;    
    float hz1 = texture(iChannel0, UV + eps.yx).x;
    float hz2 = texture(iChannel0, UV - eps.yx).x;    

    vec3 i = vec3(eps.x, 0.0, (hx2-hx1)*scale);
    vec3 j = vec3(0.0, eps.x, (hz2-hz1)*scale);    

    return normalize(cross(i, j));
}



bool rayMarching(in vec3 origin, in vec3 ray, out vec3 m) {

    float	marchingDist = 0.0;
    float 	nbIter 		 = 0.0;

    for(int i = 0; i<50; i++) {

        m = origin + ray * marchingDist;    

    	float dist = map(m);

        if(dist < 0.0001) {
            return true;
        }
        else {
            marchingDist += dist;

            if(marchingDist >= zFar) {
                break;
            }
        }
    }

	return false;    
}


vec4 computeColor(in vec3 ray, in vec3 m, in vec3 normal, in vec3 light, in vec3 eye) {

    vec2 UV = ((normal.xy + 1.0) * 0.5) * (20.0 + cos(iTime*0.01)*5.0);

    vec3 pixel = texture(iChannel0, UV).xyz;

    vec3 normalMap = computeNormalMap(UV);

    vec3 k = normal;
    vec3 i = normalize(vec3(-k.z, 0.0, k.x));
    vec3 j = cross(k, i);

    mat3 local = mat3(i, j, k);

	normal = normalMap * local;    


    vec3 lightRay = normalize(m - light);
    float diffuse = max(0.0, dot(normal, -lightRay));

    vec3 reflectedLight  = reflect(lightRay, normal);
    float hilight 		 = pow(max(dot(reflectedLight, -ray),0.0), 35.0);

    float ambiant = 0.2;       

    vec3 render = pixel * (diffuse + ambiant) + hilight;
    return vec4(render, 1.0);
}


// IQ's func <3
vec3 computeNormal(in vec3 pos)
{
	vec3 eps = vec3( 0.5, 0.0, 0.0 );
	vec3 nor = vec3(
	     map(pos+eps.xyy) - map(pos-eps.xyy),
	     map(pos+eps.yxy) - map(pos-eps.yxy),
	     map(pos+eps.yyx) - map(pos-eps.yyx));
	return normalize(nor);
}



vec4 run(in vec2 fragCoord )
{
    float 	ratio 			= iResolution.x/iResolution.y;
    vec3 	viewportCoord 	= vec3(fragCoord.xy/iResolution.y - vec2(ratio/2.0, 0.5), zNear);

    vec3	eye				= vec3(0.0, 0.0, 0.0);
    vec3	ray				= normalize(viewportCoord-eye);
    vec3	light			= vec3(5.0*cos(iTime*0.5), 3.0, -10.0);

    vec3	m;

    if(rayMarching(eye, ray, m)) {
        vec3 normal = computeNormal(m);
        return computeColor(ray, m, normal, light, eye);
    }
    else {
        return vec4(0.0, 0, 0, 1.0);        
    }

}

#define SUPERSAMPLING

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);

    #ifdef SUPERSAMPLING
    // 5x5 supersampling
    for(float dx=-0.5; dx<=0.5; dx+=0.25) {
    	for(float dy=-0.5; dy<=0.5; dy+=0.25) {
            fragColor += run(fragCoord + vec2(dx, dy));
        }
    }

    fragColor /= 25.0;

    #else
    fragColor = run(fragCoord);
    #endif
}

#define MAX_STEPS 32
