const float _UpperCloudExponent = 2.0;
const float _Radius = 1.0;
const float _Oblateness = 1.06;
const float _TimeScale = 0.02;
const float _StormSpeed = 0.1;
const float  _StormSize = 0.34;
const float _StormDepth = 0.98;
const float _CycloneStability = -0.31;

//Signed distance function for a sphere
float sdSphere(vec3 position)
{
    position.y *= _Oblateness;
    float d = length(_Center - position) - _Radius;
    return d;
}

//Raymarching loop
vec3 raymarch(vec3 position, vec3 direction)
{
    float dist;
    vec3 center = _Center;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        dist = sdSphere(position);
        if (dist < MIN_DIST)
        {
            return position;
        }

        position += direction * dist;
    }

    return vec3(0.0, 0.0, 0.0);
}

//Correct geodetic normal for a non-spherical ellipsoids
vec3 geodeticNormal(vec3 p)
{
    vec3 axes = vec3(1.0, _Oblateness*_Oblateness, 1.0);
    vec3 R = p - _Center;
    return normalize(R/axes);
}

//Y-axis rotation matrix
vec3 rotateY(vec3 p, float s)
{
    float cosTheta = cos(iTime * s);
    float sinTheta = sin(iTime * s);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    return rot * p;
}

