float mod289(float x) {
	return x - floor(x * NOISE_SIMPLEX_1_DIV_289) * 289.0;
}

vec2 mod289(vec2 x) {
	return x - floor(x * NOISE_SIMPLEX_1_DIV_289) * 289.0;
}

vec3 mod289(vec3 x) {
	return x - floor(x * NOISE_SIMPLEX_1_DIV_289) * 289.0;
}

vec4 mod289(vec4 x) {
	return x - floor(x * NOISE_SIMPLEX_1_DIV_289) * 289.0;
}

float permute(float x) {
	return mod289(
		x*x*34.0 + x
	);
}

vec3 permute(vec3 x) {
	return mod289(
		x*x*34.0 + x
	);
}

vec4 permute(vec4 x) {
	return mod289(
		x*x*34.0 + x
	);
}

float taylorInvSqrt(float r) {
	return 1.79284291400159 - 0.85373472095314 * r;
}

vec4 taylorInvSqrt(vec4 r) {
	return 1.79284291400159 - 0.85373472095314 * r;
}

vec4 grad4(float j, vec4 ip)
{
	const vec4 ones = vec4(1.0, 1.0, 1.0, -1.0);
	vec4 p, s;
	p.xyz = floor( fract(j * ip.xyz) * 7.0) * ip.z - 1.0;
	p.w = 1.5 - dot( abs(p.xyz), ones.xyz );

	// GLSL: lessThan(x, y) = x < y
	// HLSL: 1 - step(y, x) = x < y
	s = vec4(
		1.0 - step(0.0, p)
	);
	p.xyz = p.xyz + (s.xyz * 2.0 - 1.0) * s.www; 

	return p;
}

//3D
float snoise(vec3 v)
{
	const vec2 C = vec2(
		0.166666666666666667, // 1/6
		0.333333333333333333  // 1/3
	);
	const vec4 D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
	vec3 i = floor( v + dot(v, C.yyy) );
	vec3 x0 = v - i + dot(i, C.xxx);

// Other corners
	vec3 g = step(x0.yzx, x0.xyz);
	vec3 l = 1.0 - g;
	vec3 i1 = min(g.xyz, l.zxy);
	vec3 i2 = max(g.xyz, l.zxy);

	vec3 x1 = x0 - i1 + C.xxx;
	vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
	vec3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y

// Permutations
	i = mod289(i);
	vec4 p = permute(
		permute(
			permute(
					i.z + vec4(0.0, i1.z, i2.z, 1.0 )
			) + i.y + vec4(0.0, i1.y, i2.y, 1.0 )
		) 	+ i.x + vec4(0.0, i1.x, i2.x, 1.0 )
	);

// Gradients: 7x7 points over a square, mapped onto an octahedron.
// The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
	float n_ = 0.142857142857; // 1/7
	vec3 ns = n_ * D.wyz - D.xzx;

	vec4 j = p - 49.0 * floor(p * ns.z * ns.z); // mod(p,7*7)

	vec4 x_ = floor(j * ns.z);
	vec4 y_ = floor(j - 7.0 * x_ ); // mod(j,N)

	vec4 x = x_ *ns.x + ns.yyyy;
	vec4 y = y_ *ns.x + ns.yyyy;
	vec4 h = 1.0 - abs(x) - abs(y);

	vec4 b0 = vec4( x.xy, y.xy );
	vec4 b1 = vec4( x.zw, y.zw );

	//vec4 s0 = vec4(lessThan(b0,0.0))*2.0 - 1.0;
	//vec4 s1 = vec4(lessThan(b1,0.0))*2.0 - 1.0;
	vec4 s0 = floor(b0)*2.0 + 1.0;
	vec4 s1 = floor(b1)*2.0 + 1.0;
	vec4 sh = -step(h, vec4(0.0));

	vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
	vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

	vec3 p0 = vec3(a0.xy,h.x);
	vec3 p1 = vec3(a0.zw,h.y);
	vec3 p2 = vec3(a1.xy,h.z);
	vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
	vec4 norm = taylorInvSqrt(vec4(
		dot(p0, p0),
		dot(p1, p1),
		dot(p2, p2),
		dot(p3, p3)
	));
	p0 *= norm.x;
	p1 *= norm.y;
	p2 *= norm.z;
	p3 *= norm.w;

// Mix final noise value
	vec4 m = max(
		0.6 - vec4(
			dot(x0, x0),
			dot(x1, x1),
			dot(x2, x2),
			dot(x3, x3)
		),
		0.0
	);
	m = m * m;
	return 42.0 * dot(
		m*m,
		vec4(
			dot(p0, x0),
			dot(p1, x1),
			dot(p2, x2),
			dot(p3, x3)
		)
	);
}

//4D
float snoise(vec4 v)
{
	const vec4 C = vec4(
		0.138196601125011, // (5 - sqrt(5))/20 G4
		0.276393202250021, // 2 * G4
		0.414589803375032, // 3 * G4
	 -0.447213595499958  // -1 + 4 * G4
	);

// First corner
	vec4 i = floor(
		v +
		dot(
			v,
			vec4(0.309016994374947451) // (sqrt(5) - 1) / 4
		)
	);
	vec4 x0 = v - i + dot(i, C.xxxx);

// Other corners

// Rank sorting originally contributed by Bill Licea-Kane, AMD (formerly ATI)
	vec4 i0;
	vec3 isX = step( x0.yzw, x0.xxx );
	vec3 isYZ = step( x0.zww, x0.yyz );
	i0.x = isX.x + isX.y + isX.z;
	i0.yzw = 1.0 - isX;
	i0.y += isYZ.x + isYZ.y;
	i0.zw += 1.0 - isYZ.xy;
	i0.z += isYZ.z;
	i0.w += 1.0 - isYZ.z;

	// i0 now contains the unique values 0,1,2,3 in each channel
	vec4 i3 = clamp(i0, 0.0, 1.0);
	vec4 i2 = clamp(i0-1.0, 0.0, 1.0);
	vec4 i1 = clamp(i0-2.0, 0.0, 1.0);

	//	x0 = x0 - 0.0 + 0.0 * C.xxxx
	//	x1 = x0 - i1  + 1.0 * C.xxxx
	//	x2 = x0 - i2  + 2.0 * C.xxxx
	//	x3 = x0 - i3  + 3.0 * C.xxxx
	//	x4 = x0 - 1.0 + 4.0 * C.xxxx
	vec4 x1 = x0 - i1 + C.xxxx;
	vec4 x2 = x0 - i2 + C.yyyy;
	vec4 x3 = x0 - i3 + C.zzzz;
	vec4 x4 = x0 + C.wwww;

// Permutations
	i = mod289(i); 
	float j0 = permute(
		permute(
			permute(
				permute(i.w) + i.z
			) + i.y
		) + i.x
	);
	vec4 j1 = permute(
		permute(
			permute(
				permute (
					i.w + vec4(i1.w, i2.w, i3.w, 1.0 )
				) + i.z + vec4(i1.z, i2.z, i3.z, 1.0 )
			) + i.y + vec4(i1.y, i2.y, i3.y, 1.0 )
		) + i.x + vec4(i1.x, i2.x, i3.x, 1.0 )
	);

// Gradients: 7x7x6 points over a cube, mapped onto a 4-cross polytope
// 7*7*6 = 294, which is close to the ring size 17*17 = 289.
	const vec4 ip = vec4(
		0.003401360544217687075, // 1/294
		0.020408163265306122449, // 1/49
		0.142857142857142857143, // 1/7
		0.0
	);

	vec4 p0 = grad4(j0, ip);
	vec4 p1 = grad4(j1.x, ip);
	vec4 p2 = grad4(j1.y, ip);
	vec4 p3 = grad4(j1.z, ip);
	vec4 p4 = grad4(j1.w, ip);

// Normalise gradients
	vec4 norm = taylorInvSqrt(vec4(
		dot(p0, p0),
		dot(p1, p1),
		dot(p2, p2),
		dot(p3, p3)
	));
	p0 *= norm.x;
	p1 *= norm.y;
	p2 *= norm.z;
	p3 *= norm.w;
	p4 *= taylorInvSqrt( dot(p4, p4) );

// Mix contributions from the five corners
	vec3 m0 = max(
		0.6 - vec3(
			dot(x0, x0),
			dot(x1, x1),
			dot(x2, x2)
		),
		0.0
	);
	vec2 m1 = max(
		0.6 - vec2(
			dot(x3, x3),
			dot(x4, x4)
		),
		0.0
	);
	m0 = m0 * m0;
	m1 = m1 * m1;

	return 49.0 * (
		dot(
			m0*m0,
			vec3(
				dot(p0, x0),
				dot(p1, x1),
				dot(p2, x2)
			)
		) + dot(
			m1*m1,
			vec2(
				dot(p3, x3),
				dot(p4, x4)
			)
		)
	);
}

float ridgedNoise(vec4 position, float frequency, float amplitude)
{
    return((1.0 - abs(snoise(position * frequency))) * 2.0 - 1.0) * amplitude;
}

vec4 renderSurface(vec3 normal, vec3 sp, vec3 camPos, float h)
{
    //Mouse inputs
    float lerp = clamp(iMouse.x/iResolution.x, 0.0, 1.0);
    float temp = clamp(-0.5 + iMouse.y/iResolution.y, 0.0, 0.15);
    vec3 _Color = mix(vec3(0.1,0.1,0.8), vec3(0.8,0.5,0.3), lerp);
    vec3 _Emissive = vec3(temp, 0.0, 0.0);

    float dist = length(_Center - camPos);

    vec4 seed;
    float fade = min(0.5, max(1.0 - dist / 24.0, 0.0));

    vec3 worldNormal = normal;

    //Surface Distortion
    //Calculate uv offsets
    seed.xyz = worldNormal;
    seed.x += _WindSpeed1 * iTime * _TimeScale;
    seed.w = iTime * _TimeScale * 0.000001;
    float n1 = ridgedNoise(seed, 12.0, 1.0);
    float n2 = snoise(seed * 24.0) + n1;
    float n3 = snoise(seed * 64.0) + n2;
    float n4 = min(0.25,ridgedNoise(seed, 16.0, n3) - n3);
    float offset = mix(n1 + n2, n4, fade);

    //Calculate second layer of noise to simulate contra-rotation of cloud bands/zones
    seed *= -0.5;
    seed.x += _WindSpeed2 * iTime * _TimeScale;
    seed.w *= 10000.0;
    float n12 = ridgedNoise(seed, 12.0, 1.0);
    float n22 = snoise(seed * 24.0);
    float n32 = snoise(seed * 64.0);
    float n42 = ridgedNoise(seed, 32.0, n32);
    offset += mix(n12 + n22, n42, fade) * 0.9;

    //Modulate noise bands by cos of lattitude
    float factor = cos(h * 4.0) * 0.7;
    offset *= 1.0 - factor;

    //Calculate Cyclone Locations
    float s = _StormSize;
    vec3 n = vec3(worldNormal.x, worldNormal.y * 1.5, worldNormal.z);
    n.x += iTime * _TimeScale * _StormSpeed;
    float t1 = snoise(n * 2.0) - s;
    float t2 = snoise((n + 400.0) * 2.0) - s;
    float t3 = snoise((n + 800.0) * 2.0) - s;
    float threshold = max(t1 * t2 * t3, 0.0) * _StormDepth;

    //Cyclone offsets
    seed.xyz = worldNormal;
    float cycloneOffset = (snoise(seed.xyz * threshold * 0.2 + t1 + (iTime * _TimeScale * 0.2)) + snoise(seed.xyz * 0.2 + t2) + n2*_CycloneStability + snoise(seed.xyz * 0.001) + snoise(seed * 0.0001) * t3) * threshold * threshold * 175.0;
    cycloneOffset *= sin(abs(h)/_Radius);
    offset += cycloneOffset;

    //Calculate lookup coordinate
    float u =  h * _BandScale + offset/150.0;

    //Lighting Model

    //Sample texture
    vec3 texCol = texture(iChannel0, vec2(u,0.0)).r * _Color;

    worldNormal = rotateY(worldNormal, -0.02 * _TimeScale);

    //Upper Cloud Layer
    float cloudScale = 10.0;
    vec3 skewedSeed = vec3(worldNormal.x, worldNormal.y * 8.0 + iTime * _TimeScale * _WindSpeed1, worldNormal.z);
    float uCloud = clamp(max(snoise(normal*2.0) + snoise(skewedSeed * cloudScale) + snoise(skewedSeed * 8.0) + snoise(skewedSeed*32.0) + n4, 0.0) * _UpperCloudStrength, 0.0, 1.0);
    texCol.rgb = mix(texCol.rgb, _UpperCloudColor, pow(uCloud, _UpperCloudExponent));

    //seed *= 8;
    //seed.y *= 2;
    //float spots = pow(abs(snoise(seed)), 4);
    //texCol.rgb = lerp(texCol.rgb, _UpperCloudColor, saturate(spots));

    //Calculate screen space normal from partial derivatives of color values
    float value = dot(texCol.rgb, texCol.rgb);
    float snx = dFdx(value);
    float sny = dFdy(value);
    float snz = sqrt(1.0 - snx * snx - sny * sny);
    vec3 ssn = -vec3(snx, sny, snz);

    vec3 N = mix(ssn, normal, clamp(dist/(2.5*_Radius), 0.5, 1.0));
    //float3 N = normalize(ssn + normal*((dist)/(_Radius)));

    //Calculate lambertian reflection
    //float NdotL = max(dot(N, lightDir), 0);
    vec3 _LightDir = -normalize(sp - _LightPos);
    float NdotL = clamp(dot(N, _LightDir), 0.0, 1.0);

    vec4 col;
    col.rgb = texCol * _LightCol * NdotL + _Emissive;

    //float w = 0.1;
    //float3 wrappedDiffuse = lightCol * saturate((NdotL + w) / ((1 + w) * (1 + w)));

    //Fresnel-based atmosphere effect. Not physically accurate but looks good from most distances and relatively cheap.
    float fresnel = max(0.0, min(1.0, _FresnelBias + ((40.0) * pow(1.0 + dot(normalize(sp - camPos), normal), _FresnelPow))));
    col.r *= 1. - fresnel * 1.44;
    col.g *= 1. - fresnel * 0.9;
    col.b *= 1. - fresnel * 0.7;
    col.rgb += (fresnel + _Emissive) * NdotL;
    col.rgb += _Emissive * fresnel;
    col.a = max(0.0,1.0 - fresnel);

    //if (col.a <= 0.0)
    //{
    //    col.rgb = vec3(0.0);
    //}

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 rayOrigin = vec3(0.0, 0.0, -5.0);

    //Compute eye vector from field of view
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan((_FoV/2.0) * _Deg2Rad);    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));

    #ifdef ROTATE
    rayOrigin = rotateY(rayOrigin, 0.1);
    rayDir = rotateY(rayDir, 0.1);
    #endif

	vec3 sp = raymarch(rayOrigin, rayDir);

    vec3 col;

    if (sp != vec3(0.0))
    {
        //fragColor = vec4(0.0,0.0,0.0,1.0);
        vec3 normal = geodeticNormal(sp);

        vec4 color = renderSurface(normal, sp, rayOrigin, sp.y);

        col.rgb = color.rgb * color.a * color.a;
    }
    else
    {
        col.rgb += smoothstep(0.85, 0.95, abs(snoise(rotateY(rayDir, 0.1) * 128.0)));
    }

    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-iResolution.xy + 2.0 * fragCoord)/iResolution.y;

    float r = length(uv);

    float t = 25.0;

    float time = iTime;

    float a[3];

    float c = 0.0;

    for (int i = 0; i < 3; i++)
    {

    float s = r;

    float tuv = uv.x;
    uv.x = uv.x - uv.y;
    uv.y = uv.y +tuv;

    s *= (sin(abs(uv.x) * t * 0.2));

    s *= (sin(abs(uv.y) * t * 0.2));

    float m = mod(s + (time + c) * 0.2, 0.5);

    //m *= mod(r - iTime * 0.2, 0.5);

    a[i] = m;    

    c += sin(iTime);

    }

    vec3 col = vec3(a[0], a[1], a[2]);

    fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 128
#define STEP_SIZE 0.05
#define _FoV 45.0
#define _Quality 5

#define JITTER

#define ROTATE

const vec3 _Absorption = vec3(0.7,0.6,0.4);
const vec3 _Ambient = vec3(0.4,0.15,0.2);
const vec3 _Light = vec3(1.0,1.0,1.0);
const vec3 _LightPos = vec3(0.0, 0.0, 0.0);
const float _LightIntensity = 2.0;
const float _Density = 10.0;
const float _Radius = 1.0;
const float _Mie = 0.6;
const float _G = 0.7;

//Improved Heyney-Greenstein phase function
float cornetteShanksPhase(float mu, float g)
{
	float g2 = g * g;
	return (3.0 * (1.0 - g2) * (1.0 + mu * mu)) / (2.0 * (2.0 + g2) * pow(1.0 + g2 - 2.0 * g * mu, 1.5));
}

//Ray-sphere intersection
bool raycastSphere(vec3 ro, vec3 rd, out vec3 p0, out vec3 p1, vec3 center, float r)
{
    float A = 1.0; //dot(rd, rd);
    float B = 2.0 * (rd.x * (ro.x - center.x) + rd.y * (ro.y - center.y) + rd.z * (ro.z - center.z));
    float C = dot(ro - center, ro - center) - (r * r);

    float D = B * B - 4.0 * A * C;
    if (D < 0.0)
    {
        return false;
    }
    else
    {
        float t0 = (-B - D)/(2.0 * A);
        float t1 = (-B + D)/(2.0 * A);
        p0 = ro + rd * t0;
        p1 = ro + rd * t1;
        return true;
    }
}

//Y-axis rotation matrix
vec3 rotateY(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    return rot * p;
}

//iq's gradient noise
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

//Otavio Good's noise from https://www.shadertoy.com/view/ld2SzK
const float nudge = 0.739513;	// size of perpendicular vector
float normalizer = 1.0 / sqrt(1.0 + nudge*nudge);	// pythagorean theorem on that perpendicular to maintain scale
float SpiralNoiseC(vec3 p)
{
    float n = 0.0;	// noise amount
    float iter = 1.0;
    for (int i = 0; i < 8; i++)
    {
        // add sin and cos scaled inverse with the frequency
        n += -abs(sin(p.y*iter) + cos(p.x*iter)) / iter;	// abs for a ridged look
        // rotate by adding perpendicular and scaling down
        p.xy += vec2(p.y, -p.x) * nudge;
        p.xy *= normalizer;
        // rotate on other axis
        p.xz += vec2(p.z, -p.x) * nudge;
        p.xz *= normalizer;
        // increase the frequency
        iter *= 1.733733;
    }
    return n;
}

float fbm(vec3 seed, int octaves, float freq, float lac)
{
    float val;
    float j = 1.0;
    for (int i = 0; i < octaves; i++, j+=1.0)
    {
        val += noise(seed * freq * j) / pow(j, lac);
    }

    return val;
}

float sampleVolume(vec3 pos)
{
    float rr = dot(pos,pos);
    rr = sqrt(rr);
    float f = exp(-rr);
    float p = f * _Density;

    if (p <= 0.0)
        return p;

    p += SpiralNoiseC(128.0 + pos * 4.0) * 0.75;
    pos = rotateY(pos, pos.y * SpiralNoiseC(pos * 2.0)* 2.0);
    p += SpiralNoiseC(100.0 + pos * 6.0) * 1.5;

    p = max(0.0,p);

    return p;
}

vec4 raymarch(vec3 pos, vec3 dir, float ds, int s)
{
    vec4 result = vec4(0.,0.0,0.0,1.0);
    int steps = min(s, MAX_STEPS);
    for (int i = 0; i < steps; i++)
    {
        float p = sampleVolume(pos);
        if (p > 0.0)
        {
            vec3 r = _LightPos - pos;
            float atten = _LightIntensity/dot(r, r);
            vec3 ext = max(vec3(0.000001), (_Absorption * p) + vec3(_Mie * p));
            vec3 trans = exp(-ext * ds);
            vec3 lightDir = normalize(r);
            float mu = dot(lightDir, -dir);
            float phase = cornetteShanksPhase(mu, _G);
            vec3 lum = _Ambient + _Light * phase * (1.0-_Absorption) * _Mie * p * atten;
            vec3 integral = (lum - (lum*trans))/ext;

            result.rgb += integral * result.a;
            vec3 div = vec3(0.3333333);
            result.a *= dot(trans, div);

            if (result.a <= 0.1)
                return result;
        }

        pos += dir * ds;
    }

    result = clamp(result, 0.0, 1.0);

    return result;
}

//4x4 Bayer matrix for ordered dithering
const mat4 _Bayer4x4 = mat4(vec4(0,0.5,0.125,0.625),
                        vec4(0.75,0.25,0.875,.375), 
                        vec4(0.1875,0.6875,0.0625,0.5625), 
                        vec4(0.9375,0.4375,0.8125,0.3125));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = iMouse.y/iResolution.y;
    vec3 rayOrigin = vec3(0.0, 0.0, -5.0 + (5.0 * zoom));
    //Compute eye vector from field of view
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan(radians(_FoV/2.0));    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));

    #ifdef ROTATE
    rayDir = rotateY(rayDir, iTime * 0.1);
    rayOrigin = rotateY(rayOrigin, iTime * 0.1);
    #endif

    vec4 col = vec4(0.0,0.0,0.0,1.0);
    col.rgb += smoothstep(0.5, 0.8, abs(noise(rayDir * 256.0)));

    vec3 p0, p1;
    if (raycastSphere(rayOrigin, rayDir, p0, p1, vec3(0.0), _Radius))
    {        
        #ifdef JITTER
        //Bayer matrix ordered depth jittering
        float width = (uv.x * iResolution.x);
        float height = (uv.y * iResolution.y);
        width = mod(width, 4.0);
        height = mod(height, 4.0);
        float offset = _Bayer4x4[int(width)][int(height)];
        p0 -= rayDir * offset * STEP_SIZE*2.0;
        #endif

        float dist = length(p1 - p0);
        int s = int(dist/STEP_SIZE) + 1;

        vec4 integral = raymarch(p0, rayDir, STEP_SIZE, s);

        col.rgb = mix(integral.rgb, col.rgb, integral.a);
    }	

    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float u = uv.x;
    float v = uv.y;

    float r = (abs(u-0.5)*2.0);
    if( u > 0.5 )
        r = r*r*r*r*r*r;

    float g = (abs(u-0.5)*2.0);
    g = g*g;
    g = 1.0-g;
    g = sqrt(g)/2.0;

    float b = u;
    b = b*b;

    float a = abs(u-0.5)*2.0;
    a = a*a*a;
    a = 1.0-a;

    r += g/2.0;
    b += g/2.0;

    r *= sin(iTime/2.0)/2.0+0.5;
    g *= sin(iTime/3.0)/2.0+0.5;
    b *= sin(iTime/5.0)/2.0+0.5;

	fragColor = vec4(r,g,b,1.0)*a;

    if( v > fragColor.x/2.0+0.5 )
        fragColor.x = 0.0;

    if( v > fragColor.y/2.0+0.5 )
        fragColor.y = 0.0;

    if( v > fragColor.z/2.0+0.5 )
        fragColor.z = 0.0;

    if( v > 0.48 && v < 0.5 )
        fragColor = vec4(0.0);
}

#define MAX_STEPS 128
#define STEP_SIZE 0.04
#define _FoV 55.0
#define BACKGROUND

#define JITTER

#define ROTATE

const vec3 _Emission = vec3(0.35,0.5,1.0);
const vec3 _LightColor = vec3(0.4,0.7,1.0);
const vec3 _LightPos = vec3(0.1, 0.05, 0.05);
const float _LightIntensity = 2.5;
const float _Falloff = 0.01;
const float _Density = 100.0;
const float _DensityExp = 14.0;
const float _Radius = 1.0;

//Ray-sphere intersection
bool raycastSphere(vec3 ro, vec3 rd, out vec3 p0, out vec3 p1, vec3 center, float r)
{
    float A = 1.0; //dot(rd, rd);
    float B = 2.0 * (rd.x * (ro.x - center.x) + rd.y * (ro.y - center.y) + rd.z * (ro.z - center.z));
    float C = dot(ro - center, ro - center) - (r * r);

    float D = B * B - 4.0 * A * C;
    if (D < 0.0)
    {
        return false;
    }
    else
    {
        float t0 = (-B - D)/(2.0 * A);
        float t1 = (-B + D)/(2.0 * A);
        p0 = ro + rd * t0;
        p1 = ro + rd * t1;
        return true;
    }
}

vec3 rotateY(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    return rot * p;
}

vec3 rotateX(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(1.0, 0.0, 0.0,
        			0.0, cosTheta, -sinTheta,
    			    0.0, sinTheta, cosTheta);

    return rot * p;
}

//iq's lut based value noise
float noise(vec3 x)
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+0.5)/256.0, 0.0).yx;
    float result = mix( rg.x, rg.y, f.z );
    result = (2.0 * result) - 1.0;
	return result;
}

//Brownian pink noise: S(f) ∝ 1/f^α, {0 <= α <=2}
float fbm(vec3 seed, int octaves, float freq, float lac)
{
    float val;
    float j = 1.0;
    for (int i = 0; i < octaves; i++, j+=1.0)
    {
        val += noise(seed * freq * j) / pow(j, lac);
    }

    return val;
}

float sdSphere(vec3 pos, vec3 center, float radius)
{
    return length(center-pos) - radius;
}

//Density distribution function
float sampleVolume(vec3 pos)
{
    float d = sdSphere(pos, vec3(0.0), _Radius * 0.85);
    float p = 0.75-abs(d + fbm(100.0+pos, 6, 1.0, 1.0) * 0.075);
    vec3 n = normalize(-pos);
    p += fbm(200.0+n, 6, 1.5, 1.0)*0.045;
    p = max(0.0,p);
    p = pow(p, _DensityExp);
    p *= _Density;

    if (p <= 0.0 || d > 0.6 )
        return 0.0;

    p -= smoothstep(0.0,0.5,abs(fbm(400.0+pos, 4, 2.0, 0.5)) * 0.4);

    p = max(0.0,p);

    return p;
}

//Raymarching loop
vec4 raymarch(vec3 pos, vec3 dir, float ds, int s)
{
    vec4 result = vec4(0.,0.0,0.0,1.0);
    int steps = min(s, MAX_STEPS);
    for (int i = 0; i < steps; i++)
    {
        float p = sampleVolume(pos);
        if (p >0.0)
        {
            result.rgb += _Emission * p * ds;
            result.a *= exp(-p * ds);
        }
        vec3 R = _LightPos - pos;
        float rr = dot(R, R) + 0.00001;
        float star = (_Falloff/rr) * ds;
        result.rgb += star * _LightIntensity * _LightColor;
        result.a *= exp(-star * ds);

        pos += dir * ds;
    }

    //result = clamp(result, 0.0, 1.0);

    return result;
}

//4x4 Bayer matrix for ordered dithering
const mat4 _Bayer4x4 = mat4(vec4(0,0.5,0.125,0.625),
                        vec4(0.75,0.25,0.875,.375), 
                        vec4(0.1875,0.6875,0.0625,0.5625), 
                        vec4(0.9375,0.4375,0.8125,0.3125));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = iMouse.y/iResolution.y;
    vec3 rayOrigin = vec3(0.0, 0.0, -5.0 + (5.0 * zoom));
    //Compute eye vector from field of view
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan(radians(_FoV/2.0));    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));

    #ifdef ROTATE
    float t = iMouse.x * 0.01 + iTime * 0.2;
	#else
    float t = iMouse.x * 0.01;
    #endif

    rayDir = rotateY(rayDir, t);
    rayOrigin = rotateY(rayOrigin, t);

    vec4 col = vec4(0.0,0.0,0.0,1.0);

    //Background
    float starfield = smoothstep(0.9, 1.0, abs(noise(rayDir * 140.0)));
    col.rgb += starfield * mix(vec3(1.0,0.5,0.3),vec3(0.7,0.8,1.0), starfield);

    #ifdef BACKGROUND
    float clouds = fbm(rayDir, 6, 4.0, 1.0);
    float gas = (clouds + 1.0) * 0.5;
    if (gas >= 0.0)
    {
        float turb = abs(fbm(128.0+rayDir, 8, 16.0, 1.0)) * 0.15;
        gas -= turb;
        float colors = abs(fbm(256.0 +rayDir, 8, 4.0, 2.0));
        gas *= smoothstep(-0.8,0.2,clouds);
        gas *= 1.5 - colors;
        gas = gas * gas * 0.5;
        col.rgb += gas * mix(vec3(1.0,0.4,0.0),vec3(0.0, 0.8, 1.0), gas * colors * 2.5) * 0.5;
    }
    #endif

    vec3 p0, p1;
    if (raycastSphere(rayOrigin, rayDir, p0, p1, vec3(0.0), _Radius))
    {        
        #ifdef JITTER
        //Bayer matrix ordered depth jittering
        float width = (uv.x * iResolution.x);
        float height = (uv.y * iResolution.y);
        width = mod(width, 4.0);
        height = mod(height, 4.0);
        float offset = _Bayer4x4[int(width)][int(height)];
        p0 -= rayDir * offset * STEP_SIZE*2.0;
        #endif

        float dist = length(p1 - p0);
        int s = int(dist/STEP_SIZE) + 1;

        vec4 integral = raymarch(p0, rayDir, STEP_SIZE, s);

        col.rgb = mix(integral.rgb, col.rgb, integral.a);
    }	

    fragColor = col;
}

#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    pos.x += sin(pos.x * 0.5) * 4.0;
    pos.y += sin(pos.y * 0.5) * 4.0;

    float r = length(pos);

    r = cos(exp(-r));

    float theta = (100.0 + iTime) * 0.02 * 1.0/r;
    float sinTheta = sin(theta);
    float cosTheta = cos(theta);
    mat2 m = mat2(cosTheta, -sinTheta,
                  sinTheta, cosTheta);
    pos = m * pos;

    float angle = atan(pos.y, pos.x);

    vec2 uv = vec2(0.5/r + iTime * 0.2, angle/PI);

    float l = length(uv);

    float t = 25.0;

    float time = iTime;

    float a[3];

    float c = 0.0;

    for (int i = 0; i < 3; i++)
    {

    float s = l;

    float tuv = uv.x;
    uv.x = uv.x - uv.y;
    uv.y = uv.y +tuv;

    s += (sin(uv.x * t * 0.2));

    s += (sin(uv.y * t * 0.2));

    float m = mod(s + (time + c) * 0.2, 0.5);

    //m *= mod(r - iTime * 0.2, 0.5);

    a[i] = m;    

    c += sin(iTime);

    }

    vec3 col = vec3(a[0], a[1], a[2]);

    col.rgb *= exp(r);
    col.rgb *= pow(r, 4.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define float2 vec2
#define float3 vec3
#define float4 vec4

float2 dw(float2 p, float2 c, float t)
{
    p-=c;
    float l=length(p);
    float s=sin(l-t);
    s/=l;
    s*=clamp(1.0-l*0.02,0.0,1.0);
    float x = 0.5 + 0.5*(p.x*s);
    float y = 0.5 + 0.5*(p.y*s);
    return float2(x,y);
    return float2(s*0.5+0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	float2 uv = ((fragCoord-iResolution.xy/2.0)/iResolution.xx)*100.0;
    float2 m=((iMouse.xy-iResolution.xy/2.0)/iResolution.xx)*100.0;
    float t=iTime*15.0;
    float2 col=float2(0);
    col+=dw(uv,m,t);
    col+=dw(uv,float2(-13,8),t+1.0);
    col+=dw(uv,float2(8,15),t+2.0);
    col+=dw(uv,float2(-18,-5),t-1.0);
    col*=0.25;
    fragColor = vec4(col.xxy,1);
}

#define MAX_STEPS 256
#define STEP_SIZE 0.05
#define _FoV 45.0

#define JITTER

const vec3 _Absorption = vec3(0.4,0.2,0.7);
const vec3 _Light = vec3(0.5,0.9,0.9);
const vec3 _LightPos = vec3(0.0, 2.0, 1.0);
const float _Density = 0.75;

//Ray-sphere intersection
bool raycastSphere(vec3 ro, vec3 rd, out vec3 p0, out vec3 p1, vec3 center, float r)
{
    float A = 1.0; //dot(rd, rd);
    float B = 2.0 * (rd.x * (ro.x - center.x) + rd.y * (ro.y - center.y) + rd.z * (ro.z - center.z));
    float C = dot(ro - center, ro - center) - (r * r);

    float D = B * B - 4.0 * A * C;
    if (D < 0.0)
    {
        return false;
    }
    else
    {
        float t0 = (-B - D)/(2.0 * A);
        float t1 = (-B + D)/(2.0 * A);
        p0 = ro + rd * t0;
        p1 = ro + rd * t1;
        return true;
    }
}

//Y-axis rotation matrix
vec3 rotateY(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    return rot * p;
}

//iq's gradient noise
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

//Fractal (pink) noise function S(f) ∝ 1/f^α, {0 <= α <=2}
float fbm(vec3 seed, int octaves, float freq, float lac)
{
    float val;
    for (int i = 0; i < octaves; i++)
    {
        val += noise(seed * freq * float(i+1)) / (pow(float(i+1), lac));
    }

    return val;
}

float sampleVolume(vec3 pos)
{
    pos = rotateY(pos, iTime * 0.1);
    float rr = dot(pos,pos);
    rr = sqrt(rr)+0.5;
    float p = 1.0/(rr*20.0);
    p *= _Density;

    if (p < 0.02)
        return p;

    pos = rotateY(pos, noise(pos.y* 0.2 + pos));
    float n = noise(pos*2.0);
    n = max(0.0, n - abs(fbm(pos+32.0, 4, 4.0, 0.8)));
	n += p * 0.1;

    return n;
}

vec4 raymarch(vec3 pos, vec3 dir, float ds, int s)
{
    vec4 result = vec4(0.0,0.0,0.0,1.0);
    int steps = min(s, MAX_STEPS);
    for (int i = 0; i < steps; i++)
    {
        float p = sampleVolume(pos);
        if (p > 0.0)
        {
            vec3 r = _LightPos - pos;
            float atten = 1.0/dot(r, r)*32.0;
            vec3 scatter = _Light * atten * p * ds;
            vec3 amb = vec3(0.03,0.03,0.0);
            float ext = 1.0 - exp(-p * ds);
            result.rgb += (amb + scatter) * result.a * _Absorption;
            result.a *= exp(-p);

            if (result.a <= 0.0)
                return result;
        }

        pos += dir * ds;
    }

    result = clamp(result, 0.0, 1.0);

    return result;
}

//4x4 Bayer matrix for ordered dithering
const mat4 _Bayer4x4 = mat4(vec4(0,0.5,0.125,0.625),
                        vec4(0.75,0.25,0.875,.375), 
                        vec4(0.1875,0.6875,0.0625,0.5625), 
                        vec4(0.9375,0.4375,0.8125,0.3125));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = iMouse.y/iResolution.y;
    vec3 rayOrigin = vec3(0.0, 0.0, -5.0 + (5.0 * zoom));
    //Compute eye vector from field of view
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    vec2 uv2 = (-1.0 + 2.0 * uv) * vec2(ar, 1.0);
    float d = ar/tan(radians(_FoV/2.0));    
    vec3 rayDir = normalize(vec3(uv2, d));

    vec4 col = vec4(0.0,0.0,0.0,1.0);
    col.rgb += smoothstep(0.5, 0.8, abs(noise(rotateY(rayDir, iTime * 0.1) * 256.0)));

    vec3 p0, p1;
    if (raycastSphere(rayOrigin, rayDir, p0, p1, vec3(0.0), 1.0))
    {        
        #ifdef JITTER
        //Bayer matrix ordered depth jittering
        float width = (uv.x * iResolution.x);
        float height = (uv.y * iResolution.y);
        width = mod(width, 4.0);
        height = mod(height, 4.0);
        float offset = _Bayer4x4[int(width)][int(height)];
        p0 -= rayDir * offset * STEP_SIZE*2.0;
        #endif

        float dist = length(p1 - p0);
        int s = int(dist/STEP_SIZE) + 1;

        vec4 integral = raymarch(p0, rayDir, STEP_SIZE, s);

        col.rgb = col.rgb * integral.a + integral.rgb * (1.0-integral.a);  
        col.rgb += pow(1.0001 + dot(uv2,uv2), -1000.0) * 4.0 * vec3(1.0,0.8,0.5) * (integral.a+0.25);

    }	

    fragColor = col;
}

#define MAX_STEPS 64
