const float _Oblateness = 1.02;
const vec3 _Center = vec3(0.0,0.0,0.0);

const vec3 _LightPos = vec3(-10.0, 0.0, -20.0);
const vec3 _LightCol = vec3(1.0, 1.0, 1.0);

const vec4 _Color = vec4(0.5,0.7,0.8, 1.0);
const vec3 _Emissive = vec3(0.0, 0.0, 0.0);
const float _Specular = 0.5;
const float _SpecularHardness = 10.0;
const float _FresnelPow = 16.0;
const float _FresnelBias = 0.1;

const vec4 _OceanColor = vec4(0.2, 0.3, 0.45, 1.0);
const float _OceanDepth = 0.1;

