//Signed distance function for a spheroid. 
//For oblateness(O), O == 1 : Sphere, O < 1 : Prolate Spheroid, O > 1 : Oblate Spheroid 
float sdSpheroid(vec3 position, float oblateness)
{
    vec3 center = _Center;
    position.y *= oblateness;
    return length(center - position) - _Radius;
}

//Raymarching loop
vec3 raymarch(vec3 position, vec3 direction)
{
    float dist;
    vec3 center = _Center;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        dist = sdSpheroid(position, _Oblateness);
        if (dist < MIN_DIST)
        {
            return position;
        }

        position += direction * dist;
    }

    return vec3(0.0, 0.0, 0.0);
}

//Fast geodesic normal calculation
vec3 fastNormal(vec3 p)
{
    p.y *= _Oblateness;
    return normalize(p - _Center);
}

//Correct geodetic normal for a non-spherical ellipsoid
vec3 geodeticNormal(vec3 p)
{
    vec3 axes = vec3(1.0, _Oblateness*_Oblateness, 1.0);
    vec3 R = p - _Center;
    return normalize(R/axes);
}

//Simplex noise from https://www.shadertoy.com/view/XtBGDG
float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(12.9898,78.233,128.852))) * 43758.5453)*2.0-1.0;
}

float snoise(vec3 p)
{

	float f3 = 1.0/3.0;
	float s = (p.x+p.y+p.z)*f3;
	int i = int(floor(p.x+s));
	int j = int(floor(p.y+s));
	int k = int(floor(p.z+s));

	float g3 = 1.0/6.0;
	float t = float((i+j+k))*g3;
	float x0 = float(i)-t;
	float y0 = float(j)-t;
	float z0 = float(k)-t;
	x0 = p.x-x0;
	y0 = p.y-y0;
	z0 = p.z-z0;

	int i1,j1,k1;
	int i2,j2,k2;

	if(x0>=y0)
	{
		if(y0>=z0){ i1=1; j1=0; k1=0; i2=1; j2=1; k2=0; } // X Y Z order
		else if(x0>=z0){ i1=1; j1=0; k1=0; i2=1; j2=0; k2=1; } // X Z Y order
		else { i1=0; j1=0; k1=1; i2=1; j2=0; k2=1; }  // Z X Z order
	}
	else 
	{ 
		if(y0<z0) { i1=0; j1=0; k1=1; i2=0; j2=1; k2=1; } // Z Y X order
		else if(x0<z0) { i1=0; j1=1; k1=0; i2=0; j2=1; k2=1; } // Y Z X order
		else { i1=0; j1=1; k1=0; i2=1; j2=1; k2=0; } // Y X Z order
	}

	float x1 = x0 - float(i1) + g3; 
	float y1 = y0 - float(j1) + g3;
	float z1 = z0 - float(k1) + g3;
	float x2 = x0 - float(i2) + 2.0*g3; 
	float y2 = y0 - float(j2) + 2.0*g3;
	float z2 = z0 - float(k2) + 2.0*g3;
	float x3 = x0 - 1.0 + 3.0*g3; 
	float y3 = y0 - 1.0 + 3.0*g3;
	float z3 = z0 - 1.0 + 3.0*g3;	

	vec3 ijk0 = vec3(i,j,k);
	vec3 ijk1 = vec3(i+i1,j+j1,k+k1);	
	vec3 ijk2 = vec3(i+i2,j+j2,k+k2);
	vec3 ijk3 = vec3(i+1,j+1,k+1);	

	vec3 gr0 = normalize(vec3(noise3D(ijk0),noise3D(ijk0*2.01),noise3D(ijk0*2.02)));
	vec3 gr1 = normalize(vec3(noise3D(ijk1),noise3D(ijk1*2.01),noise3D(ijk1*2.02)));
	vec3 gr2 = normalize(vec3(noise3D(ijk2),noise3D(ijk2*2.01),noise3D(ijk2*2.02)));
	vec3 gr3 = normalize(vec3(noise3D(ijk3),noise3D(ijk3*2.01),noise3D(ijk3*2.02)));

	float n0 = 0.0;
	float n1 = 0.0;
	float n2 = 0.0;
	float n3 = 0.0;

	float t0 = 0.5 - x0*x0 - y0*y0 - z0*z0;
	if(t0>=0.0)
	{
		t0*=t0;
		n0 = t0 * t0 * dot(gr0, vec3(x0, y0, z0));
	}
	float t1 = 0.5 - x1*x1 - y1*y1 - z1*z1;
	if(t1>=0.0)
	{
		t1*=t1;
		n1 = t1 * t1 * dot(gr1, vec3(x1, y1, z1));
	}
	float t2 = 0.5 - x2*x2 - y2*y2 - z2*z2;
	if(t2>=0.0)
	{
		t2 *= t2;
		n2 = t2 * t2 * dot(gr2, vec3(x2, y2, z2));
	}
	float t3 = 0.5 - x3*x3 - y3*y3 - z3*z3;
	if(t3>=0.0)
	{
		t3 *= t3;
		n3 = t3 * t3 * dot(gr3, vec3(x3, y3, z3));
	}
	return 96.0*(n0+n1+n2+n3);

}

float ridgedNoise(vec3 position, float frequency, float amplitude)
{
    return((1.0 - abs(snoise(position * frequency))) * 2.0 - 1.0) * amplitude;
}

//This function generates the planet from layered noise and handles shading
vec4 renderSurface(vec3 normal, vec3 sp, vec3 camPos, float h)
{
    //Get direction to center for parallel light rays
    vec3 lightDir = -normalize(_Center - _LightPos);
    //float dist = length(_Center - _WorldSpaceCameraPos);
    vec3 worldNormal = normal;

    vec3 seed = worldNormal;

    //Terrain Generation

    //Fractal Noise
    const int octaves = 8;
    float scale = 3.0;
    float falloff = 1.0;

    float offset = 0.0;
    for (int i = 0; i < octaves; i++)
    {
        offset += snoise(seed * scale * float(i * i)) / falloff;
        falloff *= 2.0;
    }

    //Scales noise based on lattitude to simulate polar climate
    offset += abs(h * h);

    //Ground color
    float t = clamp((1.0 + offset) / 2.0, 0.0, 1.0);
    vec4 texCol = mix(_Color, vec4(1.0, 1.0, 1.0, 1.0), t);

    //Set Sea Level
    float depth = _OceanDepth * clamp(iMouse.y * 0.5, 0.0, 1000.0);
    float ocean = step(offset, depth);
    texCol = mix(texCol, _OceanColor, ocean);

    //Upper Cloud Layer
    vec3 skewedSeed = vec3(worldNormal.x, worldNormal.y * _UpperCloudSkew, worldNormal.z);
    vec3 mirroredSeed = worldNormal;
    mirroredSeed.xy *= -1.0;
    float uCloud = clamp(max(snoise(skewedSeed * 2.0) - ridgedNoise(seed, 4.0, 1.0) + snoise(skewedSeed * _UpperCloudScale)+ snoise(normal * 8.0) + snoise(skewedSeed * 32.0) + snoise(mirroredSeed * 64.0) / 4.0 + snoise(normal * 128.0)/4.0, 0.0) * _UpperCloudStrength, 0.0, 1.0);
    float cloudFade = pow(uCloud, _UpperCloudExponent);
    texCol.rgb = mix(texCol.rgb, _UpperCloudColor.rgb * 2.0, cloudFade);

    //Lower Cloud Layer
    if (_LowerCloudStrength > 0.0)
    {
        seed *= 8.0 * _LowerCloudFrequency;
        seed.y *= 2.0;
        float lowerClouds = pow(abs(snoise(seed)), 4.0);
        lowerClouds -= snoise(seed) + snoise(seed) + snoise(normal * 4.0)/2.0 + snoise(seed * 2.0) / 4.0 + snoise(seed * 8.0) / 3.0 + snoise(seed * 16.0) / 6.0;
        lowerClouds -= abs(snoise(seed * _LowerCloudTurbulence));
        lowerClouds = clamp(lowerClouds, 0.0, 1.0);
        texCol.rgb = mix(texCol.rgb, _UpperCloudColor.rgb * 2.0, clamp(lowerClouds, 0.0, 1.0) * _LowerCloudStrength);
    }

    //Lambert Reflectance
    float NdotL = dot(normal, lightDir);

    //Blinn-Phong Specular
    vec3 viewDir = -normalize(sp - camPos);
    vec3 halfangle = (lightDir + viewDir)/length(lightDir + viewDir);
    float NdotH = dot(normal, halfangle);
    float intensity = pow(clamp(NdotH, 0.0, 1.0), _SpecularHardness);

    vec4 col;
    col.rgb = clamp(texCol.rgb * _LightCol * NdotL + (intensity * ocean * (1.0-cloudFade) * _Specular) + _Emissive, 0.0, 1.0);

    //Fresnel-based atmosphere effect. Not physically accurate but looks good from most distances and relatively cheap.
    float fresnel = clamp(_FresnelBias + (40.0) * pow(1.0 + dot(normalize(sp - camPos), normal), _FresnelPow), 0.0, 1.0);
    //col.a = max(0.0,1.0 - fresnel);

//    float NdotV = dot(normal, viewDir);
//    col.rgb *= saturate(tex1D(_ScatteringTex, -NdotL * _ScatteringTex_ST.x + _ScatteringTex_ST.z));
//    col.rgb += saturate(tex1D(_ScatteringTex, -NdotL * _ScatteringTex_ST.y + _ScatteringTex_ST.w)) * fresnel * NdotL;
//    col.a *= 1-fresnel;
    col.rgb += (fresnel + _Emissive) * NdotL;
    col.rgb *= 1.0-fresnel;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Y-axis rotation matrix
    float cosTheta = cos(iTime * 0.2);
    float sinTheta = sin(iTime * 0.2);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    //World camera position
    vec3 camPos = vec3(0.0,0.0,-25.0);
    #ifdef ROTATE
    camPos = rot * camPos;
    #endif

    //Screen space uv coords
    vec2 uv = fragCoord/iResolution.xy;

    //Eye vector
    vec3 viewDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(iResolution.x/iResolution.y, 1), 4));
    #ifdef ROTATE
    viewDir = rot * viewDir;
    #endif

    //Get world position on sphere
    vec3 wPos = raymarch(camPos, viewDir);

    vec3 col;

    if (wPos != vec3(0.0))
    {

        //Calculate surface normal
        vec3 wNormal = geodeticNormal(wPos);

        col = renderSurface(wNormal, wPos, camPos, abs(wPos.y)).rgb;
    }
    else
    {
        col.rgb += smoothstep(0.85, 0.9, abs(snoise(rot * viewDir * 32.0)));
    }

    fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 256
#define STEP_SIZE 0.06
#define _FoV 45.0
#define _Quality 5

#define JITTER

#define ROTATE

const vec3 _Absorption = vec3(0.7,0.6,0.5);
const vec3 _Ambient = vec3(0.4,0.15,0.2);
const vec3 _Light = vec3(1.0,0.3,0.2);
const vec3 _LightPos = vec3(0.0, 0.0, 0.0);
const float _LightIntensity = 2.0;
const float _Density = 15.0;
const float _Radius = 1.0;
const float _Mie = 0.4;
const float _G = 0.7;

//Improved Heyney-Greenstein phase function
float cornetteShanksPhase(float mu, float g)
{
	float g2 = g * g;
	return (3.0 * (1.0 - g2) * (1.0 + mu * mu)) / (2.0 * (2.0 + g2) * pow(1.0 + g2 - 2.0 * g * mu, 1.5));
}

//Ray-sphere intersection
bool raycastSphere(vec3 ro, vec3 rd, out vec3 p0, out vec3 p1, vec3 center, float r)
{
    float A = 1.0; //dot(rd, rd);
    float B = 2.0 * (rd.x * (ro.x - center.x) + rd.y * (ro.y - center.y) + rd.z * (ro.z - center.z));
    float C = dot(ro - center, ro - center) - (r * r);

    float D = B * B - 4.0 * A * C;
    if (D < 0.0)
    {
        return false;
    }
    else
    {
        float t0 = (-B - D)/(2.0 * A);
        float t1 = (-B + D)/(2.0 * A);
        p0 = ro + rd * t0;
        p1 = ro + rd * t1;
        return true;
    }
}

//Y-axis rotation matrix
vec3 rotateY(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    return rot * p;
}

//iq's gradient noise
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

float fbm(vec3 seed, int octaves, float freq, float lac)
{
    float val;
    float j = 1.0;
    for (int i = 0; i < octaves; i++, j+=1.0)
    {
        val += noise(seed * freq * j) / pow(j, lac);
    }

    return val;
}

float sampleVolume(vec3 pos)
{
    float r = length(pos);
    float f = exp(-r);
    float p = f * _Density;

    if (p <= 0.5)
        return p;

    pos = rotateY(pos, pos.y * noise(16.0+pos) * 4.0);
    p *= fbm(pos, _Quality, 2.0, 0.5)*8.0*f;
    p -= abs(noise(150.0+pos * 0.4))*100.0*f;
    p += pow(f,8.0)*33.33333*_Density;

    p = max(0.0,p);

    return p;
}

vec4 raymarch(vec3 pos, vec3 dir, float ds, int s)
{
    vec4 result = vec4(0.,0.0,0.0,1.0);
    int steps = min(s, MAX_STEPS);
    for (int i = 0; i < steps; i++)
    {
        float p = sampleVolume(pos);
        if (p > 0.0)
        {
            vec3 r = _LightPos - pos;
            float atten = _LightIntensity/dot(r, r);
            vec3 ext = max(vec3(0.000001), (_Absorption * p) + vec3(_Mie * p));
            vec3 trans = exp(-ext * ds);
            vec3 lightDir = normalize(r);
            float mu = dot(lightDir, -dir);
            float phase = cornetteShanksPhase(mu, _G);
            vec3 lum = _Ambient + _Light * phase * (1.0-_Absorption) * _Mie * p * atten;
            vec3 integral = (lum - (lum*trans))/ext;

            result.rgb += integral * result.a;
            vec3 div = vec3(0.3333333);
            result.a *= dot(trans, div);

            if (result.a <= 0.05)
                return result;
        }

        pos += dir * ds;
    }

    result = clamp(result, 0.0, 1.0);

    return result;
}

//4x4 Bayer matrix for ordered dithering
const mat4 _Bayer4x4 = mat4(vec4(0,0.5,0.125,0.625),
                        vec4(0.75,0.25,0.875,.375), 
                        vec4(0.1875,0.6875,0.0625,0.5625), 
                        vec4(0.9375,0.4375,0.8125,0.3125));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = iMouse.y/iResolution.y;
    vec3 rayOrigin = vec3(0.0, 0.0, -5.0 + (5.0 * zoom));
    //Compute eye vector from field of view
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan(radians(_FoV/2.0));    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));

    #ifdef ROTATE
    float t = iTime * 0.1;
    rayDir = rotateY(rayDir, t);
	rayOrigin = rotateY(rayOrigin, t);
    #endif

    vec4 col = vec4(0.0,0.0,0.0,1.0);
    col.rgb += smoothstep(0.5, 0.8, abs(noise(rotateY(rayDir, iTime * 0.1) * 256.0)));

    vec3 p0, p1;
    if (raycastSphere(rayOrigin, rayDir, p0, p1, vec3(0.0), _Radius))
    {        
        #ifdef JITTER
        //Bayer matrix ordered depth jittering
        float width = (uv.x * iResolution.x);
        float height = (uv.y * iResolution.y);
        width = mod(width, 4.0);
        height = mod(height, 4.0);
        float offset = _Bayer4x4[int(width)][int(height)];
        p0 -= rayDir * offset * STEP_SIZE*2.0;
        #endif

        float dist = length(p1 - p0);
        int s = int(dist/STEP_SIZE) + 1;

        vec4 integral = raymarch(p0, rayDir, STEP_SIZE, s);

        col.rgb = mix(integral.rgb, col.rgb, integral.a);
    }	

    fragColor = col;
}

#define NUM_SAMPLES 8
#define STEP_SIZE 0.4
#define NOISE_SIMPLEX_1_DIV_289 0.00346020761245674740484429065744
#define _Radius 1.0
#define _FoV 45.0
#define _Deg2Rad 0.01745329

const float _Freq = 10.0;
const float _Timescale = 0.1;

