float mod289(float x) {
	return x - floor(x * NOISE_SIMPLEX_1_DIV_289) * 289.0;
}

vec2 mod289(vec2 x) {
	return x - floor(x * NOISE_SIMPLEX_1_DIV_289) * 289.0;
}

vec3 mod289(vec3 x) {
	return x - floor(x * NOISE_SIMPLEX_1_DIV_289) * 289.0;
}

vec4 mod289(vec4 x) {
	return x - floor(x * NOISE_SIMPLEX_1_DIV_289) * 289.0;
}

float permute(float x) {
	return mod289(
		x*x*34.0 + x
	);
}

vec3 permute(vec3 x) {
	return mod289(
		x*x*34.0 + x
	);
}

vec4 permute(vec4 x) {
	return mod289(
		x*x*34.0 + x
	);
}

float taylorInvSqrt(float r) {
	return 1.79284291400159 - 0.85373472095314 * r;
}

vec4 taylorInvSqrt(vec4 r) {
	return 1.79284291400159 - 0.85373472095314 * r;
}

vec4 grad4(float j, vec4 ip)
{
	const vec4 ones = vec4(1.0, 1.0, 1.0, -1.0);
	vec4 p, s;
	p.xyz = floor( fract(j * ip.xyz) * 7.0) * ip.z - 1.0;
	p.w = 1.5 - dot( abs(p.xyz), ones.xyz );

	// GLSL: lessThan(x, y) = x < y
	// HLSL: 1 - step(y, x) = x < y
	s = vec4(
		1.0 - step(0.0, p)
	);
	p.xyz = p.xyz + (s.xyz * 2.0 - 1.0) * s.www; 

	return p;
}

//4D
float snoise(vec4 v)
{
	const vec4 C = vec4(
		0.138196601125011, // (5 - sqrt(5))/20 G4
		0.276393202250021, // 2 * G4
		0.414589803375032, // 3 * G4
	 -0.447213595499958  // -1 + 4 * G4
	);

// First corner
	vec4 i = floor(
		v +
		dot(
			v,
			vec4(0.309016994374947451) // (sqrt(5) - 1) / 4
		)
	);
	vec4 x0 = v - i + dot(i, C.xxxx);

// Other corners

// Rank sorting originally contributed by Bill Licea-Kane, AMD (formerly ATI)
	vec4 i0;
	vec3 isX = step( x0.yzw, x0.xxx );
	vec3 isYZ = step( x0.zww, x0.yyz );
	i0.x = isX.x + isX.y + isX.z;
	i0.yzw = 1.0 - isX;
	i0.y += isYZ.x + isYZ.y;
	i0.zw += 1.0 - isYZ.xy;
	i0.z += isYZ.z;
	i0.w += 1.0 - isYZ.z;

	// i0 now contains the unique values 0,1,2,3 in each channel
	vec4 i3 = clamp(i0, 0.0, 1.0);
	vec4 i2 = clamp(i0-1.0, 0.0, 1.0);
	vec4 i1 = clamp(i0-2.0, 0.0, 1.0);

	//	x0 = x0 - 0.0 + 0.0 * C.xxxx
	//	x1 = x0 - i1  + 1.0 * C.xxxx
	//	x2 = x0 - i2  + 2.0 * C.xxxx
	//	x3 = x0 - i3  + 3.0 * C.xxxx
	//	x4 = x0 - 1.0 + 4.0 * C.xxxx
	vec4 x1 = x0 - i1 + C.xxxx;
	vec4 x2 = x0 - i2 + C.yyyy;
	vec4 x3 = x0 - i3 + C.zzzz;
	vec4 x4 = x0 + C.wwww;

// Permutations
	i = mod289(i); 
	float j0 = permute(
		permute(
			permute(
				permute(i.w) + i.z
			) + i.y
		) + i.x
	);
	vec4 j1 = permute(
		permute(
			permute(
				permute (
					i.w + vec4(i1.w, i2.w, i3.w, 1.0 )
				) + i.z + vec4(i1.z, i2.z, i3.z, 1.0 )
			) + i.y + vec4(i1.y, i2.y, i3.y, 1.0 )
		) + i.x + vec4(i1.x, i2.x, i3.x, 1.0 )
	);

// Gradients: 7x7x6 points over a cube, mapped onto a 4-cross polytope
// 7*7*6 = 294, which is close to the ring size 17*17 = 289.
	const vec4 ip = vec4(
		0.003401360544217687075, // 1/294
		0.020408163265306122449, // 1/49
		0.142857142857142857143, // 1/7
		0.0
	);

	vec4 p0 = grad4(j0, ip);
	vec4 p1 = grad4(j1.x, ip);
	vec4 p2 = grad4(j1.y, ip);
	vec4 p3 = grad4(j1.z, ip);
	vec4 p4 = grad4(j1.w, ip);

// Normalise gradients
	vec4 norm = taylorInvSqrt(vec4(
		dot(p0, p0),
		dot(p1, p1),
		dot(p2, p2),
		dot(p3, p3)
	));
	p0 *= norm.x;
	p1 *= norm.y;
	p2 *= norm.z;
	p3 *= norm.w;
	p4 *= taylorInvSqrt( dot(p4, p4) );

// Mix contributions from the five corners
	vec3 m0 = max(
		0.6 - vec3(
			dot(x0, x0),
			dot(x1, x1),
			dot(x2, x2)
		),
		0.0
	);
	vec2 m1 = max(
		0.6 - vec2(
			dot(x3, x3),
			dot(x4, x4)
		),
		0.0
	);
	m0 = m0 * m0;
	m1 = m1 * m1;

	return 49.0 * (
		dot(
			m0*m0,
			vec3(
				dot(p0, x0),
				dot(p1, x1),
				dot(p2, x2)
			)
		) + dot(
			m1*m1,
			vec2(
				dot(p3, x3),
				dot(p4, x4)
			)
		)
	);
}

vec3 raySample(vec3 position)
{
    vec3 value = vec3(0.0);

    float t = _Timescale * iTime;

    float div = snoise(vec4(position * snoise(vec4(position + 16.0, t)) * 1.0, t));
    value.r = snoise(vec4(position*div, t));
    value.g = snoise(vec4(position*div + 4.0, t));
    value.b = snoise(vec4(position*div + 8.0, t));
    value = abs(value);
    value = clamp(value, 0.0, 1.0);

    return value;
}

//Transfer function
vec3 rayIntegral(vec3 position, vec3 direction)
{
    vec3 integral;

    for(int i = 0; i < NUM_SAMPLES; i++)
    {
        integral += raySample(position) * STEP_SIZE;

        position += direction * STEP_SIZE;
    }

    return integral;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 rayOrigin = vec3(0.0);

    //Compute eye vector from field of view
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan((_FoV/2.0) * _Deg2Rad);    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));

    vec3 col = vec3(1.0);

    col = rayIntegral(rayOrigin, rayDir);

    fragColor = vec4(col, 1.0);
}

