void animateScene(in float time) {
    //animate the sphere position
	const float pi = 3.1415926535;
	const float rs = 2.0;
	const float spr = 5.0;
	float as = 2.0*pi * time /spr;

    spherePos = cameraTarget + rs * vec3(-sin(as), 0.0, cos(as)) + vec3(0, 1, 0);

    lightPos += vec3(0.0, 10.5 + 9.5 * cos(time) - 10.0, 0.0);

    float targetDist = length(cameraTarget - cameraPos);
    cameraPos -= vec3(0, 0, targetDist);
    cameraPos += targetDist * vec3(sin(time), max(sin(time*0.5), 0.0), cos(time));

}


float raySphere(    vec3 rayPos    ,     vec3 rayDir, vec3 spherePos, float sphereRadius,
                out vec3 intersecPt, out vec3 normal)
{
    // Explanation:
    //
    //  Any point P(t) on the ray respect the following equation:
    //
    //         P(t) = rayPos + t * rayDir                            (1)
    //
    //  Any point on the sphere must be at sphereRadius distance from the sphere center.
    //  Which, for a point P of space, yields to follow equation:
    //
    //                 distance(P - spherePos)   = sphereRadius
    //     <=>         distance(P - spherePos)^2 = sphereRadius^2
    //     <=> dot(P - spherePos, P - spherePos) = sphereRadius^2    (2)
    //
    // The intersection point(s) of the ray with the sphere must respect both equations.
    // So, we can replace P of equation (2) with (1) to express both constraints into a
    // single equation:
    //
    //      dot(rayPos+t*rayDir - spherePos, rayPos+t*rayDir - spherePos) = sphereRadius^2
    //  <=> dot(t*rayDir + rayPos-spherePos, t*rayDir + rayPos-spherePos) = sphereRadius^2
    //  <=> dot(t * rayDir + diff, t * rayDir + diff)                     = sphereRadius^2
    //        ; with diff = rayPos - spherePos
    //
    // As we can 'distribute' additive component over a dot product, we have:
    //
    //      dot(t*rayDir, t*rayDir) + 2*dot(diff,t*rayDir) + dot(diff, diff) = sphereRadius^2
    //  <=> t^2*dot(rayDir, rayDir) + t*2*dot(diff,rayDir) + dot(diff, diff) = sphereRadius^2
    //  <=> t^2*dot(rayDir, rayDir) + t*2*dot(diff,rayDir) + dot(diff, diff)-sphereRadius^2 = 0
    //  <=> a*t^2 + b*t + c = 0
    //        ; with:
    //                a =   dot(rayDir, rayDir)
    //                b = 2*dot(diff  , rayDir)
    //                c =   dot(diff  , diff  ) - sphereRadius^2
    //
    // This is a 2nd degree (quadratic) equation to solve.
    //
    // If discriminant di (= b^2 - 4*a*c) is negative, the ray doesn't intersect the sphere.
    // Otherwise there are up to two solutions (t1 and t2).

    vec3 diff = rayPos - spherePos;

    float a =       dot(rayDir, rayDir);
    float b = 2.0 * dot(diff  , rayDir);
    float c =       dot(diff  , diff  ) - sphereRadius * sphereRadius;

    float di = b*b - 4.0*a*c;

    if (di >= 0.0)
    {
        float sdi = sqrt(di);
        float den = 1. / (2.0 * a);
        float t1  = (-b - sdi) * den;
        float t2  = (-b + sdi) * den;

        // We can notice that 'a' (so 'den') and sdi are positive.
        // So, we know for sure that t1 <= t2 whatever the configuration.

        // We can also list all the possibilities of intersection or non-intersection:
        //
        //    t1...t2.....r------------>	(t1<0 & t2<0)			no intersection
        //       t1t2.....r------------>	(t1<0 & t2<0 & t1=t2)	no intersection
        //    t1........t2r------------>	(t1<0 & t2=0)			assume no intersection
        //            t1t2r------------>	(t1=t2=0)				assume no intersection
        //    t1..........r--------t2-->	(t1<0 & t2>0)			intersection at (t2, -n)
        //              t1r--------t2-->	(t1=0 & t2>0)			intersection at (t2, -n)
        //                r---t1---t2-->	(t1>0 & t2>t1)			intersection at (t1,  n)
        //                r---t1t2----->	(t1>0 & t2=t1)			intersection at (t1,  n)

        // This can be summed up by:
		//        
        // If t1 >  0, then the sphere is ahead of the ray, and the point we see is the
        //             one associated to t1 with a normal pointing outward the sphere.
        //             
        // If t1 <= 0 and t2 >  0, then the ray starts inside the sphere, and the point we see
        //                         is the one associated to t2 with a normal point inward the
        //                         sphere.
        //                         
        // Otherwise, there is no intersection or we assume there is none.

        float t = -1.0;
        float dir = 1.0;
        if (t1 > 0.0)
        	t = t1;
        else if (t2 > 0.0)
        {
            t = t2;
            dir = -1.0;
        }
        else
            return t;

        // As we found the value of t, we can insert it into equation (1) to find
        // the position of the first intersection point encountered along the ray.
        intersecPt = rayPos + t * rayDir;

        // Then, we can use the property of a sphere that the infinite line along the
        // normal vector to a point on the sphere is passing through the center of the
        // sphere.
        // Then, we need to reverse the vector direction if the intersection point is
        // seen from inside the sphere.
        normal     = normalize(intersecPt - spherePos) * dir;

        return t;
    }

    return -1.0;
}

//----------------------------------------------------------------------------------------------

// NOTE: planeNormal must be a unit-vector!
float rayPlane(    vec3 rayPos    ,     vec3 rayDir, vec3 planePos, vec3 planeNormal,
               out vec3 intersecPt, out vec3 normal)
{
    // Explanation:
    //
    //  Any point P(t) on the ray respect the following equation:
    //
    //         P(t) = rayPos + t * rayDir                            (1)
    //
    //  The analytic equation of a plane is of the form: a * x + b * y + c * z + d = 0
    //  That can be rewritten with a dot-product:              (a,b,c).(x,y,z) + d = 0
    //  With (a,b,c) being the normal N to the plane which defines the orientation of the plane,
    //  with (x,y,z) being any 3D point of space, and 'd' defining at which position the plane
    //  is located.
    //
    //  NOTE: Without loss of generality (a,b,c) can be normalized and d scaled accordingly.
    //        So, the normal is assumed here to be a unit-vector!!!
    //
    //  So, we have:    dot(planeNormal, P) + d = 0   for any 3D point P on the plane
    //
    //  We can find d because we know our plane pass through planePos point.
    //  This means planePos also follow the above equation:
    //      
    //          dot(planeNormal, planePos) + d = 0
    //      <=> d = -dot(planeNormal, planePos)
    //
    //  So, we now know the complete equation of the plane:
    //
    //          dot(planeNormal, P) + (-dot(planeNormal, planePos)) = 0
    //      <=> dot(planeNormal, P) - dot(planeNormal, planePos) = 0
    //      <=> dot(planeNormal, P - planePos) = 0                   (2)
    //
    // The intersection point(s) of the ray with the plane must respect both equations.
    // So, we can replace P of equation (2) with (1) to express both constraints into a
    // single equation:
    //
    //      dot(planeNormal, (rayPos + t * rayDir) - planePos) = 0
    //  <=> dot(planeNormal, t * rayDir + (rayPos - planePos)) = 0
    //
    // As we can 'distribute' additive component over a dot product, we have:
    //
    //      dot(planeNormal, t * rayDir) + dot(planeNormal, (rayPos - planePos)) = 0
    //  <=> t * dot(planeNormal, rayDir) + dot(planeNormal, rayPos - planePos) = 0
    //  <=> t * dot(planeNormal, rayDir) = -dot(planeNormal, rayPos - planePos)
    //  <=> t * dot(planeNormal, rayDir) =  dot(planeNormal, planePos - rayPos)
    //
    // We can find t if 'den = dot(planeNormal, rayDir)' is not zero.
    // In the case it is zero, it means the ray is parallel to the plane, and there will be
    // no interesction (or an infinity if the rayPos is on the plane. In that case, we choose
    // to assume that we have no intersection at all).
    //
    // So, here after in this explanation, we assume 'den' is non zero.
    //
    // We can then find the value of 't' at which the intersection occurs:
    //
    //    t = dot(planeNormal, planePos - rayPos) / dot(planeNormal, rayDir)

    float den = dot(planeNormal, rayDir);

    if (abs(den) <= 0.000001)	// To avoid numerical instabilities we consider the ray to be 
        return -1.0;			// parallel if the angle between the normal and the ray is
        						// ALMOST zero.

    float t = dot(planeNormal, planePos - rayPos) / den;

    // As we found the value of t, we can insert it into equation (1) to find
    // the position of the first intersection point encountered along the ray.
    intersecPt = rayPos + t * rayDir;

    // NOTE: The normal to be returned has to be pointing 'towards' the rayPos position.
    //       So, the angle between the ray direction and the normal must > 90°, so the
    //       dot-product should be negative.
    //       This can be expressed by taking the 'sign' of den
    normal = -sign(den) * planeNormal;

    return t;
}

//----------------------------------------------------------------------------------------------

void computeCameraRayFromPixel( in vec2 pixCoord, out vec3 rayPos, out vec3 rayDir)
{
    // Compute camera focal length for a height on image plane going in [-1,1] range
    //
    //        
    //                     -1
    //                  /|  
    //                 / |
    //                /  |
    //               /   |
    //              /). .|. . . . . . fovY (vertical angle field of view expressed in degrees)
    //             /  )  |
    //            +---)--|-----> cz
    //            |\  )  |           î
    //            | \)   |           |
    //            v  \   |           |
    //           cy   \  |           | h
    //                 \ |           |
    //                  \|           |
    //                     1         v
    //                         
    //            <------>
    //              focal length
    //
    //    We have that:
    //
    //        tan((2*Pi/180)*cameraFovY/2) = h / focal
    //    <=> focal = h / tan(radians(cameraFovY/2))
    //    <=> focal = 1 / tan(radians(cameraFovY/2))

    float focal = 1.0 / tan(radians(cameraFovY) / 2.0);

    // Compute camera three main axis (X,Y,Z) expressed in world space
    //
    //  NOTES: - First, we compute the Z camera unit vector that goes from the camera position
    //           and look toward the camera target.

    vec3 cz = normalize(cameraTarget - cameraPos);

    //         - Then, we use the fact that the cross-product of two vectors (even if not
    //           perpendicular) gives a third vector that is perpendicular to the two others.
    //           Plus, the direction given by the result is given by applying the 'right-hand'
    //           technique by pointing the thumb along the first vector, the index finger along
    //           the second vector, and the result will be given by the middle finger.
    //           NOTE: the length of the resulting vector needs to be normalized if the two
    //                 input vectors aren't perpendicular AND of unit length.
    //         - To compute the cx vector, we can assume, temporarily, that the cy vector is the
    //           perfect '-up' vector.
    //           This is possible because cy must be as near as '-up' and the true cy vector
    //           will be in the same plane than the plane defined by the cz & the 'up' vectors.
    //         - So, as: cx = cross-product(cy, cz)
    //                      = normalized(cross-product(-up, cz))

    vec3 up = vec3(0,1,0);							// perfect up vector
    vec3 cx = normalize(cross(-up, cz));

    //         - Finally, we can find the true cy by applying: cy = cross-product(cz, cx)

    vec3 cy = normalize(cross( cz, cx));

    // Here we apply a set of transformations to transform the coordinates range:
    //
    //     pixCoord.xy                  is in range [0..  iResolution.x] x [0..  iResolution.y]
    //
    //  2.*pixCoord.xy                  is in range [0..2*iResolution.x] x [0..2*iResolution.y]
    //
    //  2.*pixCoord.xy - iResolution.xy is in range
    //                        [-iResolution.x..iResolution.x] x [-iResolution.y..iResolution.y]
    //
    //  pt = (2.*pixCoord.xy - iResolution.xy) / iResolution.y is in range
    //                         [-ratio,ratio] x [-1, 1] 
    //            with ratio = iResolution.x / iResolution.y;
    //
    // Here the scaled applied is uniform because we divide horizontal & vertical coordinates
    // by the same value: iResolution.y

    vec2 pt = (2.*pixCoord - iResolution.xy) / iResolution.y;

	// We can now find the ray properties:
    //   - The ray start position is the camera position
    //   - The ray direction can be decomposed into 3 sub-vectors:
    //      *            dy = (-pt.y) * cy   because pt vertical axis is going in opposite
    //                                       direction than cy, and pt.y is in range [-1,1],
    //                                       which matches the range spanned by vertical
    //                                       positions (in camera space) on the image plane
    //                                       located at the focal length distance along cz.
    //
    //      * similarly: dx =    pt.x * cx   NOTE: pt.x and horizontal positions (in camera
    //                                             space) shares the same range (taking into
    //                                             account the image ratio. Said differently,
    //                                             image & camera have the same ratio).
    //      * and:       dz =   focal * cz
    //
    //     which brings that: rayDir = pt.x * cx - pt.y * cy + focal * cz
    //
    //   - Finally, the ray direction is normalized because it makes it more practical later on.

    rayPos = cameraPos;
    rayDir = normalize(pt.x * cx - pt.y * cy + focal * cz);
}

//----------------------------------------------------------------------------------------------

// The aim of this routine is to find the nearest intersection the ray has with ALL the objects
float computeNearestIntersection(vec3 rayPos, vec3 rayDir,
                                 out int objectId, out vec3 intersecI, out vec3 normalI)
{
    // Set the default value when no intersection is found: we hit the 'sky'
    float minDist  = FLT_MAX;
          objectId = skyId;

    // Test the sphere
    vec3 intersecS, normalS;
    float distS = raySphere(rayPos, rayDir, spherePos, sphereRadius, intersecS, normalS);
    if ((distS > 0.0) && (distS < minDist))
    {
        objectId  =  sphereId;
        minDist   =     distS;
        intersecI = intersecS;
          normalI =   normalS;
    }

    // Test the plane
    vec3 intersecP, normalP;
    float distP =  rayPlane(rayPos, rayDir,  planePos,  planeNormal, intersecP, normalP);
    if ((distP > 0.0) && (distP < minDist))
    {
        objectId  =   planeId;
        minDist   =     distP;
	    intersecI = intersecP;
    	  normalI =   normalP;
    }

    // To remain coherent with the raySphere & rayPlane function that returns -1 when no
    // intersetion is found, we add the following two lines:
    if (objectId == skyId)
        minDist = -1.0;

    return minDist;
}

//----------------------------------------------------------------------------------------------

vec3 getSphereColorAtPoint(vec3 pt)
{
    return sphereCol;
}

//----------------------------------------------------------------------------------------------

// pt is assumed to be on the plane surface
vec3 getPlaneColorAtPoint(vec3 pt)
{
    // As the plane is textured with a checkboard pattern, we need to define
    // a 'texture coordinate frame'.
    //
    // NOTE: As this is pixel independent it could be calculated once and
    //       defined by 'const' global value at the top of this source code.
	//    
    // For that, we need first to compute the two X & Y axis of the plane that will serve with
    // the plane position as defining fully this texture coordinate frame.
    //
    // We assume the plane normal is the plane Z-axis.
    //
    // We want to find what will be the plane X-axis if this axis is as near as possible to the
    // world X axis.

    vec3 worldX = vec3(1,0,0);
    vec3 axisX  = normalize(worldX - dot(worldX, planeNormal) * planeNormal);

    // We then find the plane Y-axis thanks to the cross-product
    // properties with orthonormal basis
    vec3 axisY  = normalize(cross(planeNormal, axisX));

    // Now, find the coordinate of the input point according to this texture coordinate frame
    vec3 diff = pt - planePos; 
    float u = dot(diff, axisX);
    float v = dot(diff, axisY);

    // Finally, apply the checkboard pattern by using this very concise formula:
    return mod(floor(u * 0.5) + floor(v * 0.5), 2.0) < 1.0  ? planeCol1 : planeCol2;
}

//----------------------------------------------------------------------------------------------

vec3 getObjectColorAtPoint(int objectId, vec3 pt, out Material objectMat)
{
    if (objectId == sphereId)
    {
        objectMat = sphereMat;
        return getSphereColorAtPoint(pt);
    }
    else if (objectId == planeId)
    {
        objectMat = planeMat;
        return getPlaneColorAtPoint(pt);
    }

    return skyCol;
}

//----------------------------------------------------------------------------------------------

float getShadowAtPoint(in vec3 I, in vec3 normalI, in Material objectMat, in vec3 L, in float Ldist)
{
    int object;
    vec3 vec1, vec;
    float dist = computeNearestIntersection(I, L, object, vec1, vec);
    if (dist<Ldist && dist>0.0) {
        return 0.0;
    } else {
        return 1.0;
    }
}

//----------------------------------------------------------------------------------------------

vec3 computePhongShading(vec3 objectCol, Material objectMat, vec3 N, vec3 L, vec3 R, vec3 V, float shadowFactor)
{
    // Phong shading is simplification of the 'Rendering equation' of Kajiya.
    //
    // It consists:
    //  - in removing all diffuse inter-reflections and to replace them by one ambiant component
    //  - in assuming the BRDF of material is the addition of a purely diffuse (mat, lambertian)
    //    surface, plus a perfectly specular (mirror-like) component.

    // NOTES:
    //  - The diffuse color is a mix of the light color and the object color.
    //    It is logical as a light source has a spectrum that is absorbed by the object surface.
    //    It is, then, the resulting spectrum that is diffused in all direction.
    //
    //    For example, a red light shining on a 'blue' (under white light) object would appear
    //    black, because a 'blue' object is blue because it absorbs all wavelength except the
    //    blue one that ends up diffusely reflected off the surface towards the observer.
    //    Hence, the red spectrum part of the light would be absorbed by the object, leaving
    //    nothing to be reflected.
    //
    //    So, we would thing the diffuse shading equation would be something like:
    //            factor * max(lightCol - (1-objectCol), 0)
    //               ; where 1-objectCol represents the color absorbed by th object.
    //                 and max(lightCol - (1-objectCol), 0) would then represents the
    //                 color remaining after absorbption.
    //    But that is not what Phong shading is!
    //
    //  - The diffuse ammount depends first on the properties of the object to be 'mat', and
    //    it's controlled with object Kd parameter.
    //
    //    It also depends on how 'frontward' the object surface is to the light.
    //    The more 'frontward' we are, the more light the surface receive, and will then diffuse
    //    in every direction of the hemisphere at the surface point.
    //    This is expressed by the cosinus of the angle between the normal and the light vector,
    //    which is expressed by the dot-product between those two vectors.
    //
    //    When the angle is >= 90° the light doesn't shine anymore on the point, so the
    //    contribution of this light is zero!
    //    To take that into account, and avoid negative value of the cosinus, we need to clamp
    //    its value in [0,1] range. This is done by taking the max between 0 and the cosinus.
	//
    //  - The ambiantCol parameter is the most 'unrealistic' parameter because if not set
    //    properly, you can obtain a color that is totaly irrelevant of the light sources
    //    and the surrounding diffuse objects, and more importantly the object's color itself!
    //    Logically, the ambiant should behave like a diffuse lighting, and should be a mix
    //    of the ambiant color and the object color.
    //    But that is not what Phong shading is!
    //
    //    A good use is to set Ka to be a small value, and to set the ambiant color to a
    //    color that is mainly the color of the light and a bit of the overall color of the
    //    entire scene.
    //
    //    An improvment would be to replace the Ka and ambiantCol by a single ambiant component
    //    associated to each object.
    //    It would enable to emulate the reddish aspect of an object near a red wall.
    //    Plus, the color would be blended with the object color.
    //    We could even add a directional & distance aspect to better emulate inter-reflections.
    //    But that would not be Phong shading anymore! :-)
    //
    //  - The specular works quite like the diffuse but the resulting color depends only on the
    //    light color because specularity comes from light that did not interact much with the
    //    micro-geometry of the object surface, hence the reflected color does not depend on the
    //    object color.
    //
    //  - The specular component in Phong shading is a cheat!
    //    As the light source is a point, the perfectly specular reflection would produce a
    //    single dot (if not nothing because of aliasing).
    //
    //    To give the illusion of a light that has a size, Phong applies a kind of diffuse
    //    principle that relate the reflection direction (from the light that bounces off the
    //    object surface) and the observer viewing direction.
    //    The nearer those vectors are (the smaller the angle in-between) the more reflected
    //    light the observer is receiving.
	//
    //    To control how big the light spot appears (the pseudo light-size is), a power is
    //    applied on the cosinus of the angle (like the diffuse, it must remain in [0,1] range).
    //    The higher the value, the smaller the spot will appears.

    vec3 ambiant  = objectMat.Ka * ambiantCol;
    vec3 diffuse  = objectMat.Kd *  objectCol * lightCol *     max(dot(N,L), 0.);
    vec3 specular = objectMat.Ks *              lightCol * pow(max(dot(R,V), 0.), objectMat.Kn);

    vec3 phongCol = ambiant + shadowFactor*diffuse + shadowFactor*specular;

    return phongCol;
}

