void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;

    animateScene(time);


    // Compute the ray to be casted through the pixel towards the 3D scene
    vec3 rayPos, rayDir;
    computeCameraRayFromPixel(fragCoord, rayPos, rayDir);

	// Test ray-objetcs intersections and find the nearest one
    // (with its associated intersection point and normal at the object surface)
    int  objectId;
    vec3 intersecI, normalI;
    float distI = computeNearestIntersection(rayPos, rayDir, objectId, intersecI, normalI);

    // Apply the shading to the points that are on the sphere surface and seen by the camera
    if (distI > 0.0)
    {
        // unit-vector going from the surface point toward the light
	    vec3 L = normalize(lightPos - intersecI);

        // unit-vector of the reflection direction of the light at the surface point
    	vec3 R = 2.0 * dot(normalI, L) * normalI - L;

        // unit-vector going from the surface point toward the camera 
        vec3 V = -rayDir;

        // Get the diffuse color at the intersection point
        Material objectMat;
        vec3 objectCol = getObjectColorAtPoint(objectId, intersecI,objectMat);

        vec3 I = intersecI + normalI*eps;

        float Ldist = length(lightPos-I);

        float shadowfactor = getShadowAtPoint(I, normalI, objectMat, L, Ldist);
        //float shadowfactor = 1.0;
        // Apply the Phong shading to compute the color
        // of the surface point as seen from the camera
    	fragColor = vec4(computePhongShading(objectCol, objectMat, normalI, L, R, V, shadowfactor), 1);
    }
    else	// We did not hit the sphere, so we have the sky color (here: black)
     	fragColor = vec4(skyCol, 1);
}

#define _FoV 45.0
#define _Iterations 16
#define _Esc 2.0
#define MAX_STEPS 512
#define MIN_DIST 0.00065
#define _Eps 0.4
#define _TextureScale 1.0
#define _SpecularExp 100.0
#define _PI 3.14159265

//This exponent determines the order of the generated fractal
//Most people seem to like the 8th order but the quadratic and cubic ones are interesting as well
#define _Exp 8.0

#define AUTOROTATE

//Ray-sphere intersection test
bool raycastSphere(vec3 ro, vec3 rd, vec3 center, float r)
{
    float A = 1.0; //dot(rd, rd);
    float B = 2.0 * (rd.x * (ro.x - center.x) + rd.y * (ro.y - center.y) + rd.z * (ro.z - center.z));
    float C = dot(ro - center, ro - center) - (r * r);

    float D = B * B - 4.0 * A * C;
    if (D < 0.0)
    {
        return false;
    }
    else
    {
        return true;
    }
}

//Y-axis rotation matrix
vec3 rotateY(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    return rot * p;
}

//Distance estimator
float map (vec3 pos)
{   
    vec3 z = pos;
    float dr = 1.0;
    float r = 0.0;

    for (int i = 0; i < _Iterations; i++)
    {
        r = length(z);

        if (r > _Esc)
            break;

        //Convert to spherical coords and apply phase shift
        float phase = iTime * 0.2;
        float theta = acos(z.z/r) + phase;
        float phi = atan(z.y, z.x);

        dr = pow(r, _Exp - 1.0) * _Exp * dr + 1.0;        
        float zr = pow(r, _Exp);
        theta *= _Exp;
        phi *= _Exp;

        //Convert back to cartesian coords
        z = zr * vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));

        z += pos;
    }

    return _Eps * log(r) * r/dr;
}

//Approximate surface normal using central difference
vec3 calculateNormal(vec3 p)
{
    const float eps = 0.001;

    return normalize(vec3(map(p + vec3(eps, 0, 0)) - map(p - vec3(eps, 0, 0)),
                          map(p + vec3(0, eps, 0)) - map(p - vec3(0, eps, 0)),
                          map(p + vec3(0, 0, eps)) - map(p - vec3(0, 0, eps))));
}

vec3 raymarch (vec3 pos, vec3 dir)
{
    for (int i = 0; i < MAX_STEPS; i++)
    {
        float dist = map(pos);

        if (dist < MIN_DIST)
            return pos;

        pos += dist * dir;
    }

    return vec3(0.0);
}

//iq's soft shadow method
float softShadow(vec3 ro, vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    int i = 0;
    for( float t=mint; t < maxt && i < 128; )
    {
        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
        i++;
    }
    return res;
}

//Tri-planar texture mapping
vec3 triPlanar(vec3 p, vec3 n, float k)
{
    vec2 xUV = p.zy/_TextureScale;
    vec2 yUV = p.xz/_TextureScale;
    vec2 zUV = p.xy/_TextureScale;

    vec3 colX = texture(iChannel0, xUV).rgb;
    vec3 colY = texture(iChannel0, yUV).rgb;
    vec3 colZ = texture(iChannel0, zUV).rgb;

    vec3 blend = pow(abs(n),vec3(k));
    blend = blend/(blend.x + blend.y + blend.z);

    return colX * blend.x + colY * blend.y + colZ * blend.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef AUTOROTATE
    	float rot = iTime * 0.15;
    #else
        float rot = (iMouse.x/iResolution.x) * 2.0 * _PI;
    #endif

    vec3 rayOrigin = vec3(0.0, 0.0, -5.5 + (5.5 * (iMouse.y/iResolution.y)));

    //Compute perspective correct vector from field of view angle
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan(radians(_FoV/2.0));    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));

    vec3 col = vec3(1.0,0.6,0.4) * smoothstep(0.8,1.1,dot(rayDir, vec3(0.0,0.0,1.0)));

    rayDir = rotateY(rayDir, rot);
    rayOrigin = rotateY(rayOrigin, rot);

    //Bounding sphere test
    if (raycastSphere(rayOrigin, rayDir, vec3(0.0), 1.5))
    {

        vec3 sp = raymarch(rayOrigin, rayDir);

        //Only perform shading on surface points
        if (sp != vec3(0.0))
        {
            vec3 n = calculateNormal(sp);

            //Inverse square attenuation
            vec3 lightPos = vec3(0.0, 0.0, -5.0);
            lightPos = rotateY(lightPos, rot);
            vec3 r = sp - lightPos;
            float intensity = 20.0;
            float atten = 1.0/dot(r, r) * intensity;

            //Diffuse term
            float NdotL = dot(n, -rayDir);

            //Blinn-Phong specular term
            vec3 h = (n-rayDir)/length(n-rayDir);     
            float spec = pow(max(dot(n, h), 0.0), _SpecularExp) * max(0.0, NdotL);

            //Soft shadows
            float d = length(r);
            vec3 dir = r/d;
            float shadow = softShadow(sp, -dir, 0.01, d, 8.0);

            //Ambient light
            vec3 amb = vec3(0.1,0.1,0.1);

            //Scroll the texture in the direction of phase shift. Not perfect but close enough.
            sp.z -= iTime * 0.2;
            col = amb + triPlanar(sp, n, 0.2) * (NdotL + spec) * atten * shadow;
        }
    }

    fragColor = vec4(col,1.0);
}

#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    float r = length(pos);

    float theta = iTime + sin(r * 12.0) * 0.04;
    float cosTheta = cos(theta);
    float sinTheta = sin(theta);
    mat2 m = mat2(cosTheta, -sinTheta,
                  sinTheta, cosTheta);

    pos = m * pos;

    float angle = atan(pos.y, abs(pos.x));

    vec2 uv = vec2(r - iTime, angle/PI);

    float l = length(uv);

    float t = 25.0;

    float time = iTime;

    float a[3];

    float c = 0.0;

    for (int i = 0; i < 3; i++)
    {

    float s = l;

    float tuv = uv.x;
    uv.x = uv.x - uv.y;
    uv.y = uv.y +tuv;

    s += (sin(uv.x * t * 0.2));

    s += (sin(uv.y * t * 0.2));

    float m = mod(s + (time + c) * 0.2, 0.5);

    //m *= mod(r - iTime * 0.2, 0.5);

    a[i] = m;    

    c += sin(iTime);

    }

    vec3 col = vec3(a[0], a[1], a[2]);

    col *= r;
    col = 1.0 - exp(-col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Sonic Fusion by Martijn Steinrucken aka BigWings - 2019
// Email:countfrolic@gmail.com Twitter:@The_ArtOfCode
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// What you get when you play with the inside of a torus for too long.
// Originally inspired by: https://turtletoy.net/turtle/90e6288a6b
// 
// Turns out that if music is groovy enough, it can cause fusion! ;)
// Use mouse to scrub time.
//
// Music: Daft Punk - Beyond (Nicolas Jaar Feat Dave Harrington Remix)
// https://soundcloud.com/mamulashvili1/daft-punk-beyond-nicolas-jaar-feat-dave-harrington-remix

#define MAX_STEPS 50.
#define SURF_DIST .001
#define SIN(x) (sin(x)*.5+.5)
#define COS(x) (cos(x)*.5+.5)
#define S(x) smoothstep(0.,1.,x)
#define PI 3.1415
#define HPI 1.5708

float bigRadius = 1.;
float smallRadius = .8;

float Fade(float a, float b, float c, float d) {
    float t = mod(iTime,315.); // iChannelTime[0];   // doesn't work on some systems ?
	return smoothstep(a, b, t)*smoothstep(d, c, t);
}

vec3 R(vec2 uv, vec3 p, vec3 l, vec3 up, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(up, f)),
        u = cross(f, r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}


float GlowDist(vec3 p) {
    float x = atan(p.x, p.z)+PI;
    float fft = texture(iChannel0, fract(vec2(x/6.2831+.5)), 1./512.).x;

    p.y += (fft-.5)*.4;

    float s = 1.+sin(x*4.+iTime)*.05;
    float circle = length(vec2(length(p.xz*s)-bigRadius, p.y));
	float d = (circle+smallRadius*.001);
    return d;
}

float D(vec3 p) {
    float circle = length(vec2(length(p.xz)-bigRadius, p.y));
	float d = -(circle-smallRadius);
    return d;
}

vec3 N(vec3 p, float eps) {
	vec2 e = vec2(eps,0);
    return normalize(
        vec3(
           D(p+e.xyy)-D(p-e.xyy),
           D(p+e.yxy)-D(p-e.yxy),
           D(p+e.yyx)-D(p-e.yyx)
        )
    );
}

vec2 TV(vec3 p) {
    float y = atan(length(p.xz)-bigRadius, p.y);
	float x = atan(p.x, p.z);
    return vec2(x, y); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m = iMouse.xy/iResolution.xy;
    vec2 UV = fragCoord/iResolution.xy;
	vec2 uv = (UV-.5);
    uv.x *= iResolution.x/iResolution.y;

   	float t = iTime*2.-m.x*100.;	// can scrub 50 seconds with mouse
    float t4 = t/4.;
    float t8 = t/8.;
    float t16 = t/16.;
    float t32 = t/32.;

    float fft = texture( iChannel0, vec2(.7,0) ).x;

    smallRadius = mix(.125, .8, S(SIN(t8)));

    // screen twist
    float d = 1.-dot(uv,uv);
    float twist = Fade(0., 100., 200., 300.);
    uv *= Rot(d*2.*twist*sin(t16)*(1.-COS(t32)));

    // torus cam setup
    vec3 ro = vec3(0., sin(t8)*.25*smallRadius, -bigRadius-sin(t4)*smallRadius*.3);
    float lookAngle = ((SIN(-t8)));
    vec3 lookDir = vec3(0, 0, 1);
    lookDir.xz *= Rot(lookAngle*HPI);
    float fov =  mix(.5, .1+COS(t32), Fade(30.,60., 250., 300.));      
    vec3 rd = R(uv*Rot(t*.1), ro, ro+lookDir, vec3(0,1,0), fov);

    // Ray march
    float dO = 0.;
    float minDistC = 5.;
    for(float i=0.; i<MAX_STEPS; i++) {
    	vec3 p = ro + dO*rd;
        float dS = D(p);
        float dC = GlowDist(p);

        minDistC = min(minDistC, dC);

        dO += min(dS, dC);
        if(dS<SURF_DIST) break;
    }

    vec3 col = vec3(0);

    // get pos, normal and torus uvs
    vec3 p = ro + dO*rd;
 	vec3 n = N(p,.01);
    vec2 tv = TV(p)/6.2831+.5;

    // Texture
    tv.y += t*.05*Fade(143.5, 143.51, 220.,230.);
    d = fract((tv.y+tv.x)*10.);
    float outline = smoothstep(.3, .28, abs(d-.5));
    float swirl = smoothstep(.25, .2, abs(d-.5));
    float hatchUv = (tv.y-tv.x)*10.*3.1415;
    float hatch = sin(hatchUv*20.+t4)*SIN(t4);
    hatch += sin(hatchUv+t)*SIN(t8);
    col += (outline-swirl*hatch)*.5;

    // vignette
    col *= 1.-dot(uv,uv);

    // color inversion
    float bw = 1.-clamp(sin(length(uv)*1.-t*.1)*3., 0., 1.);
    col = mix(col, 1.-col, bw);

    // add more 3d-ness
    float fresnel = -dot(n, rd);
    fresnel *= fresnel;
    col *= mix(1., fresnel*fresnel, SIN(t8*.5674));


    // Add some color
    vec3 c = cross(rd, n);
    c.xy *= Rot(t);
    c.zy *= Rot(t4);
    c = c*.5+.5;
    c = pow(c, vec3(1.+SIN(t4)*3.));
    float tasteTheRainbow = Fade(82., 83., 100., 120.);
    tasteTheRainbow += Fade(143.5, 144.5, 170., 200.);
    tasteTheRainbow += Fade(205., 206., 230., 260.);
    col = mix(col, col*c*3., tasteTheRainbow);

    // add lightning
    float lightning = fft*(.1/(minDistC));
    vec3 lightningCol = lightning*mix(vec3(1.,.1,.1), vec3(.1, .1, 1.), bw);
    float fade = Fade(21., 25., 50., 70.);
    fade += Fade(82., 83., 100., 120.);
    fade += Fade(143.5, 144.5, 170., 200.);
    fade += Fade(215., 216., 230., 260.);

    col += lightningCol * fade;

    col *= Fade(0., 10., 290., 315.);

    // preview window gets the party right away
    if(iResolution.x<300.) {
       col = col*c*3.+lightningCol;
    }

    fragColor = vec4(col,1.0);
}

#define S(v,r,e) smoothstep(-.5*e/R.y, .5*e/R.y,  abs( fract(v)- .5) -r/2. )

void mainImage( out vec4 O, vec2 u )
{
	vec2  R = iResolution.xy,
          U = ( u*2. - R ) / R.y;

    float r = length(U) * 10.,
      barId = ceil(r),
        TAU = 6.2831852,
          a = atan(U.y, U.x)/TAU + iTime * barId * .05 ,
      inBar = 1.- S( r, .8, 40. ),
  arcLength = .5 + .4*sin(iTime*.1), 
      inArc = S( a-arcLength/2.+.5, arcLength, 8./r );

    O = vec4(1.-inBar*inArc);
}

// An attempt to express 'the symbol' as a Distance Field using some simple shapes and modifiers...
//
// I suck at modelling!

#define PI 3.141592
#define	TAU 6.28318

mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// simple bend(Y) mod
vec2 opBendTest( vec2 p, float angle, float xmod )
{
    p.x += xmod;
    p = rotate( angle * p.x ) * p.xy;
    p.x -= xmod;
    return p;
}
// simple spriral(x) mod
float spiral(vec2 p, float sa, float b)
{
  float a = atan(p.y, p.x);
  float l = length(p);
  float n = (log(l/sa)/b - a) / (2.*PI);
  float upper_ray = sa * exp(b *(a + 2.*PI*ceil(n)));
  float lower_ray = sa * exp(b *(a + 2.*PI*floor(n)));
  return min(abs(upper_ray - l), abs(l-lower_ray));
}

// 2D-shapes (Trapezoid, Ring, Box, Triangle)
float dot2(in vec2 v ) { return dot(v,v); }
float sdTrapezoid( in vec2 p, in float r1, float r2, float he )
{
    vec2 k1 = vec2(r2,he);
    vec2 k2 = vec2(r2-r1,2.0*he);

	p.x = abs(p.x);
    vec2 ca = vec2(p.x-min(p.x,(p.y < 0.0)?r1:r2), abs(p.y)-he);
    vec2 cb = p - k1 + k2*clamp( dot(k1-p,k2)/dot2(k2), 0.0, 1.0 );

    float s = (cb.x < 0.0 && ca.y < 0.0) ? -1.0 : 1.0;

    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float ring(vec2 uv, float rad, float thickness)
{
    return abs(rad - length(uv)) - thickness;
}

float sdBox( in vec2 p, in vec2 b ) 
{
    vec2 q = abs(p) - b;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}    

// signed distance to a 2D triangle
float sdTriangle( in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

// 1xTriangle, 4xTrapezoid, 3xBox, 1xRing
// 1xBend distort modifier
// 1xSpiral distor modifier
// 5xCircle cuts
float NewSymbol(vec2 uv)
{
    vec2 p = uv+vec2(0.27,0.09);
	float s1 = length(p)-0.2;						// circle1
  	float s2 = length(p+vec2(-0.22,-0.01))-0.18;	// circle2
	p.x = spiral(p.xy, PI*0.5, -0.33);				// Spiral distort
    float d = sdTrapezoid(p,0.015,0.015,0.08);		// could just be a box...
	d = max(d, s1);									// subtract circle
	d = max(d,-s2);									// subtract circle

    p = vec2(abs(uv.x),uv.y);
    //	d = min(d,sdTriangle(vec2(-0.155,0.1),vec2(-0.055,0.1),vec2(-0.105,0.05),p.yx)); // mid-cross tri
    d = min(d, sdTrapezoid(p.yx+vec2(0.105,-0.075),0.0,0.05,0.025));   			// mid-cross trapeziod!
    d = min(d,sdBox(p+vec2(-0.05,0.105),vec2(0.025,0.025)));						// mid-cross bar
    d = min(d,sdTriangle(vec2(0.0, -0.425),vec2(0.1, -0.25),vec2(-0.025, -0.35),p.xy)); // bottom triangle

	p = opBendTest(uv+vec2(0.16, -0.088),radians(77.0),-0.103);
    d = smin(d, sdTrapezoid(p.yx,0.018,0.025,0.19), 0.012); // bent arm (attempted to smooth the join, needs work)

    d = min(d,ring(uv+vec2(0.0,-0.26),0.13,0.028));			// top-ring
    d = min(d,sdBox(uv+vec2(0.0,0.13),vec2(0.032,0.2)));	// main body
    d = min(d,sdBox(uv+vec2(-0.08,-0.085),vec2(0.09,0.025))); // horn1 (bar)

    float cuts = length( uv+vec2(-0.17,-0.32))-0.21;
    cuts = min(cuts,length( uv+vec2(-0.17,0.15))-0.21);
    cuts = min(cuts,length( uv+vec2(-0.73,-0.085))-0.4);
    p = uv+vec2(-0.26,-0.085);
    d = min(max(sdTrapezoid(p.yx,0.025,0.13,0.09),-cuts),d); // horn2 (cutout)
    return d;
}

float map(vec3 p)
{
    float time = iTime+0.2;
    p.z -= 1.5;

    float twist = 0.5+sin(fract(time*0.4)*TAU)*0.5;
    twist *= p.y * 1.5;
    p.xz *= rotate(twist+fract(time*0.26)*TAU);

    float dist = NewSymbol(p.xy);

    float dep = 0.02;
    vec2 e = vec2( dist, abs(p.z) - dep );
    dist = min(max(e.x,e.y),0.0) + length(max(e,0.0));
    dist -= 0.01;
    return dist;
}


// Tetrahedral normal, to save a couple of "map" calls. Courtesy of IQ.
vec3 normal( in vec3 p )
{
    // Note the slightly increased sampling distance, to alleviate
    // artifacts due to hit point inaccuracies.
    vec2 e = vec2(0.0025, -0.0025); 
    return normalize(
        e.xyy * map(p + e.xyy) + 
        e.yyx * map(p + e.yyx) + 
        e.yxy * map(p + e.yxy) + 
        e.xxx * map(p + e.xxx));
}

vec3 render(vec2 uv)
{
	vec3 ro = vec3(0.0, 0.0, 0.0);
	vec3 rd = normalize(vec3(uv, 2.95));
	vec3 p = vec3(0.0);
	float t = 0.;
	for (int i = 0; i < 120; i++)
    {
		p = ro + rd * t;
		float d = map(p);
		if (d < .001 || t > 20.) break;
		t += d*0.75;
	}

    vec3 c = vec3(0.35,0.35,0.45);
    c*= 1.2-abs(uv.y);

    if (t<20.0)
    {
	   	vec3 lightDir = normalize(vec3(1.0, 1.0, 0.5));
        vec3 nor = normal(p);

        float dif = max(dot(nor, lightDir), 0.0);
        c = vec3(0.5) * dif;

        float tf = 0.16;
        c += vec3(0.65,0.6,0.25) + reflect(vec3(p.x*tf, p.y*tf,p.z*tf), nor);

        vec3 ref = reflect(rd, nor);
        float spe = max(dot(ref, lightDir), 0.0);
        c += vec3(2.0) * pow(spe, 32.);
    }

    c *= 1.0 - 0.3*length(uv);
	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
	vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 col = render(uv);
	fragColor = vec4(col, 1.);
}

// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float f (vec3 p)
{
    return sdTorus(p,vec2(cos(iTime)+4.5,.5*cos(iTime)+1.1));
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( vec3 p, float t )
{
    float e = 1.e-3*t;
    vec2 h = vec2(1.0,-1.0)*0.5773;
    return normalize( h.xyy*f( p + h.xyy*e ) + 
					  h.yyx*f( p + h.yyx*e ) + 
					  h.yxy*f( p + h.yxy*e ) + 
					  h.xxx*f( p + h.xxx*e ));
}

// https://www.shadertoy.com/view/tsXGRl
vec3 bg (vec2 p)
{    
    vec2 c = step(.1,fract(p*2.25+.05));
    return vec3(c.x*c.y);
}

void mainImage( out vec4 c, in vec2 a )
{
    vec3 o = vec3(0,sin(iTime)+2.5,0);
    vec3 y = vec3(0,1,0);
    vec3 w = normalize(-o);
    if (abs(dot(o,y)) > .999) y = y.xxy;
    vec3 u = normalize(cross(w,y));
    vec2 g = (a-.5*iResolution.xy)/iResolution.x;
	vec3 d = mat3(u,cross(u,w),w) * normalize(vec3(g,.25));
    float t = 0., e = 1.e-4, m = 1.e6, h;
    vec3 p = o;
    while (t < m)
    {
        h = f(p);
        if (h < e) break;
        t = min(m,t+h);
        p = o+d*t;
    }
    vec3 n = t > e && t < m ? calcNormal(p,t) : bg(g);
    c = vec4(n,1);
}

float distToCirc(vec3 rayPos, vec3 rayVec, vec3 planePos, vec3 planeNormal)
{
	float  dist = dot(rayPos - planePos, planeNormal) / dot(rayVec, planeNormal);
	return dist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 pixelColor  = vec4(1.0);  
    vec2 pixelPos    = fragCoord.xy;

    vec2 uv    = fragCoord / iResolution.xy;
    vec3 col   = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    pixelColor = vec4(col * (abs(fragCoord.y - iResolution.y / 2.0)) * 0.005, 1.0);

    vec3 camPos = vec3(iResolution.x / 2.0, iResolution.y / 2.4, 600.0);
    vec3 rayVec = normalize(vec3(pixelPos, 0.0) - camPos);


    vec3 planeNormal = vec3(cos(iTime) / 5.0,  1.0,  0.0);
    float offset = 500.0 * cos(iTime);
    for (int i = -10; i < 10; i ++)
    {
        vec3 planePos = vec3(0.0, iResolution.y + float(i) * 100.0 + offset, -2100.0);

        float dist    = distToCirc(camPos, rayVec, planePos, planeNormal);

        if (dist > 0.0)
        {
            vec3 point = rayVec * (abs(dist) + camPos);
            float cDist = distance(point, planePos);
            if (cDist < 1000.0 && cDist > 900.0) pixelColor = vec4(col, 1.0);
        }
    }



    fragColor = pixelColor;
}

// Forked from the amazing shader by mhnewman: https://www.shadertoy.com/view/XlXGD7

//	My first contribution to Shadertoy
//	I have been a big fan of this community for a while and I want to thank iq for
//	this wonderful site.
//	Hash functions from David Hoskins via https://www.shadertoy.com/view/4djSRW

const mat2 m = mat2(1.616, 1.212, -1.212, 1.616);

float hash12(vec2 p) {
	p = fract(p * vec2(5.3983, 5.4427));
    p += dot(p.yx, p.xy + vec2(21.5351, 14.3137));
	return fract(p.x * p.y * 95.4337);
}

vec2 hash21(float p) {
	vec2 p2 = fract(p * vec2(5.3983, 5.4427));
    p2 += dot(p2.yx, p2.xy +  vec2(21.5351, 14.3137));
	return fract(vec2(p2.x * p2.y * 95.4337, p2.x * p2.y * 97.597));
}

float noise(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
	vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(mix(hash12(i + vec2(0.0, 0.0)), 
                   hash12(i + vec2(1.0, 0.0)), u.x),
               mix(hash12(i + vec2(0.0, 1.0)), 
                   hash12(i + vec2(1.0, 1.0)), u.x), u.y);
}

float hash12_3(vec2 p) {
	float f = hash12(p);
    return f * f * f;
}

float noise_3(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
	vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(mix(hash12_3(i + vec2(0.0, 0.0)), 
                   hash12_3(i + vec2(1.0, 0.0)), u.x),
               mix(hash12_3(i + vec2(0.0, 1.0)), 
                   hash12_3(i + vec2(1.0, 1.0)), u.x), u.y);
}

float fbm(vec2 p) {
    float f = 0.0;
    f += 0.5 * noise(p); p = m * p;
    f += 0.25 * noise(p); p = m * p;
    f += 0.125 * noise(p); p = m * p;
    f += 0.0625 * noise(p); p = m * p;
    f += 0.03125 * noise(p); p = m * p;
    f += 0.015625 * noise(p);
    return f / 0.984375;
}

vec3 getDir(vec2 screenPos) {
    screenPos -= 0.5;
	screenPos.x *= iResolution.x / iResolution.y;

    return normalize(vec3(0.0, -1.0, -3.0)
                     + screenPos.x * vec3(1.0, 0.0, 0.0)
                     - screenPos.y * vec3(0.0, -0.948683298, 0.316227766));
}

bool getPosition(in vec3 camera, in vec3 dir, out vec2 pos) {
    bool valid = false;

	float b = dot(camera, dir);
	float c = dot(camera, camera) - 1.0;
	float h = b * b - c;
	if (h > 0.0) {
        valid = true;

        vec3 p = camera + (-b - sqrt(h)) * dir;
        pos = p.xz + iTime * vec2(0.005, 0.02) + vec2(-iMouse.x, iMouse.y) / 1000.0;        
	}

	return valid;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 screen = fragCoord.xy / iResolution.xy;

    vec3 camera = vec3(0.0, 1.2, 0.7);
    vec3 dir = getDir(screen);

    vec3 earth = vec3(0.0, 0.0, 0.0);
    vec2 position;
    if (getPosition(camera, dir, position)) {
        float geography = fbm(6.0 * position);

        float coast = 0.2 * pow(geography + 0.5, 50.0);
        float population = smoothstep(0.2, 0.6, fbm(2.0 * position) + coast);
        vec2 p = 40.0 * position;
        population *= (noise_3(p) + coast); p = m * p;
        population *= (noise_3(p) + coast); p = m * p;
        population *= (noise_3(p) + coast); p = m * p;
        population *= (noise_3(p) + coast); p = m * p;
        population *= (noise_3(p) + coast);
        population = smoothstep(0.0, 0.02, population);

        vec3 land = vec3(0.1 + 2.0 * population, 0.07 + 1.3 * population, population);
        vec3 water = vec3(0.0, 0.05, 0.1);
        vec3 ground = mix(land, water, smoothstep(0.49, 0.5, geography));

        vec2 wind = vec2(fbm(30.0 * position), fbm(60.0 * position));
        float weather = fbm(20.0 * (position + 0.03 * wind)) * (0.6 + 0.4 * noise(10.0 * position));

        float clouds = 0.8 * smoothstep(0.35, 0.45, weather) * smoothstep(-0.25, 1.0, fbm(wind));
        earth = mix(ground, vec3(0.5, 0.5, 0.5), clouds); 

        float lightning = 0.0;
        vec2 strike;
        if (getPosition(camera, getDir(hash21(iTime)), strike)) {
            vec2 diff = position - strike;
            lightning += clamp(1.0 - 1500.0 * dot(diff, diff), 0.0, 1.0);
        }
        lightning *= smoothstep(0.65, 0.75, weather);
        earth += lightning * vec3(1.0, 1.0, 1.0);
    }

    vec3 altitude = camera - dir * dot(camera, dir);
    float horizon = sqrt(dot(altitude, altitude));

    vec3 atmosphere = vec3(0.2, 0.25, 0.3);
    atmosphere = mix(atmosphere, vec3(0.05, 0.1, 0.3), smoothstep(0.992, 1.004, horizon));
    atmosphere = mix(atmosphere, vec3(0.1, 0.0, 0.0), smoothstep(1.0, 1.004, horizon));
    atmosphere = mix(atmosphere, vec3(0.2, 0.17, 0.1), smoothstep(1.008, 1.015, horizon));
    atmosphere = mix(atmosphere, vec3(0.0, 0.0, 0.0), smoothstep(1.015, 1.02, horizon));

    horizon = clamp(pow(horizon, 20.0), 0.0, 1.0);
    fragColor = vec4(mix(earth, atmosphere, horizon), 1.0);
}

//I made some tweaks to catafest's simple flame shader for fun 
//Original can be found here: https://www.shadertoy.com/view/3sX3RX

//I wanted to preserve his simplicity, but make it a little more realistic while adding parameters that could 
//be easily tweaked for clarity

//Bloom brightness
#define Bloom 0.1
//Vertical bobbing caused by candle impurities
#define VerticalMotion 0.01
//Horizontal paremeter that simulates small air currents
#define HorizontalMotion 0.35

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv;
    float aspect = iResolution.y / iResolution.x;
    nuv.x *= aspect;

	vec2 pos = uv * 2.0-vec2(1.,1.);
    // create horizontal flame variation
    pos.x += sin(iTime*0.1) * uv.y * (sin(iTime - uv.y) + cos((iTime - uv.y) * 0.1)) * 0.5 * HorizontalMotion;

    // create vertical flame variation 
    pos.y += VerticalMotion * fract(sin(30.0*iTime)) + VerticalMotion * sin(iTime);

    // select background to black
	vec3 color = vec3(0.0,0.0,0.0);

	// set scale of flame 
	float p =.001;

    // create shape of flame (output y)
	float y = pow(abs(pos.x), 3.0 + cos(iTime)*0.1)/(1.0*p)*1.0;

	// create the hight of flame 
	float flame_out = length(pos+vec2(pos.x,y)) * 0.78332; // 0.78332 = sin(0.9)

	// fix colors flame by RGB
	color.rg += smoothstep(0.0,0.3,0.6-flame_out);

    // fix color of flame by G (green)
	color.g /=2.4;

    //add slight blue to the base of the flame
    color.b -= 0.2 * pos.y / flame_out;

    //Add bloom
    color.rg += smoothstep(0.0, 10.1, 1.0 / distance(nuv, vec2(pos.x+0.25, (pos.y*0.1+0.4)))) * Bloom;
    color.r += smoothstep(0.0, 5.1, 1.0 / distance(nuv, vec2(pos.x+0.25, (pos.y*0.1+0.5)))) * Bloom;

    // output color
	color += pow(color.r,1.0);
    fragColor = vec4(color,1.0);
}

