#ifdef GL_ES
precision mediump float;
#endif

const float dMax = 10.0;

float rand(vec2 p) {	
	return fract(sin(dot(p, vec2(12.231,64.102))) * 63360.01203);
}

float noise(vec2 p) {
    float A = rand(vec2(floor(p.x), floor(p.y)));
    float B = rand(vec2(floor(p.x) + 1.0, floor(p.y)));
    float C = rand(vec2(floor(p.x), floor(p.y) + 1.0));
    float D = rand(vec2(floor(p.x) + 1.0, floor(p.y) + 1.0));

    float fc = fract(p.x);
    float bicubicc = fc * fc * (3.0 - 2.0 * fc);

    float fr = fract(p.y);
    float bicubicr = fr * fr * (3.0 - 2.0 * fr);

    float AB = mix(A, B, bicubicc);
    float CD = mix(C, D, bicubicc);

    float final = mix(AB, CD, bicubicr);

    return final;
}


float heightmap(vec3 p) {


    float dMin = dMax; // nearest intersection
    float d; // depth
    float material = -1.0; // material ID

    // adding octaves of height
    float h = 0.0; // height
    float w = 0.5; // octave weight
    float m = 0.4; // octave multiplier

    //iterating through 10 octaves of height
    for (int i=0; i < 9; i++) {
        h += w * noise(p.xz * m);
        w *= 0.5;
        m *= 2.0;
    }


    //makes a more mountainous look
    h += smoothstep(0.3, 1.0, h);

    d = p.y - h;
    if (d<dMin) { 
        dMin = d;
        material = 0.0;
    }

    return dMin;
}




float raymarch(vec3 ro, vec3 rd) {
    const float marchDist = 0.001; // precision
    float currHeight = 0.0; // distance
    float nextStep = marchDist * 2.0; // step

    for (int i=0; i < 36; i++) {
        if (abs(nextStep)> marchDist) {
            currHeight += nextStep; // next step
            float pos = heightmap(ro + rd * currHeight); // next intersection
            nextStep = pos; // distance

        } else {
            break;
        }
    }
    return currHeight;
}



const float epsilon = 0.001;
vec2 e = vec2(epsilon, 0.0); // For swizzling
vec3 calcNormal(vec3 p) {
    float x = heightmap(p - e.xyy) - heightmap(p + e.xyy);
    float y = 2.0*epsilon;
    float z = heightmap(p - e.yyx) - heightmap(p + e.yyx);
    return normalize(vec3(x,y,z));
}

vec3 render( vec3 ro, vec3 rd ) {

    //color of the sky
    vec3 color = vec3(0.3,0.4,0.6);
    float height = raymarch(ro, rd);

    vec3 light = normalize(vec3(1.0, 1.0, 0.0));
    vec3 world = ro + rd * height;


    vec3 normal = calcNormal(world); // terrain normals


    // color it like a mountain if it satisfies these heights
    if (height < dMax) {

        // gray undertone of rock color
        color = vec3(102.0) / 255.0;

        float slope = 1.0 - dot (normal, vec3 (0.0, 1.0, 0.0));
        if (slope < 0.05){
    		color = mix(vec3(1.0, 1.0, 1.0), color, 0.15);
    	}

        //phong lighting model
        float ambient = 0.4;
        float diffuse = 2.25 * clamp(dot(normal, light), 0.0, 2.0);
        float specular = pow(clamp(dot(rd, reflect(light, normal)), 0.0, 1.0), 32.0);

        color *= (ambient + diffuse);
    }

    if (height > dMax){
   		float fog = exp(-0.005 * height * height); // exponential fog equation
   		color = mix(color, vec3(1.0), fog); // add fog in distance
    }

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = 2.0*(fragCoord/iResolution.xy) - vec2(1.0);
    uv.x *= iResolution.x/iResolution.y;

    float x = 0.0 + 16.0 + (0.5*iTime);
	float y = 0.0;
	float z = 0.0 + 16.0 + sin(0.1*iTime);
	vec3 rayOrigin = vec3(x, y, z); // position;
    rayOrigin.y = 0.4 * noise((rayOrigin.xz * 0.5)) + 1.5;

    const vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 target = vec3(0.0);
    vec3 look = normalize(rayOrigin - target);


    vec3 cameraForward = -look;
    vec3 cameraRight = normalize(cross(cameraForward, up));
    vec3 cameraUp = normalize(cross(cameraRight, cameraForward));

    vec3 rayDirection = vec3(uv, 1.0);
    rayDirection = normalize(rayDirection.x * cameraRight + rayDirection.y * cameraUp + rayDirection.z * cameraForward);


    vec3 color = render(rayOrigin, rayDirection);
    fragColor = vec4(color, 1.0);
}

vec2 cossin(float r) { return vec2(cos(r), sin(r)); }
mat2x2 rotMat(float r) { 
    vec2 cs = cossin(r);
    return mat2x2(cs.x, -cs.y, cs.y, cs.x);
}
void rotate(inout vec2 p, float r) { p = rotMat(r)*p; }
void translate(inout vec2 p, vec2 offset) { p+=offset; }

float closesedMirror = 100.0;
float totalMirror = 0.0;
float flipCount = 0.0;
void vmirror(inout vec2 p) {
    if(p.y < .0) flipCount += 1.0;
    p.y=abs(p.y);
    totalMirror += p.y;
    closesedMirror=min(closesedMirror,p.y);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void scale(inout vec2 p, float s) { p *= s; }
void mainImage( out vec4 fragColor, in vec2 uv ) {
    uv/=iResolution.xy;
    uv = (uv-.5)*2.0;
    uv.x*=iResolution.x/iResolution.y;
    float angle;

    scale(uv, 1.0);

     angle = atan(uv.y,uv.x);
    float d = length(uv);


    //uv = vec2(cos(uv.x), sin(uv.y));
    //translate(uv, cossin(iTime));


    fragColor.r=(1.0-length(fract(uv+.5)-.5));
   	fragColor.g=cos(.1*iTime*length(floor(uv+.5)))*.2+.2;
    //fragColor.gb=fract(uv);


    //*
    scale(uv, 3.0);
	float max_itr = 100.0;
    float itr = max_itr;
    vec3 colorScale = vec3(3,4,5) * itr *.2;

    vec2 center = vec2(0,0);
     angle = 0.0;
    while(itr-->0.0) {
        angle = fract(angle/3.1415926535)*3.1415926535;
        center = cossin(angle);
        angle += iTime/max_itr;


	    // move mirror center to center to (0,0)
	    translate(uv, -center);
	    rotate(uv, angle);

        vmirror(uv);
	    rotate(uv, -angle);
	    translate(uv, center);
    }

    // coloring 
    uv = fract(uv);
    float K = .2;

    float bloom = pow(totalMirror + .0, -K);
    float mirror_dist = closesedMirror*max_itr;
    float cell_color = abs(cos(flipCount));

    fragColor = vec4(0);
    fragColor.r = fract(bloom);

    fragColor.g = fract(bloom*.1);
    fragColor.b = fract(bloom*.01);

    float base = iTime*.1;
    float hue = base+ sin(cell_color*iTime)*.2;
    fragColor.rgb = hsv2rgb(vec3(hue, bloom, mirror_dist*.1));

}

float Band(float t, float start, float end,float blur){
    float step1 = smoothstep (start - blur, start + blur, t);
    float step2 = smoothstep( end + blur, end-blur,t);
    return step1 * step2;
}

float Rectangle(vec2 uv, float blur, float left, float right,float bottom, float top){
    float r;
    r = Band(uv.x, left, right, blur);
    r *= Band(uv.y, bottom,top,blur);
    return r;
}

float Square( vec2 uv, float blur, float point) {
