////////////////////////////////////////////////////////////////////////////////
//
// Created 2019 by Matthew Arcus
//
// Simple 2D Voronoi. Examine the 16 cells nearest to the nearest grid point.
//
// For drawing the lines, take the closest point and find minimum distance to
// the bisector of that point and all the other grid points examined (we store
// the point from the first pass in an array). This seems to give a correct
// distance function to the cell borders. An approximate but satisfactory
// solution is to just keep the closest N points.
//
// Mouse-x: zoom
// Mouse-y: line width
// 'g': show grid
// 'i': show isolines
// 'm': monochrome
// 'p': show cell points
// 'q': show glitches
// 'v': show voronoi cells
// 'w': wrap grid
//
////////////////////////////////////////////////////////////////////////////////

// Determine how far outside the cell the cell point may go.
// Too far and we get glitches where the computed nearest cell
// boundary is not in fact the closest (see with 'q' key).
const float K = 0.6;  // Point offset limit. 0.5 = the cell itself
const float gridsize = 3.0;

bool alert = false;

void assert(bool t) {
  if (!t) alert = true;
}

// Wang Hash, used to seed RNG
uint ihash(uint a) {
  a = (a ^ 61U) ^ (a >> 16);
  a *= 9U;
  a = a ^ (a >> 4);
  a = a * 0x27d4eb2dU;
  a = a ^ (a >> 15);
  return a;
}

// Note that we convert from float to integer to unsigned
uint ihash(ivec2 p) {
  uint i = uint(p.x), j = uint(p.y);
  return ihash((i << 16) ^ j);
}

// xorshift from George Marsaglia, http://www.jstatsoft.org/v08/i14/paper,
// via https://www.shadertoy.com/view/tsf3Dn from zeh.
uint xorshift(uint value) {
  // Xorshift*32
  value ^= value << 13;
  value ^= value >> 17;
  value ^= value << 5;
  return value;
}

uint seed = 1U;
uint rng() {
  //seed = lcg(seed);
  seed = xorshift(seed);
  return seed;
}

// Uniform random float in [0,1)
float rand() {
  return float(rng())/pow(2.0,32.0);
}

const int CHAR_G = 71;
const int CHAR_I = 73;
const int CHAR_M = 77;
const int CHAR_P = 80;
const int CHAR_Q = 81;
const int CHAR_V = 86;
const int CHAR_W = 87;

bool keypress(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
  return v * mix( vec3(1.0), rgb, s);
}

float bisectordistance(vec2 pos, vec2 a, vec2 b) {
  vec2 centre = 0.5*(a+b);
  return dot(pos-centre,normalize(a-b));
}

// Returns point for particular cell in the grid
// Also seeds RNG for the cell.
vec2 getcellpoint(vec2 grid, float i, float j) {
  float speed = 0.5;
  float t = speed*iTime;
  vec2 cell = grid + vec2(i,j);
  vec2 mcell = cell;
  if (keypress(CHAR_W)) mcell = mod(mcell,2.0*gridsize);
  seed = ihash(ivec2(mcell)); // Seed RNG for this cell
  vec2 p = vec2(rand(),rand());
  vec2 offset = K+t*p;
  offset = mod(offset,4.0*K);
  offset = min(offset,4.0*K-offset);
  offset -= K;
  return cell+vec2(0.5)+offset;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float scale = 1.0;
  float lwidth = 0.05;

  if (iMouse.x != 0.0) {
    scale *= 2.0*iMouse.x/iResolution.x;
    lwidth *= 2.0*iMouse.y/iResolution.y;
  }
  vec2 pos  = (2.0*fragCoord - iResolution.xy)/ iResolution.y;
  pos *= scale;
  pos *= gridsize;
  vec2 grid = round(pos); // Nearest grid point
  const int NPOINTS = 16;
  vec4 points[NPOINTS];   // vec4(p.x,p.y,distance,color);
  // And iterate through 16 cells around the grid point.
  int pointindex = 0;
  int closest = 0;
  float mindist = 1e8;
  for (float i = -2.0; i <= 1.0; i++) {
    for (float j = -2.0; j <= 1.0; j++) {
      vec2 cellpoint = getcellpoint(grid,i,j);
      float d = distance(cellpoint,pos);
      if (d < mindist) {
        closest = pointindex;
        mindist = d;
      }
      points[pointindex++] = vec4(cellpoint,d,rand());
    }
  }
  vec3 basecolor = vec3(0.8);
  if (!keypress(CHAR_V)) {
    // Look through previously calculated points and find minimum distance
    // from point to cell wall.
    float mindist = 1e8;
    for (int i = 0; i < NPOINTS; i++) {
      if (i != closest) {
        mindist = min(mindist,bisectordistance(pos,points[closest].xy,points[i].xy));
      }
    }
    if (keypress(CHAR_M)) {
      basecolor = vec3(points[closest].w);
    } else {
      basecolor = hsv2rgb(points[closest].w,1.0,1.0);
    }
    if (!keypress(CHAR_I)) basecolor *= 2.0*(mindist+0.1)*(0.5*sin(80.0*mindist)+0.5);
    basecolor = mix(vec3(0.05),basecolor,smoothstep(lwidth,1.25*lwidth,mindist));

    // Check things: for the next layer of cells out, check that the bisector
    // distance isn't closer that the one we have calculated above.
    if (keypress(CHAR_Q)) {
      for (float i = -3.0; i <= 2.0; i++) {
        for (float j = -3.0; j <= 2.0; j++) {
          if (i >= -2.0 && i <= 1.0 && j >= -2.0 && j <= 1.0) continue;
          vec2 cellpoint = getcellpoint(grid,i,j);
          if (distance(cellpoint,points[closest].xy) > 1e-2) {
            float d = bisectordistance(pos,points[closest].xy,cellpoint);
            assert(d >= mindist);
          }
        }
      }
    }
  }

  if (keypress(CHAR_G)) {
    // Draw grid
    vec2 gridpos = pos-floor(pos); // Distance to cell wall
    gridpos = min(gridpos,1.0-gridpos);
    basecolor *= 0.5+0.5*smoothstep(0.04,0.05,min(gridpos.x,gridpos.y));
  }
  if (keypress(CHAR_P)) {
    basecolor *= 0.25+0.75*smoothstep(1.25*lwidth,1.5*lwidth,
                                      distance(pos,points[closest].xy));
  }
  basecolor = sqrt(basecolor);
  if (alert) basecolor.x = 1.0;
  fragColor = vec4(basecolor,1);
}

