#define PI 3.14159265359
#define EPSILON 0.01

struct DirLight
{
    vec3 dir;
    vec3 color;
};

struct Material
{
    vec3 albedo;
    float roughness;
    float metallic;
};

vec3 kLightDir = vec3(0.0, 0.1, 0.0);
float kSSS = 0.25;
float kRoughness = 0.1;

float smin(float d1, float d2, float k)
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}


mat2 rot(float r) { return mat2(cos(r), sin(r), -sin(r), cos(r));}

float box(vec3 p, vec3 s)
{
    vec3 d = abs(p) - s;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float map(vec3 p)
{    
    float s0 = length(vec3(1.0, 1.0, 2.0) * p + vec3(0.5, 0.0, 0.0)) - 0.3;
    float s1 = length(vec3(1.0, 1.0, 2.0) * p + vec3(-0.5, 0.0, 0.0)) - 0.3;
    float t = 0.0;sin(4.0 * p.y + iTime * 3.0) * 0.1;
    float b = box(p, vec3(0.3 + t, 0.3, 0.3)) - 0.05;
    return smin(s0, smin(s1, b, 0.1), 0.1);
}

vec3 norm(vec3 p)
{
    vec2 d = vec2(0.0, 0.01);
    return normalize(map(p) - vec3(
        map(p - d.yxx),
        map(p - d.xyx),
        map(p - d.xxy)
    ));
}

float NormalDistributionFunctionGGXTR(vec3 n, vec3 m, float a)
{
    float a2 = a * a;
    float NdotM = max(0.0, dot(n, m));
    float NdotM2 = NdotM * NdotM;
    float denom = (NdotM * (a2 - 1.0) + 1.0);
    denom = PI * (denom * denom);
    return a2 / denom;
}

float GeometryGGX(vec3 n, vec3 v, float a)
{
    float k = (a + 1.0) * (a + 1.0) / 8.0;
    float NdotV = max(0.0, dot(n, v));
    return NdotV / (NdotV * (1.0 - k) + k);
}

float GeometrySmith(vec3 n, vec3 v, vec3 l, float a)
{
    float g1 = GeometryGGX(n, l, a);
    float g2 = GeometryGGX(n, v, a);
    return g1 * g2;
}

vec3 FresnelSchlick(vec3 v, vec3 h, vec3 F0)
{
    float VdotH = max(0.0, dot(v, h));
    return F0 + (1.0 - F0) * pow(1.0 - VdotH, 5.0);
}

vec3 PBR(vec3 p, vec3 n, vec3 v, in DirLight l, in Material material, float shadowContrib)
{
    vec3 F0 = mix(vec3(0.04), material.albedo, material.metallic);
    vec3 h = normalize(v + l.dir);
    float roughness = material.roughness * material.roughness;
    float D = NormalDistributionFunctionGGXTR(n, h, roughness);
    vec3 F = FresnelSchlick(v, h, F0);
    float G = GeometrySmith(n, v, l.dir, roughness);
    float NdotL = max(0.0, dot(n, l.dir));
    float NdotV = max(0.0, dot(n, v));
    vec3 Kd = (1.0 - F) * (1.0 - material.metallic);
    vec3 radiance = l.color;
    vec3 num = D * F * G;
    float denom = 4.0 * NdotL * NdotV;
    vec3 specularBRDF = (num / max(denom, EPSILON)) * pow(shadowContrib, 2.0);

    return (material.albedo * 0.03) + ((Kd * material.albedo / PI + specularBRDF) * radiance * NdotL * shadowContrib);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    kLightDir.x = cos(iTime * 0.5);
    kLightDir.z = sin(iTime * 0.5);
    vec3 color = vec3(0.04);
    vec2 uv = (fragCoord.xy / iResolution.xy - 0.5) * vec2(iResolution.x / iResolution.y, 1.0);
    vec3 ro = vec3(0.0, 0.0, -2.0);
    vec3 rd = normalize(vec3(uv, 1.0));
    float mx = 6.28 * (iMouse.x / iResolution.x * 2.0 - 1.0);
    float t = 0.0;
    vec3 l = normalize(kLightDir);

    ro.xz *= rot(mx);
    rd.xz *= rot(mx);

    float atten = 0.1;

    vec3 matColor = vec3(0.1, 0.1, 0.4);
    vec3 lightColor = vec3(3.0, 0.1, 0.1);

    Material mat = Material(matColor, kRoughness, 0.0);

    for (int i = 0; i < 400; ++i)
    {
        vec3 p = ro + rd * t;
        float d = map(p);

        if (d < 0.001)
        {
            vec3 l = normalize(l);
            vec3 v = normalize(-rd);
            vec3 h = normalize(v + l);
            vec3 n = norm(p);

            // Shadows
            float shadowContrib = 1.0;
            {
                vec3 rd2 = l;
                vec3 ro2 = p + rd2 * 0.1;
                float stepSize = 0.0;
                float atten = 1.0;
                for (int j = 0; j < 32; ++j)
                {
                    float d2 = map(ro2 + rd2 * stepSize);
                    if (d2 < 0.01)
                    {
                        shadowContrib = clamp(atten * stepSize, 0.0, 1.0);
                        break;
                    }
                    stepSize += 0.01;
                    atten *= 1.05;
                }
            }

            DirLight dirLight = DirLight(l, lightColor);
            color = PBR(p, n, v, dirLight, mat, shadowContrib);

            // Fake SSS
            {
                vec3 rd2 = l;
                vec3 ro2 = p + rd2 * 0.1;

                if (kSSS > 0.0)
                {
                    for (int j = 0; j < 32; ++j)
                    {
                        float stepSize = kSSS;
                        float d2 = map(ro2 + rd2 * kSSS);
                        if (d2 < 0.0)
                        {
                            break;
                        }
                        color += (matColor + (lightColor * d2 * 0.1)) * 0.01 * d2;
                    }
                }
            }

            break;
        }
        t += d * 0.5;
        if (t > 100.0) break;
    }

    // tone mapping
    color = color / (color + 1.0);

    // gamma correction
    color = pow(color, vec3(1.0 / 2.2));

    fragColor = vec4(color, 1.0);
}

#define SCALE 5.0

#define EDGE_SHARPNESS 20000.0

#define AIR_PERSPECTIVE 2.0

float getChessboardLuminance(vec2 point) {
    float value =
        (1.0 - pow(sin(point.x), EDGE_SHARPNESS))
        * sign(cos(point.x));
    value *=
        (1.0 - pow(sin(point.y - iTime), EDGE_SHARPNESS))
        * sign(cos(point.y - iTime));
    return (value + 1.0) / 2.0;    
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pixel = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    vec2 trans = vec2(pixel.x / pixel.y, log2(abs(pixel.y)));
    trans *= SCALE;

	float luminance = getChessboardLuminance(trans);

    // apply air perspective
    luminance *= abs(pixel.y * 2.0);
    fragColor = vec4(vec3(luminance),1.0);
}

