    vec2 A = abs( 2.*u/iResolution.xy - 1. );
    A[ i%2 ] < f ? i++, O += f : O++;
    O *= .5 + .5* fract( A[ i % 2 ] ); }

vec3  ballPos;
float ballRadius;
vec4  ballColor;

float getDist(vec3 rayPos)
{
    return distance(ballPos, rayPos) - ballRadius;
}

vec3 getNormal(vec3 p)
{
    float d = getDist(p);
    vec2  e = vec2(0.01, 0.0);

    vec3 n = d - vec3(
        getDist(p - e.xyy),
        getDist(p - e.yxy),
        getDist(p - e.yyx)
    );

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec4 outputPixel = vec4(0.0);
    vec2 pixelPos    = fragCoord.xy;

    vec3 camPos = vec3(
        iResolution.x / 2.0, 
        iResolution.y / 2.0, 
        -300.0
    );

    vec3 rayPos = vec3(pixelPos, 0.0);
    vec3 rayVec = normalize(rayPos - camPos);

    vec3 lightPos = vec3(
        iResolution.x / 2.0 + sin(iTime) * 100.0, 
        iResolution.y / 2.0 + cos(iTime) * 100.0,
        -100.0
    );

    ballPos    = vec3(
        iResolution.x / 2.0 + cos(iTime * 2.0) * 100.0, 
        iResolution.y / 2.0,
        99.99 + sin(iTime * 2.0) * 20.0
    );

    ballRadius = 100.0;
    ballColor = vec4(1.0, 0.4, 1.0, 1.0);

    vec3  curRayPos = rayPos;
    for (int i = 0; i < 40; i ++)
    { 
    	float distToBall = getDist(curRayPos);
        if (distToBall < .1)
        {
            vec3  normal = getNormal(curRayPos);
            float light  = dot(normalize(lightPos - curRayPos), normal);
            light = clamp(light, 0.0, 1.0) + 0.02;
            outputPixel = pow(light, 1.0 / 1.8) * ballColor;
            break;

        } else
        {
        	curRayPos += rayVec * distToBall;
        }
    }

    fragColor = outputPixel;
}

// Code by Flopine
// This is a variation on this shader : https://www.shadertoy.com/view/td23zD 
